    uv.x *= iResolution.x/iResolution.y;

    vec2
        a = 0.5 * vec2(cos(iTime), sin(iTime)),
        b = 0.5 * vec2(cos(iTime + OneThird), sin(iTime + OneThird)),
        c = 0.5 * vec2(cos(iTime - OneThird), sin(iTime - OneThird));

    fragColor = drawLine(uv, a, b, red, green);
    fragColor += drawLine(uv, b, c, green, blue);
    fragColor += drawLine(uv, c, a, blue, red);
}

int MARCH_ITERATIONS = 250;
float SMOOTHING = 0.0005;
int FRACTAL_ITERATIONS = 9;
bool DEFORM = false;
float MIN_VIEWDIS = 0.75;
float MAX_VIEWDIS = 3.0;
bool AO = false;
bool GLOW = true;
float glowThreshold = 0.0001;
float glowRadius = 0.005;
bool FIXED_CAMERA = false;
bool ORBIT = true;

vec3 transform(vec3 p, vec3 rot){
    //x axis rot
    p = vec3(p.x, p.y*cos(rot.x) - p.z*sin(rot.x), p.y*sin(rot.x) + p.z*cos(rot.x));
    //y axis rot
    p = vec3(p.x*cos(rot.y) + p.z*sin(rot.y), p.y, p.x*sin(rot.y) - p.z*cos(rot.y));
    //z axis rot
    p = vec3(p.x*cos(rot.z) - p.y*sin(rot.z), p.x*sin(rot.z) + p.y*cos(rot.z), p.z);

    p = abs(p);

    return p;
}

//http://www.fractalforums.com/sierpinski-gasket/kaleidoscopic-(escape-time-ifs)/?PHPSESSID=6c514a6be466438969dd15d053d618b2

float prim_tetra(vec3 p, float scale){
	for(int i=0;i<FRACTAL_ITERATIONS;i++){
        if(DEFORM){
        	p = transform(p,vec3(iTime*0.1,0.2,iTime*0.25));
        }
        if(p.x+p.y<0.0){p.xy = -p.yx;}
        if(p.x+p.z<0.0){p.xz = -p.zx;}
        if(p.y+p.z<0.0){p.zy = -p.zy;}
        p = p*scale - (scale - 1.0);
    }
    return (length(p)-scale)*pow(scale,-float(FRACTAL_ITERATIONS));
}


float scene(vec3 p){
    if(!FIXED_CAMERA){
        if(!ORBIT){
    		vec3 rot = vec3(2.5*sin(iTime*0.25),0.2*iTime,cos(iTime*0.1));
    		p = transform(p, rot);
        } else {
    		p = transform(p, vec3(0.6*iTime, 0.0, 0.5*(0.5+cos(iTime*0.35))));
        }
    }
    //return min(torus(p,vec2(0.8,0.2)),prim_tetra(p));
    return prim_tetra(p, 2.0);
}

vec3 calNormal(vec3 p, float smoothVal){
    return normalize(vec3(
        scene(p+vec3(smoothVal,0,0))-scene(p+vec3(-smoothVal,0,0)),
        scene(p+vec3(0,smoothVal,0))-scene(p+vec3(0,-smoothVal,0)),
        scene(p+vec3(0,0,smoothVal))-scene(p+vec3(0,0,-smoothVal))));
}

vec3 raymarch(vec2 uv, vec3 cam, vec3 lightDir, vec3 lightCol, vec3 matCol, vec3 ambient, vec3 fogCol){
    vec3 dir = normalize(vec3(2.0*uv.x-1.0,2.0*uv.y-1.0,1.0));;
    float totalDis = 0.0;
    float minDis = 100000000.0;
    vec3 p;
    //raymarch
    int i = 0;
    while(i<MARCH_ITERATIONS){
        p = cam + totalDis*dir;
        float stepDis = scene(p)*0.5;
        totalDis = totalDis + stepDis;
        if(stepDis < minDis){minDis = stepDis;}
        if(stepDis < 0.01*minDis){break;}
        i++;
    }
    //lighting
    if(minDis < glowThreshold){
    	vec3 normal = calNormal(p,SMOOTHING);
    	float diffuse = dot(lightDir,normal);
    	diffuse = 0.5*diffuse + 0.5;
    	vec3 diffuseLit = diffuse*lightCol*matCol*ambient;
        if(AO){diffuseLit = diffuseLit*smoothstep(0.75,1.0,float(i/MARCH_ITERATIONS));}
    	return mix(diffuseLit,fogCol,smoothstep(MIN_VIEWDIS,MAX_VIEWDIS,totalDis));
    } else if(GLOW) {
        return mix(vec3(0.25,0.45,0.25),vec3(0.075,0.075,0.075),smoothstep(glowThreshold,glowRadius,minDis));
    } else {
        return vec3(0.05,0.05,0.05);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
