vec3 Rect(vec2 uv, vec4 size, float blur, vec2 position, vec3 color){
	//position: left right up down
    uv -= position;

    float left = smoothstep(size.x - blur, size.x + blur, uv.x);
    float right = smoothstep(size.y + blur, size.y - blur, uv.x);

    float up = smoothstep(size.z - blur, size.z + blur, uv.y);
    float down = smoothstep(size.w + blur, size.w - blur, uv.y);


    return (left * right * up * down) * color;
}


