#define time iTime

vec3 erot(vec3 p, vec3 ax, float ro) {
	return mix(dot(p,ax)*ax,p,cos(ro)) + sin(ro)*cross(ax,p);
}

float linedist(vec2 p, vec2 a, vec2 b) {
  float k = dot(p-a,b-a)/dot(b-a,b-a);
  return distance(p, mix(a, b, clamp(k,0.,1.) ));
}

float hash(float a, float b) {
	return -1.+2.*fract(sin(dot(vec2(a,b), vec2(12.4348,74.38483)))*48573.2394);
}

float scene(vec3 p ) {
	float scale  = 0.2;
	vec3 id = floor(p*scale);
    float idhash = hash(hash(id.y, id.x), id.z);
	float hash1 = hash(idhash, id.y)*6.28;
	float hash2 = hash(id.x, idhash)*6.28;
	vec3 ax = normalize(vec3(cos(hash1), sin(hash1)*cos(hash2), sin(hash1)*sin(hash2)));
	float ro = hash(hash1+id.y, idhash)*6.28;
	p = (fract(p*scale)-0.5)/scale;
	p = erot(p, ax, ro+time*0.7);
	p += vec3(hash(hash1,hash2+6.), hash(hash1+5.,hash2), hash(hash2,hash1+3.))*1.;
	float sphere = length(p)-1.;
	return min(.5,linedist(vec2(p.z, sphere), vec2(-0.1,0.), vec2(0.1,0.))-0.1);
}

vec3 norm(vec3 p) {
	mat3 k = mat3(p,p,p) - mat3(0.01);
	return normalize(scene(p) - vec3(scene(k[0]), scene(k[1]), scene(k[2])));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
  uv = erot(vec3(uv,0.), vec3(0,0,1), cos(time*0.5)*0.5).xy;
  vec3 cam = normalize(vec3(1,uv));
  vec3 init = vec3(time,0,cos(time*0.4)*2.);
  cam=erot(cam,vec3(0,1,0),cos(time/8.)*0.3);
  cam=erot(cam,vec3(0,0,1),time/4.);
  vec3 p = init;
  bool hit = false;
  for (int i = 0; i< 100; i++) {
  	float dist = scene(p);
  	if (dist*dist < 0.00001) { hit = true; break; }
  	if (distance(p,init)>30.) break;
  	p+=cam*dist;
  }

  uv *=32.;
  uv += vec2(sin(time*1.), cos(time*1.5))*2.;
  //uv = erot(vec3(uv,0.), vec3(0,0,1), cos(time*0.5)*0.5).xy;

  fragColor = cos(uv.y)*cos(uv.x) < 0. ? vec4(0.2,0.5,0.9,1.) : vec4(0.);
  if (hit) {
  	vec3 n = norm(p);
  	vec3 rf = reflect(cam, n);
    float shadow = scene(p+rf) + .5;
  	float factor = shadow*length(sin(rf*3.)*0.5+0.5)/sqrt(2.);
  	vec3 col = mix(vec3(0.3,0.25,0.1), vec3(0.8,0.6,0.2), factor) + pow(factor*0.8, 6.);
    fragColor.xyz = mix(col, fragColor.xyz, pow(distance(p, init)/30., 50.));
  }
}

#define PI 3.14

float circle( vec2 coord, float radius )
{
    return step(radius, length(coord));
}

float smoothcircle( vec2 coord, float radius, float blur )
{
    return smoothstep(radius*(1.-blur), radius*(1.+blur), length(coord));
}

float rect( vec2 coord, vec2 size )
{
    vec2 rect = step(abs(coord), size);
    return 1. - rect.x * rect.y;
}

mat2 rotate2d(float angle)
{
    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}

float rays(vec2 uv, float nbr)
{
	uv = uv * rotate2d(iTime * 0.5);
	float propeller = cos(atan(uv.y, uv.x) * nbr);
    return smoothstep(0., 0.1, propeller);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // - Normalize Coordinate -
    vec2 uv = -(2. * fragCoord - iResolution.xy) / iResolution.y;

    // - Set Background - 
    vec3 col = vec3(1.);

    // - Useful Functions -
    //col = vec3( length(uv) );									// length(coord)
    //col = vec3( step(uv.x + 0.5, sin(iTime)) );				// step(edge, source_value) => if (source_value > edge)
    //col = vec3( smoothstep(uv.x-0.2, uv.x+0.2, sin(iTime)) );	// step(lower_edge, upper_edge, source_value)

    // - Add Shapes -

    //Coords
    vec2 pos = uv + vec2(0.);

    //pos = uv + vec2(-0.8, 0.5);
    //col *= vec3( circle(pos, 0.3) );

    // Rays
    col.z = rays(uv, 10.);

    // Head
    pos = uv + vec2(0.);
    col *= vec3( smoothcircle(pos, 0.98, 0.005) );
    col += 1. - vec3( smoothcircle(pos, .88, 0.005) );

    // Eyes
    pos = uv * vec2(1.2, 1.) + vec2(0. + 0.05 * cos(4.*iTime), 0.43 + 0.05 * sin(4.*iTime));
    vec2 new_pos = vec2(0.);
    float delta = 0.5;

    // Eye Left
    new_pos = pos + vec2(delta, 0.);
    col *= vec3( smoothcircle(new_pos, 0.3, 0.01) );
    col += 1. - vec3( smoothcircle(new_pos, 0.23, 0.01) );

    new_pos += 0.15 * vec2(cos(iTime), sin(iTime));
    col *= vec3( smoothcircle(new_pos, 0.1, 0.01) );


    // Eye Right
    new_pos = pos + vec2(-delta, 0.);
    col *= vec3( smoothcircle(new_pos, 0.3, 0.01) );
    col += 1. - vec3( smoothcircle(new_pos, 0.23, 0.01) );

    new_pos += 0.15 * vec2(cos(-iTime + PI), sin(-iTime + PI));
    col *= vec3( smoothcircle(new_pos, 0.1, 0.01) );

    // Nose
    pos = uv + vec2(0., -0.05);
    col *= vec3( rect(pos, vec2(0.05, 0.2)) );
    col *= vec3( rect(pos, vec2(0.2, 0.05)) );

    // Mouth
    pos = uv + vec2(0., -0.45 + 0.05*cos(2.*iTime));
    col *= vec3( rect(pos, vec2(0.2, 0.05)) );

    pos = uv + vec2(0., -0.65 + 0.05*sin(2.*iTime));
    col *= vec3( rect(pos, vec2(0.2, 0.05)) );

    // - Output -
    fragColor = vec4(col, 1.0);
}

