#define MS 50			// max steps
#define MD 30.          // max distance
#define SD .001         // surface distance threshold
#define EP .05          // offset for gradient normal

#define GLITCH		    // enable glitchy effects?
#define WAIT_SYNC       // wait for sound to load before advancing time

#define SND_BPM 150.         // beats per minute
#define SND_BPS SND_BPM/60.  // beats per second
#define SND_OFS 0.            // offset


float PI = acos(-1.);   // PI!

float o1=0., o2=0., o3=0.; // orgone accumulators
vec3 oc1;                  // base color that gets used in multiple places

float time;

//----------------------------------------------
// SDFs
//----------------------------------------------
float sph(vec3 p, float r) {return length(p)-r;}	// sphere

float cyl(vec2 p, float r) {return length(p)-r;}    // cylinder

//----------------------------------------------
// utility functions
//----------------------------------------------
void rot(inout vec2 p, float a) { // 2d rotation
  	float c=cos(a), s=sin(a);
  	p *= mat2(c,-s,s,c);
}

float rnd(float a) { // hash a single float
  	return fract(sin(a * 58.9199) * 4533.9863);
}

float rnd(vec2 p) {// hash 2 floats
    return fract(sin(dot(p, vec2(44.1885, 28.1643)))*4689.19966);
}

float curve(float a, float b) { // interpolation between random values (thanks Nusan)
  	float k = a / b;
  	return mix(rnd(floor(k)), rnd(floor(k)+1.), pow(smoothstep(0.,1.,fract(k)), 12.));
}

float smin(float a, float b, float h) { // smooth minimun
  	float k = clamp((a-b)/h*.5+.5,0.,1.);
  	return mix(a,b,k)-h*k*(1.-k);
}

//----------------------------------------------
// Creature definition
//----------------------------------------------
struct thing {
    float rn;
    float blobpos;
    float blobsize;
    int reps;
    vec2 offs;
    vec3 rots;
    float scale;
    float squishy;
    float tt;
};

//----------------------------------------------
// The scene
//----------------------------------------------    
float prim(vec3 p, thing th) { // The creature building block: a blob and a tentacle
  	p.z += sin(p.y+th.tt*4.)*.2; // some time based oscillation
  	p.x += cos(p.z+th.tt*3.)*.2;
  	p.y += cos(p.x+p.z+th.tt*3.5)*.1;

    // noise lookup from texture
  	vec3 np = p / 5.;
    float n = (texture(iChannel0, np.xy).r + texture(iChannel0, np.xz).r + texture(iChannel0, np.yz).r)/3.;
  	n = n * .1 - .05;

    float a = cyl(p.xz,cos(p.y/4.)*.1+n);               // main tentacle piece
  	a = smin(a, sph(p, 9.), -3.);                       // clamp it, trying to keep the end pointy

  	float b = sph(p-vec3(0.,th.blobpos,0.),th.blobsize+n);  // spherical blob part 
    b = abs(b) - .1 * th.blobsize;                          // onion trick to make it hollow

    float d = smin(a, b, th.squishy);  // merge the blob and the tentacle together

    d *= .8; // avoid artifacts due to distorted geometry

    o1 += .1 / (.1 + a*a*10.); // glow from tentacle
    o2 += .1 / (.1 + b*b*10.); // glow from blob

    return d;
}


float map(vec3 p) {

    // set up a random creature
  	thing th;
    th.tt = time / 2.;                                // creature pulsates at this rate
    th.rn = rnd(floor(time / 16.));                   // pick a new creature every 16 beats
    if(time > 224. && time < 300.) th.rn = rnd(228.); // but keep it fixed during the breakdown
    if(time > 300.) th.rn = rnd(floor(time / 4.));    // then pick a creature every 4 beats when we come back up

    th.blobpos = mix(0.,3., th.rn);                   // position of the blob on the tentacle
    th.blobsize=mix(.3, 2., th.rn);                   // size of the blob

    // number of fractal repetitions - between 2 and 4 for the first part of the tune,
    // but more complex (between 3 and 4) following the breakdown
    th.reps = time < 16. ? 0 : time > 300. ? int(mix(3.,4.,th.rn+.1)) : int(mix(2.,4.,th.rn+.1));
    th.offs = mix(vec2(.4), vec2(.8), th.rn); // position offset


    th.rots = vec3(// rotations around x,y and z axes
        mix(-PI/2.,PI/2.,rnd(th.rn+1.)), 
        mix(-PI,PI, rnd(th.rn+2.)), 
        mix(-PI/2.,-PI/2., rnd(th.rn+3.)));

    th.scale = mix(1.1, 1.5, th.rn);      // scale reduction per iteration
    th.squishy=mix(.1, 1.5, th.rn);       // squishiness (tightness of the smooth minimum function)

    oc1 = vec3(.1,.9,.1); 			 	  // base color for tentacles
    rot(oc1.xy, mix(-PI/2.,PI/2.,th.rn)); // modify it based on random id
    oc1 = max(vec3(0.), oc1);             // don't let it go negative

    if (time < 224. || time >300.)        // oscillate rotation except during the breakdown
  		rot(p.xz, sin(time));

  	float d = 9999.;                      // distance to the creature
  	vec4 fp = vec4(p,1);                  // trick to track scale during fractal iterations (stole it from Evvvvil)
  	float s = 1.;                         // initial scale
  	for(int i=0; i<5; ++i) {              // using a fixed loop size but bailing early for older WebGL compatability
        if (i == th.reps) break;
    	fp *= s;						  // apply the scale
    	s *= th.scale;                    // change the scale to be used next iteration

    	fp.xz = abs(fp.xz) - th.offs;     // absolute symmetry with offset
    	rot(fp.yz, th.rots.x*s);          // apply the rotations
        rot(fp.xz, th.rots.y*s);
    	rot(fp.xy, th.rots.z*s);

    	d = min(d, (prim(fp.xyz, th) / fp.w)); // make a tentacle
  	}

  	d = th.reps == 0 ? prim(fp.xyz, th) : d; // draw a single tentacle if the loop was skipped

    float sw = mix(d-.2, d-10., pow(fract(time / 4.),2.));  // shockwave position
    o3 += (.1 / (.1 + sw * sw * 1000.));                         // shockwave glow

  	return d;
}


//----------------------------------------------
// The marching machinery 
//----------------------------------------------
vec3 normal(vec3 p) { // gradient normal
  	vec2 off = vec2(EP,0.);
  	return normalize(map(p) - vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));
}


vec3 march(vec3 ro, vec3 rd) {
    // main ray marching loop
  	float dd=0.1;
  	vec3 col=vec3(0), // color
         p=ro+rd*dd;  // position
  	float side = sign(map(p)); // Nusan's translucency trick
    float att = 1.;   // attenuation
    for(int i=0; i<MS; ++i) {
    	float d = map(p);
    	d *= side;
        float atten = att;
        float diff = 0.; // base 'surface' color
        float frez = 0.; // freznel
        if(d < SD) {
            // close enough to an object to notice
      		vec3 n = normal(p)*side;
            side = -side;
            rd = refract(rd, n, 1. + .05*side); // do some refraction
      		d = .01; // step away for next iteration
            att *= .4;
            diff = .1;
            frez = .2*pow(max(.5, 1.-dot(n, -rd)), 3.);
    	}
    	if(dd > MD) { // falling of the edge of the world
      		dd = MD;
      		break;
    	}


        // color based on the accumulated orgones
        // oc1 is random for each creature. 
        // oc2 and oc3 are calculated from oc1
    	vec3 oc2 = 1. - oc1;
    	vec3 oc3 = normalize(o1 * oc1 + o2 * oc2);

        float dst = exp(-.0001*dd*dd*dd); // some fog

        // apply the colors and surface
        #define c(x,y) x*atten*dst*y
        vec3 c1 = oc1*o1, c2 = oc2*o2, c3=oc3*o3;
    	col += c(c1, diff*frez) +  c(c1, .01); 
    	col += c(c2, diff*frez) + c(c2, .001);
    	col += c(c3, .1) * float(time > 16. && (time < 227.|| time >288.)); // no shock wave initially or during breakdown 

        // distance and pos for next iteration
    	p += rd * d;
    	dd += d;
  	}

  	return col;
}


vec3 raydir(vec3 ro, vec3 la, vec2 uv) {
	// construct a ray
    vec3 cz = normalize(la-ro),
  	cx = normalize(cross(cz, vec3(0,1,0))),
  	cy=normalize(cross(cx,cz));
  	return normalize(cx*uv.x + cy*uv.y + cz);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    time = (iTime * SND_BPS) - SND_OFS; // convert time to beats

#ifdef WAIT_SYNC
    // don't advance the timeline if the soundtrack hasn't loaded.
    // Doing a texture lookup for this is a bit expensive, but I 
    // can't figure out another way to check for this
    if (texture( iChannel1, vec2(.1, 0.) ).x == 0.)
        time = mod(time, 16.);
#endif

    // camera position
    vec3 cam;
    if (time > 224. && time <300.) { // simple rotate during the breakdown
        //cam = vec3(0.,2., 5. * sin(time / 4.));
        cam = vec3(1.,2.,5.);
        rot(cam.xz, time/4.);
    }
    else { // otherwise, do some random zooming and rotating
        cam = vec3(
        	mix(-4.,4.,curve(time,3.)),
        	mix(-4.,4., curve(time-3.,3.)),
        	mix(3.,8.,curve(time+2.,3.))
    	)*2.5;
    }

    vec2 ouv = uv;
#ifdef GLITCH
    float glitch = curve(time,.5);
    glitch = smoothstep(.9, 1., glitch);
    uv.x += sin(uv.y*100.+time*glitch*100.)*glitch * max(0., .2-uv.y*uv.y); // sin wave glitch
    uv += mix(vec2(0), uv * (rnd(uv + time)-.5), smoothstep(.95, 1., curve(time,.1))*.5); // noisy fuzz glitch
#endif

    // set up the ray
	vec3 ro=cam, la=vec3(0), rd=raydir(ro,la,uv);

    // clear to black
  	vec3 col = vec3(0.);

    // raymarch
  	col += march(ro, rd);

    // vignette
    col *= 1. - length(uv);

    // exposure
    col = vec3(1.) - exp(-col*1.5);

	// gamma
    col = pow(col, vec3(1./2.2));

    // final color
    fragColor = vec4(col,1.0);
}

// License: CC0
//   Result after playing around with warped FBMs for a few hours
//   Based on: https://iquilezles.org/www/articles/warp/warp.htm

#define PI    3.141592654
#define TAU   (2.0*PI)
#define TTIME (iTime*TAU)

const mat2 frot = mat2(0.80, 0.60, -0.60, 0.80);

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float noise(vec2 p) {
  float a = sin(p.x);
  float b = sin(p.y);
  float c = 0.5 + 0.5*cos(p.x + p.y);
  float d = mix(a, b, c);
  return d;
}

float fbm(vec2 p) {    
  float f = 0.0;
  float a = 1.0;
  float s = 0.0;
  float m = 2.0;
  for (int x = 0; x < 4; ++x) {
    f += a*noise(p); p = frot*p*m;
    m += 0.01;
    s += a;
    a *= 0.5;
  }
  return f/s;
}

float warp(vec2 p, out vec2 v, out vec2 w) {

  vec2 vx = vec2(0.0, 0.0);
  vec2 vy = vec2(3.2, 1.3);

  vec2 wx = vec2(1.7, 9.2);
  vec2 wy = vec2(8.3, 2.8);

  rot(vx, TTIME/1000.0);
  rot(vy, TTIME/900.0);

  rot(wx, TTIME/800.0);
  rot(wy, TTIME/700.0);

  v = vec2(fbm(p + vx), fbm(p + vy));

  w = vec2(fbm(p + 3.0*v + wx), fbm(p + 3.0*v + wy));

  return fbm(p + 2.25*w);
}

vec3 normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(0.0001, 0);

  vec3 n;
  n.x = warp(p + e.xy, v, w) - warp(p - e.xy, v, w);
  n.y = 2.0*e.x;
  n.z = warp(p + e.yx, v, w) - warp(p - e.yx, v, w);

  return normalize(n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= iResolution.x/iResolution.y;

  p += TTIME*0.01;
  p *= 2.0;
  vec3 col = vec3(1.0);

  vec2 v;
  vec2 w;

  float f = warp(p, v, w);
  vec3 n = normal(p);

  vec3 lig = normalize(vec3(0., 0.2, -0.4));
  rot(lig.xy, TTIME/10.0);
  float dif = max(dot(lig, n), 0.5);

  const vec3 col1 = vec3(0.1, 0.7, 0.8);
  const vec3 col2 = vec3(0.7, 0.3, 0.5);

  col = pow(dif, 0.75)*tanh(pow(abs(f + 0.5), 1.5)) + (length(v)*col1 + length(w)*col2);

  fragColor = vec4(col, 1.0);
}

// Fork of "Gabor-noise / reac-diff like" by FabriceNeyret2. https://shadertoy.com/view/3slXDf
