#define hash(p) (-1. + 2.* fract(sin( (p) * vec3(63.31,395.467,1) ) *43141.59265) )

void mainImage(out vec4 O, vec2 U) {
    vec2 R = iResolution.xy;
         U = 5.*sin(iTime*0.34)+35.* (2.*U - R ) / R.y;

    float a =iTime*20.134 ;

    for(float i=0.; i<200.; i++){
        vec3 h = sin(iTime*0.34)*40.*hash(i); 
      //h.xy += cos(iTime+vec2(0,11)+h.z*6.28) * vec2(5,1); // animation
    	a += sign(h.z) * atan(U.x-h.x, U.y-h.y);
    }

    O = vec4( .5+.5*sin( 3.14*length(U) +a ) );
