float lerp(float a, float b, float t)
{
    return ((1. - t) * a) + (t * b);
}

float tlerp(vec4 a, vec4 n, vec3 p)
{
    float x1 = lerp(a.x, a.y, p.x);
    float x2 = lerp(a.z, a.w, p.x);
    float x3 = lerp(n.x, n.y, p.x);
    float x4 = lerp(n.z, n.w, p.x);

    float y1 = lerp(x1, x2, p.y);
    float y2 = lerp(x3, x4, p.y);

    return lerp(y1, y2, p.z);
}

float blerp(vec4 s, vec2 p)
{
    vec2 q = 1. - p;

    float v =
        s.x * q.x * q.y +
        s.y * p.x * q.y +
        s.z * q.x * p.y +
        s.w * p.x * p.y;

    return v;
}

float derp1 (float a, float b, float t)
{
    return lerp(a, b, t) / (a - b);
}

float derp3 (vec4 a, vec4 b, vec3 t)
{
    vec3 d;

    d.z = derp1(blerp(a, t.xy), blerp(b, t.xy), t.z);
    d.y = derp1(blerp(vec4(a.xy, b.xy), t.xz), blerp(vec4(a.zw, b.zw), t.xz), t.y);
    d.x = derp1(blerp(vec4(a.xz, b.xz), t.yz), blerp(vec4(a.yw, b.yw), t.yz), t.x);

    return 1./length(1./d);
}

float vlerp(vec4 a, vec4 b, vec3 p)
{
    // causses tearing in the depth field
    //p = p * p * (3. - 2. * p);
    vec3 q = 1. - p; //create a conjugate of the point


    // yes... this mess is a perfectly valid 3D interpolation.
    return a.x * p.x * p.y * p.z
         + a.y * q.x * p.y * p.z
         + a.z * p.x * q.y * p.z
         + a.w * q.x * q.y * p.z
         + b.x * p.x * p.y * q.z
         + b.y * q.x * p.y * q.z
         + b.z * p.x * q.y * q.z
         + b.w * q.x * q.y * q.z;
}

