vec3 getnorm(vec3 p)
{
    vec2 e = vec2(0.00001, 0.);
    float d = (getdist(p));

    vec3 norm = d - vec3(
        getdist(p - e.xyy),
        getdist(p - e.yxy),
        getdist(p - e.yyx)
    );



    return normalize(- norm);
}

float march(vec3 ro, vec3 rd)
{
    float d = 0.;
    float df = MAXDIST;
    vec3 p = ro + rd * d;

    for(int i = 0; i < STEPS && d < MAXDIST && df > SURFDIST; i++)
    {
        df = getdist(p);
        d += df;
        p = ro + rd * d;
    }

    return d;
}
