float surflet(vec3 p, vec3 gridPoint) {
    vec3 t2 = abs(p - gridPoint);
    vec3 t = vec3(1.f) - 6.f * pow(t2, vec3(5.f)) + 15.f * pow(t2, vec3(4.f)) - 10.f * pow(t2, vec3(3.f));
    vec3 gradient = random3(gridPoint) * 2. - vec3(1., 1., 1.);
    vec3 diff = p - gridPoint;
    float height = dot(diff, gradient);

    return height * t.x * t.y * t.z;
}
float perlinNoise3D(vec3 p) {
	float surfletSum = 0.f;
	for(int dx = 0; dx <= 1; ++dx) {
		for(int dy = 0; dy <= 1; ++dy) {
			for(int dz = 0; dz <= 1; ++dz) {
				surfletSum += surflet(p, floor(p) + vec3(dx, dy, dz));
			}
		}
	}
	return surfletSum;
}


float WorleyNoise3D(vec3 p)
{
    vec3 tile = floor(p);
    vec3 pixel = fract(p);

    float minDist = 1.0;
    for (int z = -1; z <= 1; z++) {
        for (int y = -1; y <= 1; y++) {
            for (int x = -1; x <= 1; x++) {
                vec3 neighbor = vec3(float(x), float(y), float(z));

                vec3 point = random3(tile + neighbor);
                point = 0.5 + 0.5 * sin(iTime * 0.5 + 6.28 * point);

                vec3 diff = neighbor + point - pixel;
                float dist = length(diff);
                minDist = min(minDist, dist);
            }
        }
    }
    return minDist;

}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sceneMap3D(vec3 pos, vec3 lightPos) {
    float t0 = box(rotateY(pos + SMALLBOXPOS, -9.9), vec3(3.0, 3.0, 3.0));
    float t1 = box(rotateY(pos + LARGEBOXPOS, -0.9), vec3(3.0, 8.0, 3.0));
    float t2 = box(pos + vec3(0.0, 3.0, 0.0), vec3(15.0, 1.0, 15.0));
	float t3 = plane(pos + RIGHTPOS, vec4(0.0, 0.0, 1.0, 1.0));
    float t4 = plane(pos + LEFTPOS, vec4(0.0, 0.0, -1.0, 1.0));
	float t5 = plane(pos + CEILINGPOS, vec4(0.0, -1.0, 0.0, 1.0));
    float t6 = plane(pos + BACKPOS, vec4(-1.0, 0.0, 0.0, 1.0));

    float min = min(t5, min(t4, min(t3, min(t0, min(t2, min(t1, t6))))));
    return min;
}

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
	float t0 = box(rotateY(pos + SMALLBOXPOS, -9.9), vec3(3.0, 3.0, 3.0));
    float t1 = box(rotateY(pos + LARGEBOXPOS, -0.9), vec3(3.0, 8.0, 3.0));
    float t2 = box(pos + vec3(0.0, 3.0, 0.0), vec3(15.0, 1.0, 15.0));
	float t3 = plane(pos + RIGHTPOS, vec4(0.0, 0.0, 1.0, 1.0));
    float t4 = plane(pos + LEFTPOS, vec4(0.0, 0.0, -1.0, 1.0));
	float t5 = plane(pos + CEILINGPOS, vec4(0.0, -1.0, 0.0, 1.0));
    float t6 = plane(pos + BACKPOS, vec4(-1.0, 0.0, 0.0, 1.0));

    obj = 0;
    float min = min(t5, min(t4, min(t3, min(t0, min(t2, min(t1, t6))))));
    t = min;
    if (min == t0) {obj = 3;}
    else if (min == t1) {obj = 3;}
    else if (min == t3) {obj = 0;}
    else if (min == t4) {obj = 1;}
    else if (min == t6) {obj = 3;}
    else {obj = 3;}
}

void rayCast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref) {
    eye = rotateY(vec3(0.0, 1.0, 15.0), PI * 1.5);
    ref = vec3(0.0, 0.0, 0.0);

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;

    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
	t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) 
    {
    	vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);

        if (m < 0.01) 
        {
         	return;   
        }

        t += m;
    }

    t = -1.0;
    hitObj = -1;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view) {
    float lambert = max(0.0, dot(n, light));
	switch(hitObj)
    {
        case 0: // left wall
        return vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.0, 0.0) * lambert * perlinNoise3D(p);
        break;
        case 1: // right wall
        return vec3(1.0, 0.88, 0.7) * vec3(0.0, 0.88, 0.0) * lambert * perlinNoise3D(p);
        break;
        case 3: // floor, ceiling, boxes
        return vec3(1.0, 1.0, 1.0) * lambert * WorleyNoise3D(p);
        break;
        case -1:
        return vec3(0.0);
        break;
    }

    return vec3(1.0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));
    return Intersection(t, surfaceColor, isect, hitObj);
}





