// SDF of a sphere
float sphere(vec3 p , float r, vec3 c) {
    return distance(p, c) - r;
}

// SDF of plane
float plane(vec3 p, vec3 n, vec3 c)
{
  	// n must be normalized
  	vec3 a = p - c;
  	vec3 b = n;

    if (dot(a, b) < -0.001) {
        return 10000.;
    }

  	return dot(a ,b) / length(b);
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

// transformation
mat4 inverseTransform(vec3 s, vec3 rDeg, vec3 t) {

    // convert from degrees to radians
    vec3 rotate = vec3(radians(rDeg.x), radians(rDeg.y), radians(rDeg.z));

    mat4 r_x;
    r_x[0] = vec4(1., 0., 0., 0.);
    r_x[1] = vec4(0., cos(rotate.x), sin(rotate.x), 0.);
    r_x[2] = vec4(0., -sin(rotate.x), cos(rotate.x), 0.);
    r_x[3] = vec4(0., 0., 0., 1.);


    mat4 r_y;
    r_y[0] = vec4(cos(rotate.y), 0., -sin(rotate.y), 0.);
    r_y[1] = vec4(0., 1, 0., 0.);
    r_y[2] = vec4(sin(rotate.y), 0., cos(rotate.y), 0.);
    r_y[3] = vec4(0., 0., 0., 1.);

    mat4 r_z;
    r_z[0] = vec4(cos(rotate.z), sin(rotate.z), 0., 0.);
    r_z[1] = vec4(-sin(rotate.z), cos(rotate.z), 0., 0.);
    r_z[2] = vec4(0., 0., 1., 0.);
    r_z[3] = vec4(0., 0., 0., 1.);

    mat4 rMat = r_x * r_y * r_z;

    // creating worldTransform matrix
    mat4 translateMat;
    translateMat[0] = vec4(1., 0., 0., 0.);
    translateMat[1] = vec4(0., 1., 0., 0.);
    translateMat[2] = vec4(0., 0., 1., 0.);
    translateMat[3] = vec4(t.x, t.y, t.z, 1.);

    mat4 scaleMat;
    scaleMat[0] = vec4(s.x, 0., 0., 0.);
    scaleMat[1] = vec4(0., s.y, 0., 0.);
    scaleMat[2] = vec4(0., 0., s.z, 0.);
    scaleMat[3] = vec4(0., 0., 0., 1.);

    mat4 worldTrans = translateMat * rMat * scaleMat;

    return inverse(worldTrans);
}

mat4 rotateMat(vec3 rDeg) {
        // convert from degrees to radians
    vec3 rotate = vec3(radians(rDeg.x), radians(rDeg.y), radians(rDeg.z));

    mat4 r_x;
    r_x[0] = vec4(1., 0., 0., 0.);
    r_x[1] = vec4(0., cos(rotate.x), sin(rotate.x), 0.);
    r_x[2] = vec4(0., -sin(rotate.x), cos(rotate.x), 0.);
    r_x[3] = vec4(0., 0., 0., 1.);


    mat4 r_y;
    r_y[0] = vec4(cos(rotate.y), 0., -sin(rotate.y), 0.);
    r_y[1] = vec4(0., 1, 0., 0.);
    r_y[2] = vec4(sin(rotate.y), 0., cos(rotate.y), 0.);
    r_y[3] = vec4(0., 0., 0., 1.);

    mat4 r_z;
    r_z[0] = vec4(cos(rotate.z), sin(rotate.z), 0., 0.);
    r_z[1] = vec4(-sin(rotate.z), cos(rotate.z), 0., 0.);
    r_z[2] = vec4(0., 0., 1., 0.);
    r_z[3] = vec4(0., 0., 0., 1.);

    mat4 rMat = r_x * r_y * r_z;
    return rMat;
}

// SDF of box
float box(vec3 p, vec3 s, vec3 r, vec3 t) {
    mat4 inverseTrans = inverseTransform(s, r, t);
    vec4 tempP = inverseTrans * vec4(p, 1.f);
    p = vec3(tempP.x, tempP.y, tempP.z);

    vec3 q = abs(p) - vec3(0.5, 0.5, 0.5);
  	return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2;
    if ((t2 = SPHERE1_SDF) <t) {
        t = t2;
        obj = SPHERE1_ID;
    }
    if ((t2 = SPHERE2_SDF) <t) {
        t = t2;
        obj = SPHERE2_ID;
    }
    if ((t2 = SUN_SDF) <t) {
        t = t2;
        obj = SUN_ID;
    }
}

float sceneMap3D(vec3 pos, vec3 lightPos) {
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
    }
    if ((t2 = SPHERE2_SDF) < t) {
        t = t2;
    }
    if ((t2 = SUN_SDF) < t) {
        t = t2;
    }
    return t;
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thin) {
	vec3 scatterDir = lightDir + normal * DISTORTION; // Last term is tunable
	float lightReachingEye = pow(clamp(dot(viewVec, -scatterDir),
							     0.0, 1.0), GLOW) * SCALE;
	float attenuation = max(0.0, dot(normal, lightDir)
                             + dot(viewVec, -lightDir));
    //attenutation = 1.f;
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thin;
	return totalLight;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float max_t, float k) {
    float res = 1.0;
    for(float t = min_t; t < max_t; /*No update in loop header*/) {
        float m = sceneMap3D(origin + t * dir, vec3(0., 0., 0.));
        if(m < 0.0001) {
            return 0.0; // In shadow
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

vec2 sphereUVMapSun(vec3 pos, vec3 lightPos) {
    vec3 n = normalize(pos - lightPos); // sphere point - sphere center: for the sun
    float phi = atan(n.z, n.x);
    if (phi < 0.) {
        phi += 2. * 3.1415926;
    }
    float theta = acos(n.y);
    float u = 1. - phi / (2. * 3.1415926);
    float v = 1. - theta / 3.1415926;
    return vec2(u, v);
}

vec2 planeUVMap(vec3 pos) {
    vec3 n = vec3(0., 1., 0.);
    vec3 u = normalize(vec3(1., 0., 0.));
    vec3 v = cross(n, u);

    float u_coord = dot(u, pos);
	float v_coord = dot(v,pos);
    return vec2(u_coord, v_coord);
}

vec3 computeMaterial(int hitObj, vec3 pos, vec3 n, vec3 lightVec, vec3 view, float thickness, vec3 lightPos) {
    float lambert = max(0., dot(lightVec, n)) * softShadow(lightVec, pos, 0.1, 50., 6.0);
    switch(hitObj) {
        case FLOOR_ID:
        	return vec3(1., 1., 1.) * lambert * colorPalette(cos(iTime / 2.)* WorleyNoise2D(planeUVMap(pos)));
        	break;
        case SPHERE1_ID:
        	return vec3(1., 0., 0.) * lambert;
            break;
		case SPHERE2_ID:
        	vec3 color = vec3(0.1, 0.4, 1.) * lambert;
        	return color + subsurface(lightVec, n, view, thickness) * vec3(0.2, 0.5, 1.);
            break;
        case SUN_ID:
        	return vec3(1.0, 0.88, 0.7) * colorPalette2(fbm(sphereUVMapSun(pos, lightPos)) * cos(iTime / 8.));
        	break;
        case -1:
            return vec3(0., 0., 0.);
        	break;
    }
    return vec3(0., 0., 0.);
}


vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos), 
                    	  sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                          sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}



void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.01) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}


float AO5Step(vec3 p, vec3 n) {
    float sum = 0.;
    for (float i = 1.; i <= 5.; i = i + 1.) {
        float distSDF = sceneMap3D(p + n * i * OCCDist, vec3(0.));
        sum += (1. / pow(2., i)) * (i * OCCDist - distSDF);
    }
    float ao = 1.0 - KSTEP * sum;
    return ao;
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;

    vec3 nor = computeNormal(isect, lightPos);

    vec3 lightDir = normalize(lightPos - isect);
    float thickness = AO5Step(isect, -nor);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness, lightPos);

    return Intersection(t, surfaceColor, isect, hitObj, nor);
}

// returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0, 1, 0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(0, 5.5, -35);
    vec3 ref = vec3(0, 2.5, 0);

    vec3 rayDir = rayCast(eye, ref, uv);
    vec3 lightPos = rotateY(vec3(0, 3.4, 18.0), sin(iTime * 0.5));

    Intersection isect = sdf3D(rayDir, eye, lightPos);


    // Output to screen
    fragColor = vec4(isect.color, 1.);
}

const int RAY_STEPS = 256;
const vec3 lightPos = vec3(15., 20., -20.);
const vec3 eyePos = vec3(0.0, 2., -30.0);
const vec3 refPos = vec3(0., 2.5, 0.);
const float PI = 3.14159265359;
const float FOVY = 19.5 * PI / 180.;

// The higher the value, the smaller the penumbra
const float SHADOW_HARDNESS = 10.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

/***************** BASIC SCENE SET UP *******************/

#define FLOOR_SDF plane(pos, vec4(0, 1, 0, 2.5));
#define BACK_WALL_SDF plane(pos, vec4(0, 0, -1, 5));
#define RIGHT_SPHERE_SDF sphere(pos + vec3(0., 2.5, 0.), 3., vec3(7.0, 7., 3));
#define GROUND_SPHERE_SDF sphere(pos + vec3(0., 2.5, 0.), 3., vec3(0.0, 3., 0.));
#define LEFT_SPHERE_SDF sphere(pos + vec3(0, 2.5, 0.), 3., vec3(-5.0, 7.0, -5.0));

const int FLOOR_ID = 0;
const int RED_WALL_ID = 1;
const int GREEN_WALL_ID = 2;
const int BACK_WALL_ID = 3;
const int GROUND_SPHERE_ID = 5;
const int RIGHT_SPHERE_ID = 6;
const int LEFT_SPHERE_ID = 7;

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sphere(vec3 p, float r, vec3 c) {
    return distance(p, c) - r;
}

float sceneMap3D(vec3 pos) {
    float flr = FLOOR_SDF;
    float back_wall = BACK_WALL_SDF;
    float ground_sphere = GROUND_SPHERE_SDF;
    float right_sphere = RIGHT_SPHERE_SDF;
    float left_sphere = LEFT_SPHERE_SDF;
    return min(back_wall, 
       min(flr, min(ground_sphere, min(right_sphere, left_sphere))));
}

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2 = BACK_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = BACK_WALL_ID;
    }
    t2 = GROUND_SPHERE_SDF;
    if (t2 < t) {
        t = t2;
        obj = GROUND_SPHERE_ID;
    }
    t2 = RIGHT_SPHERE_SDF
    if (t2 < t) {
        t = t2;
        obj = RIGHT_SPHERE_ID;
    }
    t2 = LEFT_SPHERE_SDF;
    if (t2 < t) {
        t = t2;
        obj = LEFT_SPHERE_ID;
    }
}

/***************** RAY MARCHING LOGIC *******************/

vec3 rayCast(vec3 eye, vec3 ref, vec2 uv) {

    float len = tan(FOVY) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    return normalize(p - eye);
}


void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01) { return; }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

/***************** EFFECTS *******************/

float shadow(vec3 dir, vec3 origin, float min_t) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = sceneMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, SHADOW_HARDNESS * m / t);
        t += m;
    }
    return res;
}

/***************** MATERIALS *******************/

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 nor, vec3 lightDir, vec3 view) {
    float lambert = max(0., dot(lightDir, nor));
    if (hitObj == GROUND_SPHERE_ID) { return vec3 (0.596, 1., 0.596) * lambert; }
    if (hitObj == LEFT_SPHERE_ID) { return vec3 (1, 0.549, 0.412) * lambert; } 
    if (hitObj == RIGHT_SPHERE_ID) { return vec3 (0., 0.6, 1.) * lambert; } 

    return vec3(0.15, 0.15, 1.0) * lambert;        

}

/***************** BASIC FUNCTIONALITY *******************/


Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    vec3 currLight = lightPos + sin(iTime * 0.75) * lightPos;

   float radius = 5.;

    float x  = 5. + radius * cos(iTime);
    float z  = -24. + radius * sin(iTime);


    vec3 lightDir = normalize(vec3(x, 20., z) - isect);


    vec3 surfaceMaterial = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    surfaceMaterial = surfaceMaterial * shadow(lightDir, isect, 0.1);

    return Intersection(t, surfaceMaterial, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.);

    vec3 eye = eyePos;
    vec3 ref = refPos;

    vec3 dir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(dir, eye);

    // Output to screen
    fragColor = vec4(isect.color, 1.);
}

#define PI 3.1415926
#define W 0.4
#define H 0.4
#define R 0.1
#define T 0.02

#define LH (H - R * 2.0)
#define LR (PI * R / 2.0)
#define LW (W - R * 2.0)



float roundedRectangle (vec2 coord, vec2 size, float radius, float thickness)
{
    vec2 halfsize = size / 2.0;
    halfsize = halfsize - vec2(radius + thickness / 2.0, radius + thickness / 2.0);

    float d = length( max( abs(coord), halfsize ) - halfsize) - radius;
    return smoothstep(thickness / 2.0, 0.0, abs( d - thickness / 2.0 ) );
}


float circle (vec2 coord, float radius)
{
    return smoothstep( 0.001, 0.0, length(coord) - radius );
} 
vec2 trajectory(float l)
{
    while (l >= LH + LR + LW + LR + LH + LR + LW + LR) {
        l = l - (LH + LR + LW + LR + LH + LR + LW + LR);
    }

    // may use `step` to optimize
    // TODO: figure out a simpler way to represent the trajectory function
    if (0.0 <= l && l < LH) {
        return vec2( W / 2.0, - H / 2.0 + R )
             + vec2( 0.0, l );
    }

    else if ( LH <= l && l < LH + LR ) {
        l = l - LH;
        return vec2( W / 2.0 - R, H / 2.0 - R)
             + vec2( R * cos( l / R ), R * sin( l / R ) );
    }

    else if ( LH + LR <= l && l < LH + LR + LW) {
        l = l - LH - LR;
        return vec2( LW / 2.0 , H / 2.0 )
             - vec2( l , 0.0 );
    }

    else if ( LH + LR + LW <= l && l < LH + LR + LW + LR ) {
        l = l - LH - LW;
        return vec2( - W / 2.0 + R, H / 2.0 - R )
             + vec2( R * cos( l / R ), R * sin( l / R ) );
    }

    else if ( LH + LR + LW + LR <= l && l < LH + LR + LW + LR + LH ) {
        l = l - LH - LW - LR - LR;
        return vec2( - W / 2.0, H / 2.0 - R )
             + vec2( 0, - l );
    }

    else if ( LH + LR + LW + LR + LH <= l && l < LH + LR + LW + LR + LH + LR ) {
        l = l - (LH + LW + LH);
        return vec2( - W / 2.0 + R, - H / 2.0 + R )
             + vec2( R * cos( l / R ), R * sin( l / R ) );
    }

    else if ( LH + LR + LW + LR + LH + LR <= l && l < LH + LR + LW + LR + LH + LR + LW ) {
        l = l - (LH + LR + LW + LR + LH + LR);
        return vec2( - W / 2.0 + R, - H / 2.0 )
             + vec2( l, 0.0 );
    }

    else if ( LH + LR + LW + LR + LH + LR + LW <= l && l < LH + LR + LW + LR + LH + LR + LW + LR ) {
        l = l - (LH + LW + LH + LW);
        return vec2( W / 2.0 - R, - H / 2.0 + R )
             + vec2( R * cos( l / R ), R * sin( l / R ) );
    }

    return vec2( W / 2.0, - H / 2.0 + R )
         + vec2( 0.0, 0.0 );
}


vec2 differential(float l) {
    return trajectory(l + 0.001) - trajectory(l);
}

float distanceFunction(vec2 pos) {
    if ( W / 2.0 - T / 2.0 < pos.x
      && pos.x < W / 2.0 + T / 2.0
      && 0.0 < pos.y
      && pos.y < LH / 2.0)
    {
        return pos.y;
    }
    else if ( pos.y > LH / 2.0 && pos.x > H / 2.0 - R )
    {
        pos = pos - vec2(LW / 2.0, LH / 2.0);
        return acos(dot(pos, vec2(1., 0.)) / length(pos)) * R + LH / 2.0;
    }
    else if ( pos.y > 0.0 && 0. < pos.x && pos.x < LW / 2.0 )
    {
        return LW / 2.0 - pos.x + LH / 2.0 + LR;
    }

    return 0.0;
}


// ä¸‰è§’æ³¢
float period(float phase, float p, float t)
{
    float a = LH + LR + LW + LR + LH + LR + LW + LR;

    float scaleT = (t - phase) / p;

    // x \in [0, 1)
    float x = fract(scaleT);

    return a * x;
}

