void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = uv - vec2(0.5, 0.5);

    // Time varying pixel color
    float colRoundedRectangle = roundedRectangle(pos, vec2(0.4, 0.4), 0.1, 0.02);

    float colStart = circle(pos - trajectory(period(0.0, 3.0, iTime)), 0.01);
    float colEnd = circle( pos - trajectory(period(0.1, 3.0, iTime)), 0.01);

    float colStart2 = circle(pos - trajectory(period(1.5, 3.0, iTime)), 0.01);
    float colEnd2 = circle(pos - trajectory(period(1.6, 3.0, iTime)), 0.01);

    // float col = colCircle;
    float col = colRoundedRectangle * (colStart + colEnd + colStart2 + colEnd2 + distanceFunction(pos) * 2.0);

    // Output to screen
    fragColor = vec4(col, col / 2.0, 0.0, 1.0);
}

