//-------------------------------------------------------------------
// Trying out ShaderToy and trying to get to grips with ray marching.
// Inspired by the amazing creations of Evvvvil, Nusan and Shane
//-------------------------------------------------------------------

#define MD 30.		// max distance
#define SD 0.0001    // surface distance
#define MS 250      // max number of steps to march
#define EPS 0.0001   // distance used for sampling the gradient

float time;

// always handy to have PI available
const float PI = acos(-1.);
const float TWOPI = 2.*PI;

// fog color.
const vec3 fog = vec3(.05,.25, .05);

// always need some glow!
float glw = 0.;

// sphere SDF
float sph(vec3 p, float r){return length(p)-r;}

// min and max which preserve material
vec2 mmin(vec2 a, vec2 b) {return a.x < b.x ? a:b;}
vec2 mmax(vec2 a, vec2 b) {return a.x > b.x ? a:b;}


// 2d rotation
void rot(inout vec2 p, float a) {
    float c=cos(a), s=sin(a);
    p *= mat2(c,-s,s,c);
}

// I was going to do something complicated here,
// but ended up doing a sphere!
vec2 prim(vec3 p) {
    return vec2(sph(p, .5), 5);
}

// the SDF for the scene
vec2 map(vec3 p) {

    // wiggle x
    p.x += sin(p.z + time*.2)*.5;
    // spiral
    rot(p.xy, sin(p.z / 3.));
    // scroll along z
    p.z -= time/3.;

    // repeat everything
    vec3 mp;
    mp.x = mod(p.x, 2.3)-1.15;
    mp.y = mod(p.y, 2.3)-1.15;
    mp.z = mod(p.z,.6)-.3;

    // fractal (ish)
    vec4 ap = vec4(mp,1.);
    // big purple sphere
    vec2 a=prim(mp);
    a.x *= .6;
    // smaller black spheres
    int n=4;
    for(int i=1; i<=n; ++i) {
    	ap *= 2.5; // scale
        ap.xyz = abs(ap.xyz)-vec3(.8); // symmetry
        rot(ap.xy, ap.z*.1 + time*.5); // movement
        vec2 b = prim(ap.xyz); // sphere
        b.x /= ap.w; // correct for scaling
        b.x *= .6;   // patch up the SDF
        b.y= 1.;     // paint it black
        a=mmin(a, b);

    }

    // Sparkles. Lots of them.
    rot(p.xy, time*.3);
    vec2 b = vec2(length(cos(p*1.+vec3(1.5+p.z*.05*sin(time),1.7+sin(time)*.2,time*2.)))-.001 ,6.);
    glw += .1/(.1*b.x*b.x*10000.);
    b.x *= .6;

    // final distance
    vec2 d=mmin(a,b);
    return d;
}

// determine the normal at point p by sampling the gradient of the SDF
vec3 normal(vec3 p) {
 	vec2 off = vec2(EPS, 0.);
    return normalize(map(p).x - vec3(map(p-off.xyy).x,map(p-off.yxy).x,map(p-off.yyx).x));
}

//-------------------------------------------------------------
// lighting and colour,
// heavily based on Evvvvil's Micro Lighting Engine Broski (TM)
//-------------------------------------------------------------

// shortcuts for calculating fake ambient occlusion and subsurface scattering
#define aoc(d) clamp(map(p + n * d).x/d, 0., 1.)
#define sss(d) smoothstep(0.,1.,map(p+ld*d).x/d)

vec3 surface(vec3 ro, vec3 rd, vec2 hit) {
	float d=hit.x;         // distance from ray origin
    float m = hit.y;       // materialID

    // albedo, or base color, based on material ID	
    vec3 al = m < 5. ? vec3(0) : 
    		m > 5. ? vec3(1) :
    		vec3(.1,.1,.4);

    vec3 p = ro + rd * d;  // the point in space
    vec3 n = normal(p);    // normal of the SDF at point p

    vec3 ld = normalize(vec3(-1));     // light direction
    float diff = max(0., dot(n, -ld)); // diffuse illumination

    vec3 lr = reflect(ld, n);          // reflected light ray
    float spec = pow(max(0., dot(lr,-rd)),32.); // specular component

    float frz = pow(max(0.1, 1. - dot(n,-rd)),4.)*.5; // freznel)

    float ao = aoc(.1); // fake AO
    float ss = sss(1.); // fake subsurface scattering

    vec3 col =  al * ao * (diff + ss) + spec; // combine it all together
    col = mix(col, fog, frz);         // fog the edges

    return col; // the final color
}

// ray marching loop
vec3 march(vec3 ro, vec3 rd) {
	float dd=0.1;		// start with a slight z offset
    vec3 col=vec3(0);   // no color initially
    vec3 p=ro + rd * dd;// current point

    for(int i=0; i<MS; ++i) {  // start marching
     	vec2 d = map(p);  // distance to SDF

        if(d.x < SD) { // close to a surface
            // get the color of the surface
            col += d.y > 0. ? surface(ro, rd, vec2(dd, d.y)) : vec3(0.);
            break; // we're done
        }

        if(dd > MD) { // we've reached the max draw distance,
            dd = MD;
            break;    // and we're done
        }

        dd += d.x;     // track the total distance
        p += rd * d.x; // and update the current position
    }

    // blend in some distance-based fog and return
    return mix(fog * pow(max(0., dot(rd, vec3(0,0,-1))),20.), col, exp(-0.003*dd*dd*dd));
}


// given the ray origin, lookat point and uv's, calculate the ray direction
vec3 raydir(vec3 ro, vec3 la, vec2 uv) {
    // x,y and z axes
    vec3 cz = normalize(la-ro);
    vec3 cx = normalize(cross(cz,vec3(0,1,0)));
    vec3 cy = normalize(cross(cx,cz));
    // project and normalize
    return normalize(cx*uv.x + cy*uv.y + cz);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    // make sure that the pixels are square
    uv /= vec2(iResolution.y / iResolution.x, 1);

    // get rid of some artifacts
    time = mod(iTime, 62.39);

    // setup the ray
    vec3 ro = vec3(0,0,1);
    vec3 rd = raydir(ro, vec3(0)-ro, uv);

    // march
    vec3 col = march(ro, rd);

    col += glw;
    // exposure
    col = vec3(1) - exp(-col * 1.1);
    // gamma
    col = pow(col, vec3(1./2.2));
    // final color
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Polar of a Cardioid
// Matthew Arcus, mla, 2020
//
// Given an implicit curve f(z) = 0, the polar curve for a point w is
// polar(z) = p.x + p.y where p = grad(f,z)*(w-z)
// The intersection of the polar with the curve itself gives the points
// on the curve whose tangents pass through p.
//
// Here we have a cardioid, drawn as a tangent envelope, and the polar of
// the pointer position, drawn as an implicit curve - the tangents through the
// pointer position meet the curve at its intersections with the polar.
//
// NEW FEATURE: (press 'g' to hide): a graph showing the distance of the
// pointer position from tangent(t), where tangent(t) is the line between
// (cos(t),sin(t)) and (cos(2t),sin(2t)) - zeros of this function are where
// a line passes through the point. The vertical lines are at -PI and +PI
// and there is a bogus zero at t = 0 in the middle (the two points
// coincide).
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.1415927;

float line(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = dot(pa, ba) / dot(ba, ba);
  float d = length(pa - ba * h);
  return d;
}

const int N = 108;
float card0(vec2 z) {
  // Draw the envelope
  float d = 1e8;
  float k = 2.0*PI/float(N);
  float t = 2.0;
  for (int i = 0; i < N; i++) {
    float theta = float(i)*k;
    vec2 p = vec2(cos(theta),sin(theta));
    vec2 q = vec2(cos(t*theta),sin(t*theta));
    d = min(d,line(z,p,q));
  }
  return d;
}

// Maybe use dual numbers here?
// I did the diff for card by hand, but it got tedious for the polar,
// so it's numeric all the way (it's more generic anyway).
// Some macrology instead of higher order functions.
#define GRAD(F,Z,EPS)                      \
  (vec2(F(Z+vec2(EPS,0))-F(Z-vec2(EPS,0)), \
        F(Z+vec2(0,EPS))-F(Z-vec2(0,EPS)))/(2.0*EPS))

float card(vec2 p) {
  // Implicit function for cardioid
  float a = 1.0/3.0; // Line up with the unit circle
  p.x += a;
  float x = p.x, y = p.y;
  float t = x*x+y*y;
  return t*t - 4.0*a*x*t - 4.0*a*a*y*y;
}

vec2 cardgrad(vec2 z) {
#define F(Z) (card(Z))
  return GRAD(F,z,0.01);
#undef F
}

float carddist(vec2 z) {
  float d = card(z);
  vec2 ds = cardgrad(z);
  d /= length(ds);
  return d;
}

float polar(vec2 z, vec2 w) {
  float eps = 0.01;
  vec2 e = vec2(eps,0);
  return dot(vec2(1),cardgrad(z)*(w-z));
}

vec2 polargrad(vec2 z, vec2 w) {
#define F(Z) (polar(Z,w))
  return GRAD(F,z,0.01);
#undef F
}

float polardist(vec2 z, vec2 w) {
  float d = polar(z,w);
  vec2 ds = polargrad(z,w);
  return d/length(ds);
}

float line(vec3 p, vec3 l) {
  return abs(dot(p,l)/(p.z*length(l.xy)));
}

float graph(vec2 z, vec2 w) {
  // Draw curve, y = f(x), ie. f(x)-y = 0
  float t = PI*z.x;
  float y = 6.0*z.y;
  vec3 p0 = vec3(cos(t),sin(t),1);
  vec3 p1 = vec3(cos(2.0*t),sin(2.0*t),1);
  vec3 l = vec3(sin(t)-sin(2.0*t),
                cos(2.0*t)-cos(t),
                cos(t)*sin(2.0*t)-sin(t)*cos(2.0*t));
  return dot(vec3(w,1),l) - y;
}

vec2 graphgrad(vec2 z, vec2 w) {
#define F(Z) (graph(Z,w))
  return GRAD(F,z,0.01);
#undef F
}

float graphdist(vec2 z, vec2 w) {
  return graph(z,w)/length(graphgrad(z,w));
}

bool key(int code) {
  return texelFetch(iChannel3, ivec2(code,2),0).x != 0.0;
}

const int CHAR_G = 71;

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  int AA = 2;
  vec3 col = vec3(0);
  vec2 w;
  if (iMouse.x > 0.0) w = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  else w = cos(0.5*iTime-vec2(0,0.4*PI));
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
      float ldist = graphdist(z,w);
      float lmin = 0.002, lmax = max(0.003,fwidth(z.x));
      float pmin = 0.015, pmax = max(0.02,2.0*fwidth(z.x));
      vec3 c = vec3(0.75);
      c.rg += 0.25*cos(2.0*PI*8.0*polardist(z,w));
      c.b += 0.25*cos(2.0*PI*8.0*carddist(z));
      c = mix(c,vec3(0.1),1.0-smoothstep(lmin,lmax,abs(card0(z))));
      c = mix(c,vec3(0,0,1),1.0-smoothstep(lmin,lmax,abs(carddist(z))));
      c = mix(c,vec3(1,0,0),1.0-smoothstep(lmin,lmax,abs(polardist(z,w))));
      if (!key(CHAR_G)) {
          c *= 0.8;
          c = mix(c,vec3(1),1.0-smoothstep(lmin,lmax,abs(ldist)));
          c = mix(c,vec3(1),1.0-smoothstep(lmin,lmax,line(z,vec2(-1,0),vec2(1,0))));
          c = mix(c,vec3(1),1.0-smoothstep(lmin,lmax,line(z,vec2(-1,0),vec2(-1,1))));
          c = mix(c,vec3(1),1.0-smoothstep(lmin,lmax,line(z,vec2(1,0),vec2(1,1))));
      }
      c = mix(c,vec3(0),1.0-smoothstep(pmin,pmax,distance(z,w)));
      col += c;
    }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1);
}

