    vec2 stTiled = fract(stScaled); // Wrap arround 1.0

    vec2 posFrag = posFragInsideGrid(stScaled);
    float randomRadius = rand2(posFrag)*rand2(posFrag); // random according to the position in grid.

   	float variationRadius = 4.0*(cos(iTime)+1.0)/2.0;

    color += vec3(circle(stTiled,randomRadius*variationRadius))*vec3(0.5,0.5,0.5);
	fragColor = vec4(color,1.0);
}

