    Intersection isect = sdf3D(rayDir, eye);

    float focalLength = (sin(iTime) *  FOCAL_RANGE +  FOCAL_RANGE) / 2.0;

    vec3 focalLine = isect.p - eye;
    float distance = length(focalLine);
