    float t = abs(disAlongCamZ - focalLength) / FOCAL_RANGE;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(0.5 * (rayDir + vec3(1., 1., 1.)), 1.0);
    fragColor = vec4(isect.color, t);
}

// ref: http://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

void pR(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float circle( vec2 p, float r) 
{
    return length(p) - r;
}

float sdUnevenCapsule( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));
    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
    return dot(p, vec2(a,b) ) - r1;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{  
    vec2 p = fragCoord / iResolution.xy * 2.0 - 1.0;
    p.x *= iResolution.x / iResolution.y;

    p *= 0.75;
    float time = iTime;
    vec2 p1 = p;
    pR(p1, time);

    p1 += vec2(0.2*sin(time), -0.25*cos(time));
    time*=sqrt(0.5);
    vec2 p2 = p + vec2(0.25*sin(time+2.0), 0.1*cos(time));
    time*=sqrt(0.75);
    vec2 p3 = p + vec2(0.1*sin(time), 0.3*cos(time));

    float d1 = sdUnevenCapsule(p1, .15, 0.25, 0.3);
    float d2 = circle(p2, .15);
    float d3 = circle(p3, .25);
    float d = d1;
    d = smin(d, d3, 10.0);
    d = opSmoothSubtraction(d2, d, 0.4);

    fragColor = mix(vec4(0.5, 0.8, 0.3, 1), vec4(0.5, 0.2, 0.7, 1.0), 1.0);

    fragColor += pow(abs(sin(d*2.0)), 0.25 + abs(sin(iTime*0.1)));
}

