float smoothNoise(vec2 uv)
{
	vec2 luv = fract(uv);
    vec2 lid = floor(uv);

    luv = luv*luv*(3.-2.*luv);

    float bl = random(lid);
    float br = random(lid + vec2(1,0));
    float b = mix(bl, br, luv.x);

    float tl = random(lid + vec2(0,1));
    float tr = random(lid + vec2(1,1));
    float t = mix(tl, tr, luv.x);

    return mix(b, t, luv.y);
}

float smoothNoiseLayered(vec2 uv, float startingZoom)
{
	float o = smoothNoise(uv*startingZoom);
    //o += smoothNoise(uv * 2.) * 1./2.;
    //o += smoothNoise(uv * 4.) * 1./4.;
    o += smoothNoise(uv * startingZoom * 2.) * 1./(2.);
    o += smoothNoise(uv * startingZoom * 4.) * 1./(4.);
    o += smoothNoise(uv * startingZoom * 8.) * 1./(8.);
    o += smoothNoise(uv * startingZoom * 16.) * 1./(16.);

    return o / 2.;
}

