float stroke(float x, float s, float w) {
    float feather = 0.005;
    // float d = step(s, x + w*0.5) - step(s, x - w*0.5);
    float d = smoothstep(s - feather, s + feather, x + w*0.5) - smoothstep(s - feather, s + feather, x - w*0.5);
    return clamp(d, 0., 1.);
}

float sinN(float x) {
    return (sin(x) + 1.) / 2.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 pos = ( 2.*fragCoord - R ) / min(R.x,R.y);

    vec3 color = vec3(0.);

    float color_divisor = 6.;
    float w_max = .1;
    float nudge = .05;

    for (int i = 0; i < 10; i++) {
        float w = sinN(iTime * 1.5 + pos.x - pos.y) * w_max;

        float sdf = (pos.x + float(i) * (w_max + nudge) - pos.y);
        color += stroke(sdf, 0., w) / color_divisor;

        sdf = (pos.x - float(i) * (w_max + nudge) - pos.y);
        color += stroke(sdf, 0., w) / color_divisor;
    }

    for (int i = 0; i < 10; i++) {
        float w = sinN(iTime * 1.5 + pos.x + pos.y) * w_max;

        float sdf_inv = (pos.x + float(i) * (w_max + nudge) + pos.y);
        color += stroke(sdf_inv, 0., w) / color_divisor;

        sdf_inv = (pos.x - float(i) * (w_max + nudge) + pos.y);
        color += stroke(sdf_inv, 0., w) / color_divisor;
    }

    color *= vec3(sinN(iTime * 0.65 + pos.y) * 0.65, 0., 0.5);


    // https://learnopengl.com/Advanced-Lighting/Gamma-Correction
    float gamma = 1.8;
    color = pow(color, vec3(1.0/gamma));

	fragColor = vec4(color, 1.);
}

vec2 hash22(vec2 p)
{
	p = vec2(dot(p, vec2(127.1, 311.7)),
		dot(p, vec2(269.5, 183.3)));

	return -1.0 + 2.0 * fract(sin(p)*43758.5453123);
}
float perlin_noise(vec2 p)
{
	vec2 pi = floor(p);
	vec2 pf = p - pi;

	vec2 w = pf * pf * (3.0 - 2.0 * pf);

	return mix(mix(dot(hash22(pi + vec2(0.0, 0.0)), pf - vec2(0.0, 0.0)),
		dot(hash22(pi + vec2(1.0, 0.0)), pf - vec2(1.0, 0.0)), w.x),
		mix(dot(hash22(pi + vec2(0.0, 1.0)), pf - vec2(0.0, 1.0)),
			dot(hash22(pi + vec2(1.0, 1.0)), pf - vec2(1.0, 1.0)), w.x),
		w.y);
}

float noise_sum(vec2 p)
{
	float f = 0.0;
	p = p * 4.0;
	f += 1.0000 * perlin_noise(p); p = 2.0 * p;
	f += 0.5000 * perlin_noise(p); p = 2.0 * p;
	f += 0.2500 * perlin_noise(p); p = 2.0 * p;
	f += 0.1250 * perlin_noise(p); p = 2.0 * p;
	f += 0.0625 * perlin_noise(p); p = 2.0 * p;

	return f;
}

