vec2 rand2d(vec2 p) {
    return fract(sin(vec2(
        dot(p, vec2(12.345, 67.890)),
        dot(p, vec2(190.61, 312.21))
    )) * 41235.45) * 2.0 - 1.0;
}

float perlin(vec3 p) {
    vec2 u = floor(p.xz);
    vec2 f = fract(p.xz);
    vec2 s = smoothstep(0.0, 1.0, f);

    vec2 a = rand2d(u);
    vec2 b = rand2d(u + vec2(1.0, 0.0));
    vec2 c = rand2d(u + vec2(0.0, 1.0));
    vec2 d = rand2d(u + vec2(1.0, 1.0));

    return mix(
        mix(dot(a, f), dot(b, f - vec2(1.0, 0.0)), s.x),
        mix(dot(c, f - vec2(0.0, 1.0)), dot(d, f - vec2(1.0, 1.0)), s.x),
        s.y
    );
}

float fbm(vec3 p) {
    p.xz *= 2.0;
    int octaves = 2;
    float amplitude = 0.5;
    float value = 0.0;
    for (int i = 0; i < octaves; i++) {
        p += iTime * vec3(0.1, 0.0, 0.3);
        value += amplitude * perlin(p);
        p *= 2.0;
        amplitude *= 0.5;
    }
    return value;
}

float water(vec3 p) {
    p.xz *= 0.2;
    float f = fbm(p);
    float g = fbm(p + vec3(0.5, 1.0, 0.2));
    return p.y + fbm(vec3(f, 0.0, g)) - 0.5;
}

float distToCenter(vec3 p) {
    return clamp(1.5 - length(p.xz) / 5.0, 0.0, 1.5);
}

float sol(vec3 p) {
    float dtm = distToCenter(p);
    float per = perlin(p * 0.7) * 1.0 + 3.5;
    return p.y - per * dtm;
//    return p.y + distToMiddle;
}

vec2 map(vec3 p) {
    float closest = 1000.0;
    float id = -1.0;

    float dist = water(p);
    if (dist < closest) { closest = dist; id = 0.5; }

    dist = sol(p);
    if (dist < closest) { closest = dist; id = 1.5; }

    return vec2(closest, id);
}

vec2 intersect(vec3 ro, vec3 rd) {
    float depth = 0.0;
    float id = -1.0;
    for (int i = 0; i < 160; i++) {
        vec2 info = map(ro + rd * depth);
        if (info.x <= 0.001) {
            id = info.y;
            break;
        }
        depth += info.x;
    }
    return vec2(depth, id);
}

vec3 getIslandColor(vec3 p) {
    vec3 baseColor = vec3(0.5, 0.21, 0.1);
    float total = 1.0;
    float sandiness = 1.0 - clamp((p.y - 0.2) * (perlin(p) * 0.5 + 0.5) * 1.3, 0.0, 1.0);
    baseColor = mix(baseColor, vec3(0.76, 0.69, 0.5), sandiness);

    float grassiness = (total - sandiness) * clamp(1.0 - (p.y - 3.87), 0.0, 1.0);
    baseColor = mix(baseColor, vec3(0.28, 0.6, 0.21), grassiness);

    baseColor = mix(baseColor, vec3(1.0, 1.0, 1.0), total - sandiness - grassiness);
//    float dtm = distToMiddle(p);
    return vec3(baseColor);
}

vec3 getSkyColor(vec3 p, vec3 rd) {
    vec3 skyColor = vec3(0.3, 0.65, 0.99);
    skyColor = mix(skyColor, vec3(1.0), pow(clamp(rd.y, 0.0, 1.0), 0.7));
    return skyColor;
}

vec3 getColor(float id, vec3 p, vec3 rd) {
    if (id < -0.5) { return getSkyColor(p, rd); }
    if (id < 1.0) { return vec3(0.21, 0.67, 0.89) * 0.7; }
    if (id < 2.0) { return getIslandColor(p); }
    return vec3(1.0, 0.0, 0.0);
}

vec3 getNormal(vec3 p) {
    const float epsilon = 0.001;
    return normalize(vec3(
        map(vec3(p.x + epsilon, p.yz)).x - map(vec3(p.x - epsilon, p.yz)).x,
        map(vec3(p.x, p.y + epsilon, p.z)).x - map(vec3(p.x, p.y - epsilon, p.z)).x,
        map(vec3(p.x, p.y, p.z + epsilon)).x - map(vec3(p.x, p.y, p.z - epsilon)).x
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    vec3 ro = vec3(13.0 - (sin(iTime) * 0.5 + 0.5), 3.0 + sin(iTime * 0.5), 1.0 + cos(iTime));
    vec3 center = vec3(0.0, 1.5, 0.0);

    vec3 lightDir = normalize(vec3(1.0, 1.0, 1.0));

    vec3 front = normalize(center - ro);
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));
    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec2 info = intersect(ro, rd);

    vec3 pos = ro + rd * info.x;

    float foginess = 1.0 - clamp(distToCenter(pos) * 6.0, 0.0, 1.0);
    if (foginess >= 1.0) {
        vec3 objColor = getSkyColor(pos, rd);
        objColor = pow(objColor, vec3(0.4545));
        fragColor = vec4(objColor, 1.0);
        return;
    }
    vec3 n = getNormal(pos);

    float ambient = 1.0;
    float diffuse = max(dot(n, lightDir), 0.0);
    float dome = 0.2 + 0.8 * clamp(n.y, 0.0, 1.0);
    float back = max(dot(n, vec3(-lightDir.x, 0.0, -lightDir.z)), 0.0);

    vec3 light = vec3(0.0);
    light += ambient * vec3(0.1, 0.1, 0.1);
    light += diffuse * vec3(1.0, 1.0, 1.12);
    light += dome * vec3(0.1, 0.1, 0.4);
    light += back * vec3(5.0, 2.9, 2.0);

    if (info.y < -0.5) {
        light = vec3(1.0);
    }

    vec3 objColor = getColor(info.y, pos, rd) * light;
    objColor = mix(objColor, getSkyColor(pos, rd), foginess);
    objColor = pow(objColor, vec3(0.4545));

    fragColor = vec4(objColor, 1.0);
}

