// SDF of a shape made of a set line and arc segments
float sdShape( in vec2 p, int kType[5], float kPath[13] )
//float sdShape( in vec2 p, int kType[2], float kPath[7] )
{
    vec2 vb = vec2(kPath[0],kPath[1]);

    float d = dot2(p-vb);
    int off = 0;
    float s = 1.0;
    for( int i=0; i<kType.length(); i++ )
    {
        vec2 va = vb;
        vec2 ds;

        if( kType[i]==0) // line (x,y)
        {
            vb = vec2(kPath[off+2],kPath[off+3]);
            ds = sdSqLine( p, va, vb );
            off += 2;
        }
        else if( kType[i]==1 || kType[i]==2 ) // arc (x,y,r)
        {
            vb = vec2(kPath[off+3],kPath[off+4]);
            ds = sdSqArc(p, va, vb, kPath[off+2], d );
        	off += 3;

        }

        // in/out test
        if ( kType[i]==2 )
        {
            bvec3 cond = bvec3( p.y<va.y, p.y<vb.y, ds.y>0.0 );
            if( all(cond) || all(not(cond)) ) s*=-1.0;
        }
        else
        {
            bvec3 cond = bvec3( p.y>=va.y, p.y<vb.y, ds.y>0.0 );
            if( all(cond) || all(not(cond)) ) s*=-1.0;
        }

        d = min( d, ds.x );
    }

    if( p.y > 0. ) s*=-1.0;

	return s*sqrt(d);
}

float sdA( in vec2 p )
{
    float width = 1.;
    float height = 1.;

   	int kType[] = int[](0,0,2,0,0);
	float kPath[] = float[](  0.0,  height*0.5,
                              width*0.5, height*0.5,
                              width*0.5, 0.0, 0.0,
                             -width*0.5, 0.0,
                              0.0, 0.0,
                              0.0,  height*0.5 );
    /*int kType[] = int[](1,0);
	float kPath[] = float[](  width*0.5, 0.0, 0.0,
                             -width*0.5, 0.0,
                              width*0.5, 0.0 );*/
    return sdShape(p,kType,kPath );
}

