void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 p2 = p;

    // distance computations
    float lineWidth = 0.08;

    float dMask = sdA(p);

    float t = mod(iTime * 0.5, 1.) * PI * 2.;

    p += vec2(-sin(t), cos(t)) * 0.08;
    float transform = sign(mod(p.x, lineWidth) / lineWidth - 0.5);
    float d = sdA(p);

    p2 -= vec2(-sin(t), cos(t)) * 0.08;
    float transform2 = sign(mod(p2.x, lineWidth) / lineWidth - 0.5);
    float d2 = sdA(p2);

    vec3 col = vec3(1.0) - sign(d)*vec3(1.0);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.15,abs(d)) );
    col *= transform;

    vec3 col2 = vec3(1.0) - sign(d2)*vec3(1.0);
	col2 = mix( col2, vec3(1.0), 1.0-smoothstep(0.0,0.15,abs(d2)) );
    col2 *= transform2;

    vec3 color = col + col2;

    float mask = 1. - sign(dMask);
	mask = mix(mask, 1., 1.0-smoothstep(0.0,0.05,abs(dMask)));

    //color = (1.-color)*mask + color*(1.-mask);
    color *= 1.-mask;

    // output
	fragColor = vec4(color, 1.0);
}

