{
    float t = iTime * TIME_SCALE;
    vec2 fc = rotate(ANGLE) * fragCoord;
    float x = fc.x; // + t * 4.0;
    float y = fc.y - Y_POSITION;

    // SINGLE ELEMENT COORD

    vec2 elCoord = vec2(mod(x, SPACE) - SPACE * 0.5, y);
    x = floor(x / SPACE) * SPACE + t;

    // CALC

    float angle = -x * 0.097;
    float sinV = sin(angle);
    float cosV = cos(angle);

    // POSITIONS

    vec2 p1 = vec2(cosV, sinV);
    vec2 p2 = vec2(-cosV, -sinV);

    // CONDITIONS

    bool condA = p1.x < p2.x; // amplitude condition
    bool condI = p1.y < p2.y; // intersection condition

    // DEPTHS

    float lineDepth = mix(range(p1.x), range(p2.x), linstep(p1.y * RADIUS, p2.y * RADIUS, y));
    float sq1Depth = range(p1.x);
    float sq2Depth = range(p2.x);

    // SHAPES : LINE

    float lineHeight = abs(sinV) * RADIUS * 2.0 + 1.0;   
    float line = rectangle(elCoord,  vec2(0.0), vec2(WIDTH, lineHeight), lineDepth * 3.0); 

    // SHAPES : SQUARES

    float sqSize1 = mix(SQUARE_SIZE, MIN_SQUARE_SIZE, sq1Depth);
    float sqSize2 = mix(SQUARE_SIZE, MIN_SQUARE_SIZE, sq2Depth);

    float[2] sq = float[2](
    	rectangle(elCoord,  vec2(0.0, p1.y * RADIUS), vec2(sqSize1), sq1Depth * 3.0),
    	rectangle(elCoord,  vec2(0.0, p2.y * RADIUS), vec2(sqSize2), sq2Depth * 3.0)
    );

    // COLORS

    vec3[2] sqColors = vec3[2](
        mix(COLOR, SHADOW, sq1Depth),
        mix(COLOR, SHADOW, sq2Depth)
    );
    vec3 lineColor = mix(COLOR, SHADOW, lineDepth);

    vec3 color = mix(BACK, lineColor, float(line));
    color = mix(mix(color, sqColors[int(condA)], float(sq[int(condA)])), sqColors[int(!condA)], sq[int(!condA)]);

    fragColor = vec4(color, 0.0);

}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define PI 3.141592653589793
#define USE_MOUSE 0
#define MAX_STEPS 126
#define MAX_DIST 30.
#define SURF_DIST .002
#define framethickness 0.1
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// https://www.shadertoy.com/view/wdGXzK
// http://mercury.sexy/hg_sdf/
vec2 pModPolar(inout vec2 p, float repetitions, float fix) {
    float angle = 2.0*PI/repetitions;
    float a = atan(p.y, p.x) + angle/2.;
    float r = length(p);
    float c = floor(a/angle);
    a = mod(a,angle) - angle/2.*fix;
    p = vec2(cos(a), sin(a))*r;

    return p;
}

float frameDist1(vec3 p) {
    float size = 1.5;
    float thickness = framethickness;
    vec4 a1 = vec4(-0.6,size,0.0,0.0); // btm
    float p1 = dot(p,a1.xyz) + a1.w;

    vec4 a2 = vec4(0.0,-size,0.0,1.0); // top
    float p2 = dot(p,a2.xyz) + a2.w;

    vec4 a3 = vec4(size,2.0,0.0,0.8); // right
    float p3 = dot(p,a3.xyz) + a3.w;

    vec4 a4 = vec4(-size,0.0,0.0,0.8); // left
    float p4 = dot(p,a4.xyz) + a4.w;

    vec4 a5 = vec4(0.0,0.0,-size,thickness); // foward
    float p5 = dot(p,a5.xyz) + a5.w;

    vec4 a6 = vec4(0.0,0.0,size,thickness); // back
    float p6 = dot(p,a6.xyz) + a6.w;

    float d = max(-p1,max(-p2,max(-p3,max(-p4,max(-p5,-p6)))));
    return d;
}

