mat3 rotateByEuler(vec3 euler){
	float x = euler.x, y = euler.y, z = euler.z;
    float a = cos( x ), b = sin( x );
	float c = cos( y ), d = sin( y );
	float e = cos( z ), f = sin( z );
    float ae = a * e, af = a * f, be = b * e, bf = b * f;
	return  mat3(
    	vec3(c * e,af + be * d,bf - ae * d),
		vec3( - c * f,ae - bf * d,be + af * d),
        vec3(d,- b * c,a * c)
    );
}

mat3 rotateX(float angle){
    float c = cos(angle);
    float s = sin(angle);

	return  mat3(
    	vec3(1.,0.,0.),
		vec3(0.,c,-s),
        vec3(0.,s,c)
    );
}

mat3 rotateY(float angle){
    float c = cos(angle);
    float s = sin(angle);

	return  mat3(
    	vec3(c,0.,s),
		vec3(0.,1.,0.),
        vec3(-s,0.,c)
    );
}

mat3 rotateZ(float angle){
    float c = cos(angle);
    float s = sin(angle);

	return  mat3(
    	vec3(c,-s,0.),
		vec3(s,c,0.),
        vec3(0.,0.,0.)
    );
}

mat3 rotateAxis(vec3 axis,float rad){
	float c = cos( rad );
	float s = sin( rad );
	float t = 1. - c;
	float x = axis.x, y = axis.y, z = axis.z;
	float tx = t * x, ty = t * y;

	return  mat3(
    	vec3(tx * x + c,tx * y + s * z, tx * z - s * y),
		vec3(tx * y - s * z,ty * y + c,ty * z + s * x),
        vec3(tx * z + s * y,ty * z - s * x,t * z * z + c)
    );
}

