//https://www.shadertoy.com/view/Xls3R7
float blob(float d1,float d2,float d3,float d4){
    float k = 3.;
	return -log(exp(-k*d1)+exp(-k*d2)+exp(-k*d3)+exp(-k*d4))/k;
}

float sceneSDF(vec3 p){
    float x = 3.0*sin(iTime*2.);
    float z = 3.0*cos(iTime*2.);
    vec3 a = vec3(0.,-z-1.,x);
    vec3 b = vec3(x,z-1.5,0.);
    vec3 v = vec3(-x,z-1.5,-.5);

	float sphere = sphereSDF(p + a,2.);
    float sphere1 = sphereSDF(p + b,2.);
    float box = boxSDF(p + v,1.,1.,1.);
    float plane = boxSDF(p + vec3(.0,2.,0.),10.,.2,10.);
   	return blob(sphere,box,sphere1,plane);
}

//http://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal(vec3 p )
{
    const float h = 0.0001;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sceneSDF( p + k.xyy*h ) + 
                      k.yyx*sceneSDF( p + k.yyx*h ) + 
                      k.yxy*sceneSDF( p + k.yxy*h ) + 
                      k.xxx*sceneSDF( p + k.xxx*h ) );
}

vec3 ray(vec2 uv,float fovy,float aspect){
    float z = -1./tan(fovy/2.);
    return normalize(vec3(uv.x*aspect,uv.y,z));
}

float rayCast(vec3 ro,vec3 rd){
   float depth = 0.;
   for(int i=0;i<MAX_MARCHING_STEPS&&depth<MAX_DEPTH;i++){
      vec3 pos = ro + rd * depth;
      float distance = sceneSDF(pos);
      depth+=distance;
   }
   return depth;
}


ReflectedLight calcReflectedLight(
    Camera camera,
    DirectionalLight directionalLights[DIRECTIONAL_LIGHT_NUM],
	PointLight pointLights[POINT_LIGHT_NUM],
    Geometry geometry,
    Material material
){
    vec3 diffuse = vec3(0.);
    vec3 specular = vec3(0.);

    vec3 position = geometry.position;
    vec3 mDiffuse = material.diffuse;
    vec3 mSpecular = material.specular;
    float shininess = material.shininess;

    vec3 N = geometry.normal;
    vec3 V = camera.eye - position;

    ReflectedLight reflectedLight;
    for(int i =0;i<DIRECTIONAL_LIGHT_NUM;++i){
    	DirectionalLight directionalLight = directionalLights[i];

        vec3 dDiffuse = directionalLight.diffuse;
        vec3 dSpecular = directionalLight.specular;

        float intensity = directionalLight.intensity;

        vec3 L = -normalize(directionalLight.dir);
        vec3 R = reflect(-L,N);
        vec3 H = normalize(V + L);

        float dotNL = dot(N,L);
        float dotNH = dot(N,H);
        float spec = pow(max(0.,dotNH),shininess);

        diffuse += dDiffuse*dotNL*mDiffuse*intensity;
        specular += dSpecular*spec*mSpecular*intensity;
    }

    reflectedLight.directDiffuse = diffuse;
    reflectedLight.directSpecular = specular;
    return reflectedLight;  
}

