#define PI (3.141)
#define MAX_ITERATIONS  (128)

// Translate
vec2 translate(vec2 uv, vec2 t) {
    return uv - t;
}

// 2d rotation matrix
mat2 rotate2d(float theta) {
    return mat2(cos(theta), -sin(theta),
                sin(theta), cos(theta));
}

// Rectangle centered at (0, 0)
float rectSdf(vec2 uv, vec2 halfSize) {
    vec2 edgeDist = abs(uv) - halfSize;
    float inDist = min(max(edgeDist.x, edgeDist.y), 0.);
    float outDist = length(max(edgeDist, 0.));
    return inDist + outDist;
}

// 1. if x in [a, b], else 0.
float inInterval(float x, float a, float b) {
    return step(a, x) - step(b, x);
}

// 1 if p in box centered at c, else 0.
float inBox(vec2 p, vec2 c, vec2 halfSize) {
    float xInBox = inInterval(p.x, c.x - halfSize.x, c.x + halfSize.x);
    float yInBox = inInterval(p.y, c.y - halfSize.y, c.y + halfSize.y);
    return xInBox * yInBox;
}

float AND(float a, float b) {
    return a*b;
}

float NOT(float a) {
    return 1. - a;
}

// Remap a square centered at c with given scale to the unit square
// Useful for creating scaled replicas around the plane
vec2 mapToUnitSquare(vec2 uv, vec2 c, vec2 scale) {
    // Rescale so entire replication square maps to biunit square
    vec2 scaleUv = uv.xy / scale.xy;
    // Remap if in target square, but preserve biunit square
    float inRemapBox = inBox(
        scaleUv,
        c,
        vec2(1.)
    );
    float inUnitSquare = inBox(
    	uv,
        vec2(0.), 
        vec2(1.)
    );
    float shouldRemap = AND(inRemapBox, NOT(inUnitSquare));
    return
        // If should remap, move back to origin
        (shouldRemap * translate(scaleUv, c))
        // Else return original coordinate for points outside biunit square
        + (NOT(shouldRemap) * uv);
}

// Single Sierpinski IFS iteration
// Scale space by 0.5, create 3 copies in triangle
vec2 sierpinski1(vec2 uv) {
    vec2 scaledCoords = (2.*uv) + vec2(0., -1.);
    vec2 withIdentifiedBl = mapToUnitSquare(
        scaledCoords,
        vec2(-1, -2),
        vec2(1.)
    );
    vec2 withIdentifiedBr = mapToUnitSquare(
        withIdentifiedBl,
        vec2(1, -2),
        vec2(1.)
    );
    return withIdentifiedBr;
}

// N iterations of Sierpinski IFS
vec2 sierpinskiCoords(vec2 uv, int iterations) {
    for(int i = 0; i < MAX_ITERATIONS; i++) {
        if (i >= iterations) { break; }
        uv = sierpinski1(uv);
    }
    return uv;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Center at (0, 0) 
    uv = translate(uv, vec2(.5));
    // Normalize dimensions
    uv.x *= iResolution.x/iResolution.y;
    // Rescale to unit square
    uv = 2.*uv;

    float time = iTime * 1.5;
    time = mod(time, PI*2.);
    // Remap to [-globalScale -globalScale] x [globalScale, globalScale]
    float globalScale = time;
    vec2 scaleLayer = uv * globalScale;
    scaleLayer *= rotate2d(time);
    scaleLayer = fract(scaleLayer);

    scaleLayer = translate(scaleLayer, vec2(0.5,1.));

    // Mirror [0, 0] x [1, 1] in tiles acrros scaleLayer
    vec2 tileLayer = fract(scaleLayer);

    vec2 sCoords = sierpinskiCoords(scaleLayer, 7);
    float unitSquare = rectSdf(sCoords, vec2(1.));
    float shape = step(0., unitSquare);

    vec3 color = vec3(0.026,0.027,0.200);
	color = mix(color, vec3(0.374,0.541,0.990), (1.-shape));

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    //Angle you want to rotate the texture to  
    float rot = radians(iTime * 45.0);  
    mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));

    vec2 rCoord = (fragCoord.xy - iResolution.xy/vec2(2.0));
    rCoord = (m *rCoord + iResolution.xy/vec2(2.0));
    vec2 uv = rCoord.xy / iResolution.xy;

    if(uv.x>0.0 && uv.y>0.0 && uv.x<1.0 && uv.y<1.0)
    	fragColor = texture(iChannel0, uv);
    else
        fragColor = vec4(1.0,1.0,1.0,1.0);
}

//
// friol 2o2o
// crt effect from https://www.shadertoy.com/view/Ms23DR
// sdf functions and fake AO by iq
// music Clutter by Induktiv
// 02.05.2020: used proper normal for reflections
// 06.05.2020: optimized compilation times a bit
//

const int iterationAmount=256;
const int numBalls=16;
vec4 ballsPositions[numBalls];


float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

vec3 rotx(in vec3 p, float a) 
{
	return vec3(p.x,
                cos(a) * p.y + sin(a) * p.z,
                cos(a) * p.z - sin(a) * p.y);
}

vec3 roty(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.z,
                p.y,
                cos(a) * p.z - sin(a) * p.x);
}

vec3 rotz(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdPlane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

vec2 SDF(vec3 r)
{
    vec3 rOrig=r;
    vec3 rDist=rOrig;
    float mat=0.0;
    float t=0.0;

    float logoDepth=.5;
    float bendAmt=.2;

    //r=roty(r,r.y*sin(iTime)*bendAmt);
    r=rotx(r,3.141592/2.0);
    //float cylouter=sdCappedCylinder(r,vec3(0.0,0.0,0.0),vec3(0.0,.6,0.0),1.0);
    //float cylinner=sdCappedCylinder(r,vec3(0.0,-2.0,0.0),vec3(0.0,1.7,0.0),0.58);

    float cylouter=sdRoundedCylinder(r,0.50,0.05,0.3);
    float cylinner=sdCappedCylinder(r,vec3(0.0,-2.0,0.0),vec3(0.0,logoDepth,0.0),0.58);

    t=opSubtraction(cylinner,cylouter);

    //vec3 rcb=roty(rOrig,rOrig.y*sin(iTime)*bendAmt);
    vec3 rcb=rOrig;
    float cuttingBox=sdBox(rcb-vec3(0.75,0.0,0.0),vec3(0.5,1.0,logoDepth));
	t=opSubtraction(cuttingBox,t);                           

    //vec3 r2=roty(rOrig,rOrig.y*sin(iTime)*bendAmt);
    vec3 r2=rotx(rOrig,3.141592);
    float upperVent=sdRoundBox(r2-vec3(.58,0.25,0.0),vec3(0.3,0.16,.3),0.03);
    t=min(t,upperVent);
    float lowerVent=sdRoundBox(r2-vec3(.58,-0.25,0.0),vec3(0.3,0.16,.3),0.03);
    t=min(t,lowerVent);

    //vec3 r3=roty(rOrig,rOrig.y*sin(iTime)*bendAmt);
    vec3 r3=rotz(rOrig,3.141592/4.01);
    float minusCube=sdBox(r3-vec3(0.8,-0.8,0.0),vec3(.45,.45,.5));

    t=opSubtraction(minusCube,t);
    float cbmLogo=t;

    float floorPlane=sdPlane(rDist-vec3(0.0,-0.1*sin(rDist.x)*2.0*cos(rDist.z),0.0),
                             vec4(0.0,1.0,0.0,1.0));
    t=min(floorPlane,t);

    float teeMin=100.0;
    for (int i=0;i<numBalls;i++)
    {
        float amigaBall=sdSphere(rOrig-vec3(2.0+ballsPositions[i].x,
                                            -0.7+abs(sin(iTime+ballsPositions[i].w)),
                                            ballsPositions[i].z),
                                 			0.4+0.2*ballsPositions[i].w);
        t=min(amigaBall,t);
        teeMin=min(t,teeMin);
    }


    //

    if (t==cbmLogo) mat=0.0;
    else if (t==floorPlane) mat=1.0;
    else if (t==teeMin) mat=2.0;
    if (t==upperVent) mat=3.0;
    if (t==lowerVent) mat=4.0;

    return vec2(t,mat);   
}

vec3 calcNormal(vec3 pos)
{
	vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        // iOS fix
        //vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        vec3 e = 0.5773*(2.0*vec3(( mod(float((i+3)/2),2.0) ),(mod(float(i/2),2.0)),(mod(float(i),2.0)))-1.0);
        n += e*SDF(pos+0.0005*e)[0];
    }
    return normalize(n);
}

vec2 castRay(vec3 rayOrigin, vec3 rayDir)
{
    float t = 0.0;

    for (int i = 0; i < iterationAmount; i++)
    {
        vec2 res = SDF(rayOrigin + rayDir * t);
        if (res[0] < (0.0001*t))
        {
            return vec2(t,res[1]);
        }
        t += res[0];
    }

    return vec2(-1.0,-1.0);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = SDF( aopos )[0];
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

vec3 Sky( vec3 ray )
{
	return mix( vec3(.8), vec3(0), exp2(-(1.0/max(ray.y,.01))*vec3(.4,.6,1.0)) );
}

float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

vec3 fog(vec3 c, float dist, vec3 fxcol)
{
    const float FOG_DENSITY = 0.06;
    vec3 FOG_COLOR = fxcol.xyz;

    float fogAmount = 1.0 - exp(-dist * FOG_DENSITY);

    return mix(c, FOG_COLOR, fogAmount);
}

vec4 bounceRender(vec3 rayOrigin, vec3 rayDir, vec2 uv)
{
    vec3 col=vec3(.52);
    vec3 L=normalize(vec3(1.0,0.2,-2.0));

    vec2 rayHit = castRay(rayOrigin, rayDir);
    float t=rayHit[0];

    if (t>0.0)
    {
    	vec3 pHit=rayOrigin + rayDir * t;
        float mat=rayHit[1];
        vec3 N=calcNormal(rayOrigin + rayDir * t);
        float NoL = max(dot(N, L), 0.0);

		if (mat==0.0)
        {
            col=vec3(NoL)*0.75;
            col+=vec3(.15,.15,.15);
        }        
        else if (mat==1.0)
        {
            float occ = calcAO( pHit, vec3(0.0,1.0,0.0) );
            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=vec3(0.5)*occ;

            float tee = -rayOrigin.y / rayDir.y;

            vec2 P = rayOrigin.xz + t * rayDir.xz;
            vec2 Q = floor(P);
            P = mod(P, 1.0);

            const float gridLineWidth = 0.1;

            float res = clamp(2048.0 / iResolution.y, 1.0, 3.0);
            P = 1.0 - abs(P - 0.5) * 2.0;
            float d = clamp(min(P.x, P.y) / (gridLineWidth * clamp(tee + res * 2.0, 1.0, 2.0)) + 0.5, 0.0, 1.0);

            float shade = mix(hash(120.0 + Q * 0.1) * 0.4, 0.3, min(tee * tee * 0.001, 1.0)) + 0.6;
            vec3 colFloor= vec3(pow(d, 
                           clamp(150.0 / (pow(max(tee - 2.0, 0.1), res) + 1.0), 0.1, 31.0)
                      )) * shade + 0.1;            
            colFloor*=vec3(0.51,0.12,0.23);
            col=mix(colFloor,col,0.5);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
        }
        else if (mat==2.0)
        {
            vec3 q=N;
            vec2 matuv = vec2( atan(N.x,N.z), acos(N.y ) );
            vec2 qp = floor(matuv*2.51);
            float intensity=mod( qp.x+qp.y, 2.0 );
            if (intensity==1.0) col=vec3(1.0,0.0,0.0);
            else col=vec3(1.0);
            //float NoL = max(dot(N, L), 0.2);
            col*=NoL;

            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
        }
        else if ((mat==3.0)||(mat==4.0))
        {
            col=vec3(NoL)*0.75;
            if (mat==4.0) col+=vec3(231.0/256.0,12.0/256.0,52.0/256.0);
            else col+=vec3(0.0,112.0/256.0,232.0/256.0);
            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
            col/=4.0;
        }
    }
    else
    {
        vec3 sk=Sky(rayDir);
        col=vec3(clamp(sk.x,0.0,1.0),clamp(sk.y,0.0,1.0),clamp(sk.z,0.0,1.0));
        //col=vec3(1.0,0.0,0.0);
    }

    //col=pow(col,vec3(0.58));
    return vec4(col,1.0);
}


vec4 render(vec3 rayOrigin, vec3 rayDir, vec2 uv)
{
    vec3 col=vec3(0.);
    vec3 L=normalize(vec3(4.0,0.2,-2.0));

    vec2 rayHit = castRay(rayOrigin, rayDir);
    float t=rayHit[0];
    vec3 N=calcNormal(rayOrigin + rayDir * t);
    float NoL = max(dot(N, L), 0.0);

    if (t>0.0)
    {
    	vec3 pHit=rayOrigin + rayDir * t;
        float mat=rayHit[1];

        if (mat==0.0) // c= logo
        {
            col=vec3(NoL)*0.75;
            col+=vec3(.25,.25,.25);
            vec3 refDir = reflect(rayDir,N);
            vec4 colReflect=bounceRender(pHit,refDir,uv);
            col=mix(col,colReflect.xyz,0.6);

            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
        }
        else if (mat==1.0) // floor
        {
            float occ = calcAO( pHit, vec3(0.0,1.0,0.0) );
            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=vec3(0.5)*occ;

            float tee = -rayOrigin.y / rayDir.y;

            vec2 P = rayOrigin.xz + t * rayDir.xz;
            vec2 Q = floor(P);
            P = mod(P, 1.0);

            const float gridLineWidth = 0.05;

            float res = clamp(2048.0 / iResolution.y, 1.0, 3.0);
            P = 1.0 - abs(P - 0.5) * 2.0;
            float d = clamp(min(P.x, P.y) / (gridLineWidth * clamp(tee + res * 2.0, 1.0, 2.0)) + 0.5, 0.0, 1.0);

            float shade = mix(hash(120.0 + Q * 0.1) * 0.4, 0.3, min(tee * tee * 0.001, 1.0)) + 0.6;
            vec3 colFloor= vec3(pow(d, 
                           clamp(150.0 / (pow(max(tee - 2.0, 0.1), res) + 1.0), 0.1, 31.0)
                      )) * shade + 0.1;            

            colFloor*=vec3(0.51,0.12,0.23);
            col=mix(colFloor,col,0.5);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
        }
        else if (mat==2.0) // amiga balls
        {
            vec3 q=N;
            vec2 matuv = vec2( atan(N.x,N.z), acos(N.y ) );
            vec2 qp = floor(matuv*2.51);
            float intensity=mod( qp.x+qp.y, 2.0 );
            if (intensity==1.0) col=vec3(1.0,0.0,0.0);
            else col=vec3(1.0);
            float NoL = max(dot(N, L), 0.1);
            col*=NoL;
            col+=pow(NoL,32.0);

            vec3 refDir = reflect(rayDir,N);

            vec4 colReflect=bounceRender(pHit,refDir,uv);
            col=mix(col,colReflect.xyz,0.7);

            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
        }
        else if ((mat==3.0)||(mat==4.0))
        {
            col=vec3(NoL)*0.75;
            if (mat==4.0) col+=vec3(231.0/256.0,12.0/256.0,52.0/256.0);
            else col+=vec3(0.0,112.0/256.0,232.0/256.0);
            vec3 refDir = reflect(rayDir,N);
            vec4 colReflect=bounceRender(pHit,refDir,uv);
            col=mix(col,colReflect.xyz,0.6);

            float cdist=pow(distance(pHit,rayOrigin),1.202);
            col=fog(col,pow(cdist,.7642),Sky(rayDir));
        }
        else
        {
            col=vec3(1.0,1.0,0.0);
        }

    }
    else
    {
        col=Sky(rayDir);
    }

    col=pow(col,vec3(0.58));
    return vec4(col,1.0);
}

vec3 getCameraRayDir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    vec3 camForward = normalize(camTarget - camPos);
    vec3 upz=vec3(0.,1.,0.);
    vec3 camRight = normalize(cross(upz, camForward));
    vec3 camUp = normalize(cross(camForward, camRight));

    float fPersp = 2.0;
    vec3 vDir = normalize(uv.x * camRight + uv.y * camUp + camForward * fPersp);

    return vDir;
}

vec2 normalizeScreenCoords(vec2 screenCoord)
{
    vec2 result = 2.0 * (screenCoord/iResolution.xy - 0.5);
    result.x *= iResolution.x/iResolution.y;
    return result;
}

float onelinerRandom(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void initBalls()
{
    float ballSpread=16.0;
    int seed=234;
    for (int b=0;b<numBalls;b++)
    {
        float x=-ballSpread/2.0+onelinerRandom(vec2(seed))*ballSpread; seed+=0x42;
        float y=onelinerRandom(vec2(seed))*3.141592*2.0; seed+=0x42;
        float z=-ballSpread/2.0+onelinerRandom(vec2(seed))*ballSpread; seed+=0x42;
        float delta=onelinerRandom(vec2(seed));
		ballsPositions[b]=vec4(x,y,z,delta);        
    }
}

vec2 curve(vec2 uv)
{
	uv = (uv - 0.5) * 2.0;
	uv *= 1.1;	
	uv.x *= 1.0 + pow((abs(uv.y) / 5.0), 2.0);
	uv.y *= 1.0 + pow((abs(uv.x) / 4.0), 2.0);
	uv  = (uv / 2.0) + 0.5;
	uv =  uv *0.92 + 0.04;
	return uv;
}

