//off makes unlit surfaces still show bit of colour
float diffuse(vec3 n, vec3 l){
    l = normalize(l);
    float off = 0.03;
	return max(0.,dot(n,l)*.9)+off;
}

//higher s = larger effect area
//higher e = more defined spec
//higher m = scaled down overall lighting
float spec(vec3 n, vec3 ro, vec3 rd, vec3 l,vec3 p){
    l = normalize(l);
    vec3 v = rd;
    vec3 r = reflect(l,n);
    float s = 0.15;
    float e = 10.;
    float m = 2.;

    //clamp before exp or you get light on both sides..
    return pow(clamp(dot(v,r)-s,0.,1.),e)/m;
}


float map(vec3 p){
    float c =length(p)-0.25;

	return c;

}

vec3 calcNormal(vec3 p){
	vec2 e = vec2(0.001,0.);
    return normalize(vec3(map(p+e.xyy)-map(p-e.xyy),
                     map(p+e.yxy)-map(p-e.yxy),
                     map(p+e.yyx)-map(p-e.yyx)));
}

vec3 castRay(vec3 ro, vec3 rd, vec3 l){

    float t = 0.;
    for(int i = 0; i<100; i++){
    	vec3 p = ro + t*rd;
        float d = map(p);
        t+=d;
		if(d>10.)
            break;
        if(d<0.01*t*.5){
			vec3 amb = vec3(0.4,0.5,0.6);
            float ll = length(p-l);
            float att=1.0/(1.0+0.1*ll+0.01*ll*ll);
            vec3 n =calcNormal(ro + t*rd); 
        	return att*amb*vec3(diffuse(n, l)+spec(n,ro,rd,l, p) );
            break;
        }
    }
    return vec3(0.);
}

