vec2 rot(vec2 p, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return vec2(p.x*c - p.y*-s, p.x*s+p.y*c);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 c = vec3(0);
    vec2 p = ((fragCoord.xy/iResolution.xy)-.5)*2.;

    c.yz = vec2(.04/abs(.03-length(p)));
    p+=rot(c.xy, iTime);

    float t = atan(p.y, p.x)+iTime*2.5;
    p = rot(rot(p, iTime), t);
    vec2 q = p;
    q*= p*(1.2+sin(t*7.))*.5;  // -1 ~ 1 -> 0 ~ 1
    q*= p*(1.2+cos(t*3.5))*5.;  // -1 ~ 1 -> 0 ~ 1
    vec2 v = vec2(1.+cos(t*10.), 1.+tan(iTime*10.));
    c.x = dot(p, v) / abs(.5-(length(q)*abs(length(q))));
    c = smoothstep(0., .1, c);
    c.yz += p;

    fragColor = vec4(vec3(abs(.5-length(c.x+q.y)), 1.-(c.y*p.x), c.z+q.x+p.y), 1);
}

