//
// Desargues' Theorem.
//
// Matthew Arcus, mla, 2020
//
// If two triangles (blue lines) are in perspective from a point
// (green lines), then corresponding sides meet in colinear points
// (red line).
//
// Mouse moves things around
// Press 'x' for a different perspective on the diagram.
//
////////////////////////////////////////////////////////////////////////////////


const float PI = 3.14159;

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // (unnormalized) normal
  int id;       // what was hit
};

struct Sphere {
  float r2;      // radius squared
  vec3 p;       // centre
  int id;
};

struct Cylinder {
  float r2;      // radius squared
  // points s and t are end points of cylinder
  vec3 s,t;
  int id;
};

// Solve Ax^2 + 2Bx + C = 0
bool quadratic(float A, float B, float C, out vec2 t) {
   float D = B*B - A*C;
   if (D < 0.0) return false;
   D = sqrt(D);
   t.x = B > 0.0 ? -B-D : -B+D;
   t.y = C/t.x;
   t.x = t.x/A;
   if (t.x > t.y) t.xy = t.yx;
   return true;
}

bool intersectSphere(Sphere s, Ray ray, inout Hit hit) {
  vec3 p = s.p;
  float r2 = s.r2;
  float c2 = dot(p,p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float A = 1.0;
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+c2-r2;
  A *= 2.5;B *= 2.5;C *= 2.5;
  vec2 res;
  if (!quadratic(A,B,C,res)) return false;
  float t = res.x;
  if (t < 0.0) t = res.y;
  if (t < 0.0 || t >= hit.t) return false;
  // Normal is the radial vector of sphere
  // We normalize it later
  hit = Hit(t, q+t*d-p, s.id);
  return true;
}

bool cylinder(vec3 q, vec3 s, float r2, vec3 p, vec3 r, out float t, out vec3 normal) {
  vec3 n = s-q; // Line direction
  float k2 = dot(n,n);
  vec3 p1 = p-q; // Move p to line space
  float rs = dot(r,n);
  float ps = dot(p1,n);
  float pr = dot(p1,r);
  float pp = dot(p1,p1);
  float A = 1.0 - rs*rs/k2;
  float B = pr - ps*rs/k2;
  float C = pp - ps*ps/k2 - r2;
  vec2 res;
  if (!quadratic(A,B,C,res)) return false;
  t = res.x;
  if (t < 0.0) t = res.y;
  if (t < 0.0) return false;
  p1 += t*r; // Final point in line space
  float lambda = dot(p1,n)/k2;
  if (lambda < 0.0 || lambda > 1.0) return false;
  normal = p1-lambda*n;
  return true;
}

bool intersectCylinder(Cylinder c, Ray ray, inout Hit hit) {
  vec3 s0 = c.s;
  vec3 s1 = c.t;
  float r2 = c.r2;
  vec3 q = ray.q, d = ray.d;
  float t;
  vec3 normal;
  if (!cylinder(s0,s1,r2,q,d,t,normal) || t >= hit.t) return false;
  // Normal is the radial vector of cylinder
  hit = Hit(t, normal, c.id);
  return true;
}

// (p+P)(q+Q) = pq + pQ + qP + PQ
vec4 qmul(vec4 p, vec4 q) {
  return p.w*q + q.w*p + vec4(cross(p.xyz,q.xyz),-dot(p.xyz,q.xyz));
}

vec3 intersect(vec3 p0,vec3 p1,vec3 q0,vec3 q1) {
  vec3 n = p1-p0;
  vec3 m = q1-q0;
  // Want p0 + kn = q0 + km
  // or
  // kn = q0 - p0 + km
  // k(n x m) = (q0 - p0) x m
  vec3 t1 = cross(n,m);
  vec3 t2 = cross(q0-p0,m);
  float k = dot(t1,t2)/dot(t1,t1);
  return p0+k*n;
}

bool dorotate = true;
bool do3d = false;

Hit intersectScene(Ray ray) {
  // O,A,B,C are arbitrary
  vec3 O = vec3( 0, 1,-1); // Centre of projection
  vec3 A = vec3( 0,-1,-1); // ABC is base triangle
  vec3 B = vec3(-1,-1,-1);
  vec3 C = vec3( 0,-1, 0);
  // G must be on CA, I on AB and D on OA, but exact position arbitrary
  float j = 2.0;
  vec3 G = j*C+(1.0-j)*A; // GHI is intersection line
  vec3 I = j*A+(1.0-j)*B;
  float k = 0.5;
  vec3 D = k*O+(1.0-k)*A; // DEF is perspective triangle
  // The other points are now determined
  vec3 E = intersect(D,I,B,O);
  vec3 F = intersect(G,D,C,O);
  vec3 H = intersect(B,C,I,G);
  Hit hit = Hit(1e8,vec3(0),0);
  float s2 = 0.04*0.04;
  float c2 = 0.015*0.015;
  intersectSphere(Sphere(s2,O,1),ray,hit);
  intersectSphere(Sphere(s2,A,1),ray,hit);
  intersectSphere(Sphere(s2,B,1),ray,hit);
  intersectSphere(Sphere(s2,C,1),ray,hit);
  intersectSphere(Sphere(s2,D,1),ray,hit);
  intersectSphere(Sphere(s2,E,1),ray,hit);
  intersectSphere(Sphere(s2,F,1),ray,hit);
  intersectSphere(Sphere(s2,G,1),ray,hit);
  intersectSphere(Sphere(s2,H,1),ray,hit);
  intersectSphere(Sphere(s2,I,1),ray,hit);
  if (!do3d && hit.t < 1e8) return hit;
  intersectCylinder(Cylinder(c2,A,O,2),ray,hit);
  intersectCylinder(Cylinder(c2,B,O,2),ray,hit);
  intersectCylinder(Cylinder(c2,C,O,2),ray,hit);
  intersectCylinder(Cylinder(c2,A,G,3),ray,hit);
  intersectCylinder(Cylinder(c2,D,G,3),ray,hit);
  intersectCylinder(Cylinder(c2,B,H,3),ray,hit);
  intersectCylinder(Cylinder(c2,E,H,3),ray,hit);
  intersectCylinder(Cylinder(c2,B,I,3),ray,hit);
  intersectCylinder(Cylinder(c2,E,I,3),ray,hit);
  intersectCylinder(Cylinder(c2,G,I,0),ray,hit); // Intersection line
  return hit;
}

vec3 light = vec3(0,0,-1);
float ambient = 0.5;
float diffuse = 0.5;

vec3 getColor(int i) {
  if (i == 0) return vec3(1,0,0);
  if (i == 1) return vec3(1,1,0);
  if (i == 2) return vec3(0,1,0);
  if (i == 3) return vec3(0,0,1);
  if (i == 4) return vec3(0,1,1);
  return vec3(1,1,1);
}

vec3 solve(Ray r) {
  Hit hit = intersectScene(r);
  if (hit.t >= 1e8) {
    return vec3(0);
  } else {
    vec3 n = normalize(hit.n);
    if (dot(r.d,n) > 0.0) n = -n;
    vec3 baseColor = 0.7*getColor(hit.id);
    vec3 color = baseColor;
    if (do3d) {
      color *= ambient+diffuse*max(0.0,dot(light,n));
      float specular = pow(max(0.0,dot(reflect(light,n),r.d)),2.0);
      color += 0.5*specular*vec3(1.0,1.0,1.0);
    }
    return color;
  }
}

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    float t = iTime;
    p.yz = rotate(p.yz,-t*0.223);
    p.zx = rotate(p.zx,-t*0.15);
  }
  return p;
}

