vec4 RotationBetween(vec3 v0, vec3 v1)
{
    // Shortest arc
    float d = dot(v0, v1);
    float s = sqrt((1.0 + d) * 2.0);
    float invs = 1.0 / s;
    vec3 c = cross(v1, v0);
    return vec4(c.xyz * invs, 0.5 * s);
}

vec4 RotationFromZ(vec3 v1)
{
    // Shortest arc from <0, 0, 1>
    int x = floatBitsToInt(2.0 + v1.z * 2.0);
    //x = 0x1FBD1DF5 + (x >> 1);
    // rsqrt from Robin's new math paper, allows one less div
    x = 0x5F37624F - (x >> 1);
    float s = intBitsToFloat(x);
    //return vec4(v1.y / s, -v1.x / s, 0.0, 0.5 * s);
    return vec4(v1.y * s, -v1.x * s, 0.0, 0.5 / s);
}

vec4 nlerp(vec4 q0, vec4 q1, float t)
{
    return normalize(mix(q0, q1, t));
}

vec4 fnlerp(vec4 l, vec4 r, float t)
{
	float ca = dot(l, r);
	float k = 0.931872f + ca * (-1.25654f + ca * 0.331442f);
	float ot = t + t * (t - 0.5f) * (t - 1.0) * k;
    return normalize(mix(l, r, ot));
}

vec4 slerp(vec4 a, vec4 b, float t)
{
    // Get half-angle between quaternions
    float cos_theta = clamp(dot(a, b), -1.0, 1.0);
    float theta = acos(cos_theta);
    float sin_theta = sqrt(1.0 - cos_theta * cos_theta);

    // Slerp
    float t0 = sin((1.0 - t) * theta) / sin_theta;
    float t1 = sin(t * theta) / sin_theta;
    vec4 r;
    r.x = a.x * t0 + b.x * t1;
    r.y = a.y * t0 + b.y * t1;
    r.z = a.z * t0 + b.z * t1;
    r.w = a.w * t0 + b.w * t1;
    return r;
}

vec3 Transform(vec4 q, vec3 v)
{
    return v * (q.w * q.w - dot(q.xyz, q.xyz)) + 2.0 * q.xyz * dot(q.xyz, v) + 2.0 * q.w * cross(q.xyz, v);
    //return v + 2.0 * cross(q.xyz, q.w * v + cross(q.xyz, v));
}

vec3 TransformZ(vec4 q)
{
    // Transforming <0,0,1> by q where q.z = 0 for tangent-space normal
    vec3 r;
    r.x = -2.0 * q.w * q.y;
    r.y =  2.0 * q.w * q.x;
    r.z =  q.w * q.w - dot(q.xy, q.xy);
    return r;

    // c = cross(a, b)
    // c.x = a.y * b.z - b.y * a.z
    // c.y = a.z * b.x - b.z * a.x
    // c.z = a.x * b.y - b.x * a.y
}

