void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.x;

    //---------------------------------------------------------------------------------------------------------
    // [LEFT SIDE] - Combined normal, switches between technique
    //---------------------------------------------------------------------------------------------------------
    // Base Normal (Disk)
    vec2 uvN = uv * (iResolution.x / iResolution.y) * 1.2 - vec2(0.12, 0.16);

  	vec3 BN = vec3(0.5, 0.5, 1.0);
    if (uvN.x > 0.0 && uvN.x < 0.83 && uvN.y < 0.83 && uvN.y > 0.0)
    {
        BN = ComputeBaseNormal(uvN * vec2(1.2,1.2));
    }

    // Detail Normal
	vec2 uvDN = uvN;
    vec3 n1 = ComputeDetailNormal(uvDN);

    float t = 0.5;
    t = fract(iTime * 0.1);

	// Combined Normal
    vec3 N = NormalBlend(BN, n1, t);

    //---------------------------------------------------------------------------------------------------------
    // [RIGHT SIDE] - Combined normal with lighting, switches between technique
    //---------------------------------------------------------------------------------------------------------
    // Base Normal (Disk)
    uvN = (uv + vec2(0.28, 0.0)) * (iResolution.x / iResolution.y) * 1.2 - vec2(0.12, 0.16);

  	BN = vec3(0.5, 0.5, 1.0);
    if (uvN.x > 1.66 && uvN.y > 0.0 && uvN.y < 0.83 && uvN.x < 2.50)
    {
        BN = ComputeBaseNormal(uvN * vec2(1.2,1.2));
    }

    // Detail Normal
	uvDN = uvN+vec2(0.33,0);
    n1 = ComputeDetailNormal(uvDN);

	// Combined Normal
    vec3 N2 = NormalBlend(BN, n1, t);

	vec3 light = normalize(vec3(1.0, 0.0, 1.0));    
    vec3 lit = vec3(clamp(dot(light, N2), 0.0, 1.0));

    // Mix normal and lit result
	vec3 color = mix(N*0.5 + 0.5, lit, float(uv.x > 0.5));

	fragColor = vec4(color, 1.0);
}

