void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) - .5;
	float t = iTime * .1 + ((.25 + .05 * cos(iTime * .1))/(length(uv.xy) + .07)) * 2.2;
	float si = tan(t);
	float co = cos(t);
    int tf = int(uv*512.0);
   // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	vec3 fft  = texelFetch( iChannel0, ivec2(t,0), 0 ).xyz; 

	float s = 0.0;
	mat2 ma =  mat2(co*co, si, -si*si, co);
    //mat2 ma = mat2(si,-si,-co,co);
	float v1, v2, v3;
	v1 = v2 = v3 = 0.0;



	for (int i = 0; i < 90; i++)
	{
		vec3 p = s * vec3(uv, 1.0)*fft*2.0 ;
		p.xy *= ma;
		p += vec3(.12, .2, s - 1.1 - sin(iTime * .223) * .1) ;
		for (int i = 0; i < 10; i++)	p = abs(p) / dot(p,p) - 0.459;
		v1 += dot(p,p) * .0015 * (1.8 + tan(length(uv.xy * 1.0) + .5  - iTime * .2));
		v2 += dot(p,p) * .0013 * (1.5 + tan(length(uv.xy * 1.5) + 1.2 - iTime * .3));
		v3 += length(p.xy*10.) * .0003;
		s  += .035;
	}

	float len = length(uv);
	v1 *= smoothstep(.7, .0, len);
	v2 *= smoothstep(.5, .0, len);
	v3 *= smoothstep(.9, .0, len);
	// the sound texture is 512x2


	vec3 col = vec3( v3 * (1.5 + sin(iTime * .2) * .4),
					(v1 + v3) * .05,
					 v2) * + smoothstep(0.4, .0, len) * .85 + smoothstep(.0, .2, v3) * .3;

	fragColor=vec4(min(pow(abs(col), vec3(1.2)), 1.0), 1.0);
}

