void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 distort = vec2(sin(uv.y * 10.0 + iTime * 3.) * 0.005,0.);
    vec3 col = texture(iChannel0,uv + distort).rgb;
    //vertical lines
    float vlines = floor(uv.x * iResolution.x * 0.5);
    float vrep = mod(vlines ,3.);
    //rgb color
    col += vec3(0.15,0.3,0.45);
    col += vec3(0.5,0.,0.) * float(vrep == 0.);
    col += vec3(0.0,0.5,0.) * float(vrep == 1.);
    col += vec3(0.0,0.,0.5) * float(vrep == 2.);
    //horizontal lines
    float hlines = floor(uv.y * iResolution.y);
    float hrep = mod(hlines,4.);

    col *= step(hrep,2.0);
    col *= sin(sin(iTime * 1.) * 2.) * 0.2 + 0.5;
    col += mod(1. - uv.y * 3.0 - iTime * 0.5,1.0) * 0.2;
    //vign
    vec2 U = pow((uv - 0.5) * 1.9, vec2(5));
    col *= pow(1.0 - dot(U,U), 3.);

    fragColor = vec4(col,1.0);
}

