// Working on this as an oriented basis function for CPPNs
float dv(vec2 uv, int am, float angle) {
    float r = length(uv);

    float th = atan(uv.y, uv.x);

    //float rin = 32.;
    //float rscale = (1.+rin)/rin - 1. / (rin*r + 1.);
    float rscale = tanh(3.*r*r);

    float value = 1.8 + rscale * 0.7*cos(float(am)*th + angle) - r;
    return tanh(value*value);
}

