float RayMarch(vec3 ro, vec3 rd)
{
	float dO=0.2;
    //Determines size of shadow

    for(int i=0; i<MAX_STEPS; i++) 
    {
    	vec3 p = ro + rd*dO;

        float dS = GetDist(p);

        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) 
        {
            //mObj.dist=dO; 	
            mObj.p=p; 	
            gres2 = vec2(dO,dS); 
            break;
    	}    

    }

    return dO;
}


