   	int kType[] = int[](0,0,0,0,0,0,0,0);
	float kPath[] = float[]( -width*0.5,  height*0.5,
                             -width*0.5, -height*0.5,
                             -width*0.0, -height*0.5,
                             -width*0.0, -height*0.1,
                              width*0.5, -height*0.5,
                              width*0.5,  height*0.5,
