struct ray
{
	vec3 o;
	vec3 d;
	float l;
};

vec4 opu(vec4 a, vec4 b)
{
	if(a.w < b.w) return a;
    else return b;
}

vec4 geo(vec3 p)
{
    float sXsin = sin(iTime * 0.8)*0.3; //---Sphere horizontal movement
    float sXsin2 = sin(iTime * 0.8)*0.23;
    float sXsinDouble = 0.2+sin(iTime * 1.5 )*0.1; //---Sphere horizontal movement 2xSpeed
    float sBlob = sin(iTime * 3. + p.y * 8.); //---'Sphere blob'

	vec4 o0 = vec4(0.2,0.6,1.0, length(p - vec3(sXsin,0.,1.5)) - 0.2 + sBlob * 0.0999);
    o0.w *= smoothstep(0.1,0.05, -p.y-0.22) + clamp(abs(sXsin),0.05,0.2); //---The little 'slimy' effect below the 'blob'
    #ifdef WALLS_ENABLED
    o0.w *= smoothstep(0.02,0.01, abs(p.x)*0.02); //---'Wall' generation
    #endif
	vec4 o0eye = vec4(vec3(1.0,1.0,1.0)*1.5, abs(length(p - vec3(sXsin,sXsinDouble,1.2)) - 0.01 + clamp(sBlob,-0.3,-0.1) * 0.0999)*0.45);
    vec4 o0eyeb = vec4(vec3(0.0,0.0,0.0)*1.5, abs(length(p - vec3(sXsin2,sXsinDouble,1.15)) - 0.08 * 0.0999)*0.5);
    vec4 o2 = vec4(0.2,0.4,0.5, p.y + 0.35);

    vec4 g = opu(o2, o0);
    vec4 g2 = opu(g, o0eye);
    vec4 g3 = opu(g2, o0eyeb);
    return g3;
}

vec4 march(ray r)
{
	vec3 col = vec3(1.0);
	for(int i = 0; i < 16; i++)
	{
		vec3 p = r.o + r.d * r.l; //---Traditional march operation
		vec4 g = geo(p);
		r.l += g.w;
		col = g.rgb;
		if(r.l > 8.)
			break;
	}
	return vec4(col, r.l);
}

vec3 normal(vec3 p)
{
	vec2 of = vec2(0.001,0.0);
	vec4 copy = geo(p);
	return normalize(copy.w - vec3(geo(p - of.xyy).w, geo(p - of.yxy).w, geo(p - of.yyx).w)); //---Traditional normals
}

float shadows(vec3 ro, vec3 rd, float b)
{
	float t = 0.01;
	float res = 1.0;
	for(int i = 0; i < 32; i++)
	{
		float g = geo(ro+rd*t).w;
		res = min(res, b * g / t); //---Traditional soft shadows
		t += g;
		if(g < 0.0005|| g > 3.0)
        {
			break;
        }
	}
	return clamp(res,0.1,1.0);
}

float lighting(vec3 p, vec3 lp)
{
	vec3 lPos = normalize(lp - p); //---Light source & uv position difference
	vec3 n = normal(p);

    float spec = pow(max(dot(lPos, n),0.),80.)*0.2; //---Specular Blinn Phong

	float light = clamp(dot(n, lPos), 0.6, 1.); //---Lighter ambient light
	return light * shadows(p, normalize(lp), 1.0) + spec;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy) / iResolution.y;

    ray r;
	r.o = vec3(0.);
	r.d = vec3(uv, 1.0);
	vec4 mm = march(r);

	vec3 m = r.o + r.d * mm.w;
	vec3 lPos = vec3(0.,2.5,1.);
    #ifdef WALLS_ENABLED
    #else
        lPos = vec3(sin(iTime * 1.0)*1.5,1.5,cos(iTime * 1.0)*1.5); //---Light position
    #endif
	vec3 col = mm.rgb * lighting(m, lPos);
	col *= exp(-0.2 * mm.w * mm.w * mm.w * mm.w) *  5.; //---Fog + quick 'fake' gamma
    if(mm.w > 2.5) col = mix(col, vec3(0.2,0.3,0.8), uv.y-0.1); //---Simple background gradient

    fragColor = vec4(col,1.0);
}

