#define TWO_PI          6.283185307179586

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

float circle(vec2 coord, vec2 pos, float r, float edge){
    vec2 tc = pos - coord;
    float radius = length(tc);
    float circle = 1. - smoothstep(r, r+edge, radius);
    return circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    uv *=2.2;
    uv.y -= 0.55;

    vec2 toCenter = -uv;
    float angle = atan(toCenter.y,toCenter.x);
    float radius = length(toCenter)*2.0;

    // Map the angle (-PI to PI) to the Hue (from 0 to 1)
    // and the Saturation to the radius
    col = hsb2rgb(vec3((angle/TWO_PI)+0.5,radius,1.0));
    float circle_shape = circle(uv,vec2(0.),0.5,0.01);
    col*=circle_shape;

    //Modify these lines to change hue and saturation values
    float h = fract(iTime/5.);
    float s = 0.4*(1.+ sin(iTime/4.));

    //Draw point on map
    float r = s/2.;
    float ang = h*TWO_PI;
    vec2 center = r*vec2(cos(ang),sin(ang));
    float point = circle(uv, center, 0.02, 0.01);
    col += point;

    //Draw circle with color
    uv.y +=1.1;
    circle_shape = circle(uv,vec2(0.),0.5,0.01);
    col +=circle_shape*hsb2rgb(vec3(h,s,1.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}

