// Some useful functions
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

//
// Description : GLSL 2D simplex noise function
//      Author : Ian McEwan, Ashima Arts
//  Maintainer : ijm
//     Lastmod : 20110822 (ijm)
//     License :
//  Copyright (C) 2011 Ashima Arts. All rights reserved.
//  Distributed under the MIT License. See LICENSE file.
//  https://github.com/ashima/webgl-noise
//
float snoise(vec2 v) {

    // Precompute values for skewed triangular grid
    const vec4 C = vec4(0.211324865405187,
                        // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,
                        // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,
                        // -1.0 + 2.0 * C.x
                        0.024390243902439);
                        // 1.0 / 41.0

    // First corner (x0)
    vec2 i  = floor(v + dot(v, C.yy));
    vec2 x0 = v - i + dot(i, C.xx);

    // Other two corners (x1, x2)
    vec2 i1 = vec2(0.0);
    i1 = (x0.x > x0.y)? vec2(1.0, 0.0):vec2(0.0, 1.0);
    vec2 x1 = x0.xy + C.xx - i1;
    vec2 x2 = x0.xy + C.zz;

    // Do some permutations to avoid
    // truncation effects in permutation
    i = mod289(i);
    vec3 p = permute(
            permute( i.y + vec3(0.0, i1.y, 1.0))
                + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(
                        dot(x0,x0),
                        dot(x1,x1),
                        dot(x2,x2)
                        ), 0.0);

    m = m*m ;
    m = m*m ;

    // Gradients:
    //  41 pts uniformly over a line, mapped onto a diamond
    //  The ring size 17*17 = 289 is close to a multiple
    //      of 41 (41*7 = 287)

    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;

    // Normalise gradients implicitly by scaling m
    // Approximation of: m *= inversesqrt(a0*a0 + h*h);
    m *= 1.79284291400159 - 0.85373472095314 * (a0*a0+h*h);

    // Compute final noise value at P
    vec3 g = vec3(0.0);
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * vec2(x1.x,x2.x) + h.yz * vec2(x1.y,x2.y);
    return 130.0 * dot(m, g);
}

#define OCTAVES 1
float fpm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = 0.6;
    float frequency = 2.0;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * fract(snoise(st+snoise(vec2(st.y+iTime*0.1,st.x))));
        st *= frequency;
        amplitude *= .5;
    }
    return value;
}

float pattern( in vec2 p, out vec2 q)
{
    q.x = fpm( p + vec2(0.240,0.670) );
    q.y = fpm( p + vec2(5.2,1.3) ) ;

    return fpm( p + q*1.5 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy/iResolution.y;
    //st -= vec2(0.5);
    vec3 color = vec3(0.0);

    vec2 q = vec2(1.0);
    float f = pattern(st*1.0,q);

    color = vec3(1.000,0.881,0.631);
    color = mix(color, vec3(0.243,0.646,0.945),f);
    color = mix(color, vec3(0.220,0.835,0.352),q.x*q.x);
    color = mix(color, vec3(0.830,0.626,0.835),q.y*q.y*2.0);
    color = mix(color, vec3(0.765,0.975,0.928), 0.5*smoothstep(0.368,0.844,abs(q.y)+abs(q.x)) );

    vec2 ex = vec2( 1.0 / iResolution.x, 0.0 );
    vec2 ey = vec2( 0.0, 1.0 / iResolution.y );
    vec3 nor = normalize( vec3( fpm(st+ex) - f, ex.x, fpm(st+ey) - f ) );

    vec3 lig = normalize( vec3(0.8,-0.5,-0.47) );
    float dif = clamp( 0.9+0.1*dot( nor, lig ), 0.0, 1.0 );

    vec3 bdrf;
    bdrf  = vec3(0.924,0.965,0.922)*(nor.y*0.5+0.5);
    bdrf += vec3(0.030,0.036,0.050)*dif;
    bdrf  = vec3(0.85,0.90,0.95)*(nor.y*0.5+0.5);
    bdrf += vec3(0.545,0.474,0.351)*dif;

    color *= bdrf;
    //color = color*color;
    color *= vec3(1.0,1.0,1.15);
    vec2 p = fragCoord.xy/iResolution.xy;
	 color *= 0.5 + 0.5 * sqrt(40.0*p.x*p.y*(1.0-p.x)*(1.0-p.y));

    fragColor = vec4(color,1.0);
}

vec2 random22(vec2 st)
{
    st = vec2(dot(st, vec2(127.1, 311.7)),
                dot(st, vec2(269.5, 183.3))
                );
    return -1.0 + 2.0 * fract(sin(st) * 43758.5453123);
}

vec3 celler2D(vec2 i,vec2 sepc)
{
    vec2 sep = i * sepc;
    vec2 fp = floor(sep);
    vec2 sp = fract(sep);
    float dist = 5.;
    vec2 mp = vec2(0.);

    for (int y = -1; y <= 1; y++)
    {
        for (int x = -1; x <= 1; x++)
        {
            vec2 neighbor = vec2(x, y);
            vec2 pos = vec2(random22(fp+neighbor));
            pos = sin( (pos*6. +iTime/2.) )* 0.5 + 0.5;
            float divs = length(neighbor + pos - sp);
            mp = (dist >divs)?pos:mp;
            dist = (dist > divs)?divs:dist;
        }
    }
    return vec3(mp,dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float cell = celler2D(uv,vec2(6.5)).z;
    float celln = 0.;
    for (int y = -1; y <= 1; y++)
    {
        for (int x = -1; x <= 1; x++)
        {
            vec2 ne = vec2(x, y);
            celln +=  celler2D(uv + ne,vec2(6.5)).z;
        }
    }
    cell = smoothstep(cell,celln,2.2);
    vec3 col = vec3(1.) * sin( cell * 8. + iTime) ;
    fragColor = vec4(col,1.0);
}

#define TWOPI 6.28318530718
#define NUM_OCTAVES 3

vec3 g_a = vec3(0.66,0.56,0.68);
vec3 g_b = vec3(0.718,0.438,0.720);
vec3 g_c = vec3(0.520,0.8,0.520);
vec3 g_d = vec3(-0.430,-0.397,-0.083);

//iq's cosine gradient https://www.iquilezles.org/www/articles/palettes/palettes.htm
vec3 grad( in float t)
{
    return g_a + g_b*cos( 6.28318*(g_c*t+g_d) );
}

float random (in vec2 uv) {
    return fract(sin(dot(uv.xy,vec2(12.9898,78.233)))*43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

//From book of shaders ch. 13 https://thebookofshaders.com/13/
float fbm ( in vec2 st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(st);
        st = rot * st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float stime = iDate.w * 0.09;
    float ctime = iTime * 0.3273;
    float ttime = iTime * 0.0697;
    float ptime = iTime * 0.0987;

    float mx = max(iResolution.x , iResolution.y);

    vec2 uv = gl_FragCoord.xy / mx;
    vec2 st = uv * 3.2; 
    vec2 tuv = uv * 1.2;

    float n_x = 2.0 * fbm(st + vec2(7.29 , ttime)) - 1.0;
    float n_y = 2.0 * fbm(st + vec2(3.67 , ttime)) - 1.0;
    float n_z = 0.5 * fbm(st + vec2(0.0 , 0.0)) + 0.5;

    vec3 n = normalize(vec3(n_x , n_y , n_z));
    vec3 l = normalize(vec3(cos(ttime) , sin(ttime) , 1.0));

    float c = dot(l,n);

    vec2 cuv = fract(9.7 * vec2(n_x , n_y) * tuv + vec2(0.0 , ctime * 0.1));
    float cx = cuv.x;
    float cy = cuv.y;
    cx = step(0.5 * sin(cx * TWOPI) + 0.5 , 0.5);
    cy = step(0.5 * sin(cy * TWOPI) + 0.5 , 0.5);


    vec2 duv = fract(9.7 * vec2(n_x , n_y) * tuv + vec2(0.0 , ptime * 0.1));
    float dx = duv.x;
    float dy = duv.y;  
    dx = step(0.5 * sin(dx * TWOPI) + 0.5 , 0.5);
    dy = step(0.5 * sin(dy * TWOPI) + 0.5 , 0.5); 

    float total = cx+cy;
    bool isWhite = mod(total , 2.0) == 0.0;

    vec3 w = vec3(1.0);

    vec3 b = vec3(0.0);

    vec3 ch = isWhite ? w:b;

   	ch -= vec3(floor(dx + dy * 0.975));

    vec3 o = grad(c + ttime * 0.5 + stime);

    //Uncomment for more chaotic shapes
    //ch -= vec3(floor(fract(c * 12.0) + 0.25));

    fragColor = vec4(ch + o , 1.0);
}

const vec3 shadowAndAmbientColor = vec3(1.0)-vec3(0.1, 0.2, 0.4);

float sph(vec3 p, float r) {
	return length(p) - r;
}

float plane(vec3 p, vec3 n, float d) {
	return dot(p, n) - d;
}

float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

float box(vec3 p, vec3 s) {
	return vmax(abs(p)-s);
}

float sdf(vec3 p) {
	float s1 = sph(p, 1.0);

    float p1 = plane(p, vec3(0.0, 1.0, 0.0), -1.0);

    float b1 = box(vec3(2.0, 0.0, 0.0)-p, vec3(1.0));

    float m1 = min(s1, p1);
    m1 = min(m1, b1);

    return m1;
}

vec3 getnormal(vec3 p) {
	float c = sdf(p);
    vec2 off = vec2(-0.01, 0.0);

    return normalize(vec3(sdf(p - off.xyy) - c, sdf(p - off.yxy) - c, sdf(p - off.yyx) - c));
}

vec3 shadow(vec3 ro, vec3 rd) {
    vec3 color = vec3(1.0);

    int steps = 50;
    float s = 10.0/float(steps);

    float tq = 0.05;
    float t = tq;

    vec3 sundir = vec3(1.0, 1.0, -1.0);

    for (int i = 0; i < steps; i++) {
        vec3 pos = ro + rd*t;
    	float d = sdf(pos + getnormal(pos)*0.02);

        color = min(color, 10.0*d/t);

        if (d < 0.01) {
            color = vec3(0.0);
            break;
        }

        t += d;
    }

    return color * shadowAndAmbientColor;
}

vec3 march(vec3 ro, vec3 rd) {
    vec3 color = vec3(0.0);

    int steps = 50;
    float s = 10.0/float(steps);

    float tq = 0.05;
    float t = tq;

    vec3 sundir = vec3(1.0, 1.0, -1.0);

    for (int i = 0; i < steps; i++) {
        vec3 pos = ro + rd*t;
    	float d = sdf(pos);

        if (d < 0.01) {
            vec3 n = getnormal(pos);
            vec3 shad = shadow(pos, normalize(sundir));
            float lum = max(0.0, dot(n, normalize(sundir)));
            float fog = 10.0/(t*t);
        	color = vec3(lum * max(min(fog, 1.0), 0.0)) * shad;
            break;
        }

        t += d;
    }

    return color;
}

