float smin(float a, float b, float k) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float snowhead(vec3 p, vec3 off) {
    float body = length(p - off) - 1.0;
    float head = length(p - vec3(0.0, 1.4, 0.0) - off) - 0.6;
    return smin(body, head, 0.2);
}

float carrot(vec3 p, vec3 off) {
    p = p - off;
    if (length(p) > 1.0) {
        return 1.0;
    }
    float rad = radians(75.0);
    float q = length(p.xy);
    return dot(vec2(sin(rad), cos(rad)), vec2(q, p.z));
}

float snoweye(vec3 p, vec3 off) {
    float left = length(p - vec3(-0.25, 0.0, 0.0) - off) - 0.1;
    float right = length(p - vec3(0.25, 0.0, 0.0) - off) - 0.1;
    return min(left, right);
}

float sol(vec3 p) {
    return p.y;
}

float rect(vec3 p, vec3 b, vec3 off) {
    vec3 q = abs(p - off) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0) - 0.1;
}

float window(vec3 p, vec3 off) {
    return max(rect(p, vec3(2.0, 2.0, 0.1), off),
        -rect(p, vec3(1.8, 1.7, 1.0), off + vec3(0.0, 0.0, 0.1)));
}

float wall(vec3 p, vec3 windowOff) {
    if (p.z < -3.0) {
        return 1.0;
    }
    float x = abs(p.x + 3.0) - 0.001;
    float z = abs(p.z + 3.0) - 0.001;
    return max(min(x, z),
        -rect(p, vec3(2.0, 2.0, 1.0), windowOff));
}

float cubic(vec3 p, vec3 off) {
    float sol = rect(p, vec3(3.0, 0.01, 3.0), off);
    float plafond = smin(sol, rect(p, vec3(3.0, 0.01, 3.0), off + vec3(0.0, 6.0, 0.0)), 0.1);
    float wall = smin(plafond, rect(p, vec3(3.0, 6.0, 0.01), off + vec3(0.0, 0.0, -3.0)), 0.1);
    wall = smin(wall, rect(p, vec3(0.01, 6.0, 3.0), off + vec3(-3.0, 0.0, 0.0)), 0.1);
    wall = smin(wall, rect(p, vec3(0.01, 6.0, 3.0), off + vec3(3.0, 0.0, 0.0)), 0.1);
    return wall;
}

float lamp(vec3 p, vec3 off) {
    return rect(p, vec3(0.5, 0.1, 0.4), off);
}

vec2 map(vec3 p) {
    float closest = 1000.0;
    float id = -1.0;

    float dist = sol(p);
    if (dist < closest) { closest = dist; id = 0.5; }

    dist = snowhead(p, vec3(0.0, 0.5, 0.0));
    if (dist < closest) { closest = dist; id = 1.5; }

    dist = snoweye(p, vec3(0.0, 2.2, 0.5));
    if (dist < closest) { closest = dist; id = 2.5; }

    dist = carrot(p, vec3(0.0, 1.9, 1.0));
    if (dist < closest) { closest = dist; id = 3.5; }

    dist = wall(p, vec3(0.0, 3.8, -4.0));
    if (dist < closest) { closest = dist; id = 4.5; }

    dist = window(p, vec3(0.0, 3.8, -3.1));
    if (dist < closest) { closest = dist; id = 5.5; }

    dist = cubic(p, vec3(1.0, 0.0, -6.15));
    if (dist < closest) { closest = dist; id = 6.5; }

    dist = lamp(p, vec3(1.0, 6.0, -6.15));
    if (dist < closest) { closest = dist; id = 7.5; }

    return vec2(closest, id);
}

vec3 calcNormal(vec3 p) {
    const float epsilon = 0.001;
    return normalize(vec3(
        map(p).x - map(vec3(p.x - epsilon, p.yz)).x,
        map(p).x - map(vec3(p.x, p.y - epsilon, p.z)).x,
        map(p).x - map(vec3(p.xy, p.z - epsilon)).x
    ));
}

vec2 intersect(vec3 ro, vec3 rd) {
    vec2 dv = vec2(0.0, -1.0);

    for (int i = 0; i < 300; i++) {
        vec2 distId = map(ro + rd * dv.x);
        if (distId.x <= 0.001) {
            dv.y = distId.y;
            break;
        }
        dv.x += distId.x;
    }
    return dv;
}

vec3 wallColor(vec3 p) {
    const vec3 base = vec3(0.8, 0.6, 0.7);
    vec3 f = fract(p);
    float clamped = length(clamp(p, 0.0, 1.0));
    return base * (f.y + 0.4);
}

vec3 carrotColor(vec3 p) {
    const vec3 base = vec3(1.5, 0.3, 0.0);
    vec3 f = fract(p * 10.0);
    vec3 color = base * (1.0 - pow(f.z, 12.0) * 0.3);
    return color;
}

vec3 getColor(vec3 p, float id) {
    if (id <= -0.5) { return vec3(0.0, 0.0, 0.0); }
    if (id <= 1.0) { return vec3(1.0, 1.0, 1.0); }
    if (id <= 2.0) { return vec3(1.0, 1.0, 1.0); }
    if (id <= 3.0) { return vec3(0.1, 0.1, 0.1); }
    if (id <= 4.0) { return carrotColor(p); } // carrot
    if (id <= 5.0) { return wallColor(p); } // wall
    if (id <= 6.0) { return vec3(0.5, 0.2, 0.1); }
    if (id <= 7.0) { return vec3(2.2, 2.2, 2.2); }
    if (id <= 8.0) { return vec3(100.0, 100.0, 100.0); }
    return vec3(1.0, 0.0, 0.0);
}

float softShadow(vec3 ro, vec3 rd, float k) {
    float res = 1.0;
    float depth = 0.0001;
    vec2 dv = vec2(1.0, -1.0);

    for (int i = 0; i < 50; i++) {
        dv = map(ro + rd * depth);
        res = min(res, smoothstep(0.0, 1.0, k * dv.x / depth));
        if (res < 0.0001) { break; }
        depth += clamp(dv.x, 0.01, 0.06);
    }
    return clamp(res, 0.0, 1.0);
}

float directLighting(vec3 p, vec3 n, vec3 lightPos, vec3 lightDir) {
    vec3 u = normalize(cross(lightDir, vec3(0.0, 1.0, 0.0)));
    vec3 v = cross(u, lightDir);
    float shadowIntensity = softShadow(p + 0.001 * n, lightDir, 10.0);

    vec3 toLight = lightPos - p;
    float attenuation = smoothstep(0.9, 1.0, dot(normalize(toLight), lightDir));

    return attenuation * shadowIntensity;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    const float r = 2.0;
    vec3 ro = vec3(r * (sin(iTime * 0.3) * 0.5 + 0.5) * 0.9 + r, 3.0 * (sin(iTime * 0.5) * 0.1 + 0.9), r * (cos(iTime * 0.3) * 0.5 + 0.5) + r);
    vec3 center = vec3(0.0, 2.0 + sin(iTime * 0.2) * 0.8 + 0.2, 0.0);
    vec3 front = normalize(vec3(center - ro));
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));
    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec2 result = intersect(ro, rd);
    vec3 pos = ro + rd * result.x;
    vec3 n = calcNormal(pos);

    vec3 lightPos = vec3(-1.5, 5.5, -4.65);
    vec3 lightDir = normalize(lightPos);
    vec3 hal = normalize(lightDir - rd); // WHAT?

    float ambient = 1.0;
    float diffuse = max(dot(lightDir, n), 0.0);
    float attenuation = 0.1 + 0.9 * smoothstep(0.9, 1.0, dot(normalize(lightPos - pos), lightDir));
    float back = max(dot(n, normalize(vec3(-lightDir.x, 0.0, -lightDir.z))), 0.0);
    float fr = pow(clamp(1.0 + dot(n , rd), 0.0, 1.0), 3.0);
    float dome = clamp(n.y * 0.8 + 0.2, 0.0, 1.0);
    float sol = clamp(-n.y * 0.8 + 0.2, 0.0, 0.0) * pow(clamp(1.0 - pos.y, 0.0, 1.0), 2.0);
    float specular = pow(clamp(dot(n, hal), 0.0, 1.0), 8.0);
    float shadow = directLighting(pos, n, lightPos, lightDir);

    vec3 light = vec3(0.0, 0.0, 0.0);
    light += ambient * vec3(0.03, 0.02, 0.01) * attenuation;
    light += diffuse * vec3(1.01, 1.0, 1.0) * pow(vec3(shadow), vec3(1.0, 1.0, 1.0)) * attenuation;
    light += back * vec3(0.42, 0.20, 0.1) * attenuation;
    light += fr * vec3(3.0, 3.0, 3.0) * attenuation * (0.25 + 0.25 * diffuse);
    light += dome * vec3(0.15, 0.1, 0.1) * attenuation;
    light += sol * vec3(0.2, 0.2, 0.2) * attenuation;
    light += specular * vec3(0.5, 0.5, 0.5) * attenuation;

    vec3 color = getColor(pos, result.y) * light;
    color = pow(color, vec3(0.4545));

    fragColor = vec4(color, 1.0);
}

#define MAXSTEPS 100
#define MAX_DISTANCE 100.0
#define GLERP 0.01

//Returns the distance to a sphere
//Pos is the position of the object.
//Sphere
//xyz describes the position of the sphere.
//w describes the radius of the sphere.
float Sphere (vec3 Pos, vec4 Sphere)
{
    return distance(Pos, Sphere.xyz) - Sphere.w;
}

//Returns the distance from objects in the scene.
float GetDistanceFromScene(vec3 Pos)
{
   float modDist = 2.0;

   vec3 ModPos = vec3(mod(Pos, modDist));

   //Sphere 'nuff said.
   float SP = Sphere(ModPos, 
                     vec4(1.0,
                     1.0,
                     1.0,
                     0.25 + sin(iTime) * 0.125));

   //Sphere 'nuff said.
   //float SP2 = Sphere(Pos, 
   //                   vec4(1.0 + cos(iTime / 4.0) * 2.0,
   //                   1.0,
   //                   6.0 + sin(iTime / 4.0) * 2.0, 
   //                   1.0) ); 

   //SP = min (SP, mod(3.0, SP));

   //Ground plane (XZ)
   float G = Pos.y; 

   float minDist = min(SP, SP);
   minDist = min(minDist, G);
   return minDist;
}

vec3 GetNormal(vec3 p)
{
    float d = GetDistanceFromScene(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDistanceFromScene(p-e.xyy),
        GetDistanceFromScene(p-e.yxy),
        GetDistanceFromScene(p-e.yyx));

    return normalize(n);
}

//Gets the depsth of the camera in the direction it's facing.
float GetDepth(vec3 Position, vec3 direction)
{
   //Total accumulated distance
   float dist = 0.0;

   //Raymarching loop.
   for (int i = 0; i < MAXSTEPS; ++i)
   {
       //Finds the current position of ray
       vec3 p = Position + dist * direction;
       //Finds the distance from objects in the scene
       float distScene = GetDistanceFromScene(p);
       //Adds that distance to the total.
       dist += distScene;
       //Check for a break condition
       if (distScene < GLERP || distScene > MAX_DISTANCE) break;
   }
   //Return the distance
   return dist;
}

//Gets the normal of an object
//Pos is the position of the object.
float GetLight(vec3 p)
{
    vec3 LightPos = vec3(0.0, 5.0, iTime);

    vec3 l = normalize(LightPos - p);

    vec3 n = GetNormal(p);

    float dif = clamp(dot(l, n), 0.0, 1.0);

    //Look for shadows

    //Get distance raymarched from surface point to the light
    float d = GetDepth(p + n * GLERP * 2.0, l);

    //Check is shorter than the distance to the light
    if (d < length(LightPos - p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) /iResolution.y;


    //Hey some camera stuff.
    vec3 Position  = vec3(0.0, 1.0, -5.0 + iTime);
    vec3 RayDir    = normalize(vec3(uv.x, uv.y, 1.0));

    // Time varying pixel color
    float depth = GetDepth(Position, RayDir);

    //Diffuse lighting by finding normal from last position found in depth function
    float dif = GetLight(Position + RayDir * depth);

    // Output to screen
    fragColor = vec4(dif, dif, dif,1.0);
    //fragColor = vec4(depth/10.0, depth/10.0, depth/10.0,1.0);
}

#define MAX_MARCH_STEPS 120
#define CLIP_DIST 1000.0
#define EPSILON 0.01

float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}

float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}

float texNoise(vec3 p) {
    return texture(iChannel0, vec2(p.xy)).r;
}


float sdBall(vec3 pos, float radius)
{
    return 0.006*texNoise(1.5*pos) + 0.04*noise(5.*pos) + length(pos) - radius;
}

float sceneSDF(vec3 pos) {
    pos = mod(pos, 10.0) - 5.0;

    float temp;
    float dist = CLIP_DIST;

    dist = (temp = sdBall(pos - vec3(0.0, 0.0, 5.0), 1.0)) < dist ? temp : dist;

    return dist;
}

float raymarch(vec3 ro, vec3 rd, float mint, float maxt) {
    float depth = mint;
    for(int i=0;i<MAX_MARCH_STEPS;i++)  {
        float dist = sceneSDF(ro+rd*depth);
        if (dist < EPSILON) return depth;

        depth += dist;
        if (depth > maxt) break;
    }

    return maxt;
}

vec3 calcNormal(vec3 pos) {
    const vec2 eps = vec2(0.001, 0.0);

    vec3 nor = vec3(
        sceneSDF(pos + eps.xyy) - sceneSDF(pos - eps.xyy),
        sceneSDF(pos + eps.yxy) - sceneSDF(pos - eps.yxy),
        sceneSDF(pos + eps.yyx) - sceneSDF(pos - eps.yyx));
    return normalize(nor);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 aspect = vec2(1.0, iResolution.y / iResolution.x);
    uv = 2.0 * uv - 1.0;
    uv *= aspect;

    vec3 col = vec3(0.0);

    vec3 ro = vec3(2.0*cos(iTime), 5.0, 2.0*sin(iTime));

    const float FOV = 90.0;
    const float RDF = 1.0 / tan(radians(FOV / 2.0));
    vec3 rd = normalize(vec3(uv, RDF));

    float dist = raymarch(ro, rd, 0.01, CLIP_DIST);

    if (dist < CLIP_DIST) {
        vec3 hitPos = ro + rd*dist;

        vec3 normal = calcNormal(hitPos);

        const vec3 lightPosition = vec3(-2.0, 2.0, 1.0);
        float lightness = max(0.0, dot(-normalize(hitPos - lightPosition), normal));

        col = vec3(mod(hitPos / 30., 1.));
        col = smoothstep(0., 1., col); // Brightens up the colors
        col *= 0.2 + 0.8*lightness;
        col *= mix(1.0, 0.0, length(hitPos - lightPosition) / 250.0);
    }

    col = pow (col, vec3 (1.0 / 2.2)); // Gamma correction
    fragColor = vec4(col, 1.0);
}

#define Rot(a) mat2(cos(a), -sin(a),sin(a), cos(a))
#define PI 3.141592
#define tau 6.283185

float sdCapsule( vec2 p, vec2 a, vec2 b)
{
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h );
}

float Branch(vec2 p, float zoom)
{
    p*=Rot(0.945);
    p -= zoom*vec2(0.31,0.43);
    float d = sdCapsule(p, vec2(0.,0.), zoom*vec2(1.,0.));
    d = min(d,sdCapsule(p* -Rot(tau/5.), vec2(0.,0.), zoom*vec2(1.,0.)));
    return d;
}

vec3 Star(vec2 p, float zoom, vec3 col)
{
    p*=Rot(tau/4.);

    float an = tau/5.;
    float sector = round(atan(p.y,p.x)/an);
    vec2 q = p;

    q.xy = Rot(an*sector)*p.xy;

    return 0.02/abs(Branch(q,zoom))*col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 m,col = vec3(0.);

    for(float i=0.;i<10.;i++)
    {     
        m = max(m,Star(uv, mod(2.5*iTime-i,10.)+0.01,vec3(0.86-mod(i,10.)*0.2,0.0,0.48+0.02*mod(i,10.))));   
    }

    col = m;

    fragColor = vec4(col,1.);
}

#define PI 3.14159265359
#define SOFT .005
#define PIXELATED 0

#define ss(a,b,c) smoothstep(a,b,c)

float circle(vec2 uv, float r, vec2 c, float softness)
{
    return ss(r+softness, r-softness, length(uv - c));
}

vec2 rot(vec2 uv, float a)
{
    vec2 i = vec2(cos(a),sin(a));
    return vec2(uv.x*i.x - uv.y*i.y, uv.y*i.x + uv.x*i.y);
}


float window(float center, float val, float width, float fall)
{
    return ss(width + fall, width, length(center - val));
}

// map square coordinates of center c, radial range and angular range.
// map to [-.5, 5]
vec2 mapCrown(vec2 uv, vec2 c, vec2 radLim)
{
    float radius = (radLim.x + radLim.y)/2.;
    float radrange = (radLim.y - radLim.x);
    vec2 nuv = uv - c;
    vec2 puv  =  vec2(length(nuv), atan(nuv.y, nuv.x) );

    uv = vec2((puv.x - radLim.x)/radrange - .5,
              puv.y/PI);

    return uv;
}


// generate a yinyang (yy) on .x and a round mask on .y
// fits in [-.5, .5] when scale 1.
vec2 yinyang(vec2 uv, float softness, float scale)
{

    float r = .25*scale;
    float whole = max(
        				// upper ball
        				circle(uv, r, vec2(0., r), softness),
                      	(uv.x > 0.)?0.:1.-circle(uv, r,vec2(0., -r), softness)

                     );
    whole +=  circle(uv, .06125*scale , vec2(0.,-r), softness);
    whole -=  circle(uv, .06125*scale , vec2(0.,r), softness);
    float br = .5*scale ;
    float mask = ss(br+softness, br-softness, length(uv));
    whole = mix(.0, whole, ss(br+softness, br-softness, length(uv)));

    return vec2(whole, mask);

}

// generates big yy with a curve string of small yys on its edge
vec2 chaos(vec2 uv, float softness, float scale, float scaleDiv)
{
    vec2 yy = vec2(0.);


    // first YY
    yy += yinyang(uv, softness, scale);

    // uv for mapping small YY's in the big YY edge
    vec2 nuv = rot(uv, PI/2.) - vec2(-0.25*scale, .0);
    vec2 nuv2 = rot(uv, PI/2.) - vec2(0.25*scale, .0);

    // crown thickness
    float thick = .5/scaleDiv;

    vec2 rads = scale*vec2(.25 - thick, .25 + thick);

    // morph UV to form an S shape on YY edge
    uv = mix(mapCrown(nuv,  vec2(.0), rads.xy),
             mapCrown(rot(nuv2,PI), vec2(.0), rads.yx) * vec2(1., -1.),
             ss( 0.01, .0, sign(uv.x)) );

    // angle of section in crown 
    // Calculated trying to aproximate section to a square
    // r2 - r1 = angle*(r2 + r1)/2
    float angle = 2.*(rads.y-rads.x)/(rads.y+rads.x);

    // repetition of angular coord
    uv.y = fract(uv.y*floor(PI/angle)) - .5;

    // string phase shift 
    uv.y = (uv.y >= 0.)?uv.y-.5:uv.y+.5;

    // scale param is 1. cause uv has been scaled here already 
	vec2 yyString = yinyang(uv, softness, 1.);
    yy = mix(yy, yyString,  yyString.y);


    // fill space out of YY
    yy.x = (yy.y > 0.)?yy.x: (uv.x < 0.)?1.:0.;

    return yy;
}

// super pose 1 chaos small YYs with the big YY of another chaos
vec2 connectYYs(vec2 uv, float softness, float startScale, float scaleDiv)
{
    float endScale = startScale/scaleDiv;
    vec2 yy = chaos(uv, softness, startScale, scaleDiv);

    //TODO: Make extra YYs follow edge curvature
    // extra YYs
    vec2 yy1 = chaos(uv+vec2(.0,  startScale *1.004), softness, startScale, scaleDiv);
	vec2 yy2 = chaos(uv+vec2(.0, -startScale *1.004), softness, startScale, scaleDiv);
    yy = mix(yy, yy1, yy1.y);
    yy = mix(yy, yy2, yy2.y);


    vec2 nuv = rot(uv, PI/2.);
    vec2 newYY = chaos(nuv, softness, endScale, scaleDiv);

    // extra strings
    vec2 newYY1 = chaos(nuv+vec2(0., endScale), softness, endScale, scaleDiv);
    vec2 newYY2 = chaos(nuv+vec2(0., -endScale), softness, endScale, scaleDiv);
    newYY = mix(newYY, newYY1, newYY1.y);
    newYY = mix(newYY, newYY2, newYY2.y);

    yy = mix(yy, newYY, newYY.y);

    return yy;
}


