    t  = (clamp( tt, 0.*SECTION_W, 1.*SECTION_W) - 0.*SECTION_W)/SECTION_W;
    t1 = (clamp( tt, 1.*SECTION_W, 2.*SECTION_W) - 1.*SECTION_W)/SECTION_W;
    t2 = (clamp( tt, 2.*SECTION_W, 3.*SECTION_W) - 2.*SECTION_W)/SECTION_W;
    t3 = (clamp( tt, 3.*SECTION_W, 4.*SECTION_W) - 3.*SECTION_W)/SECTION_W;

    // Came to this experimenting trying to make zoom more linear

    // Tip from user mla
 	vec2 uv0 = uv*startScale*exp(-t*log(scaleDiv));
 	vec2 uv1 = uv*startScale*exp(-t1*log(scaleDiv));
 	vec2 uv2 = uv*startScale*exp(-t2*log(scaleDiv));
 	vec2 uv3 = uv*startScale*exp(-t3*log(scaleDiv));

    vec2 yy = vec2(0.);
    vec2 newYY;

    float crossfade = .0001;
    float softness = .0001;

    vec2 yy0 = connectYYs(uv0, softness, startScale, scaleDiv)*window(1.*SECTION_W/2., tt, SECTION_W/2., crossfade);

    uv1 = rot(uv1, PI/2.);
    vec2 yy1 = connectYYs(uv1, softness, startScale, scaleDiv)*window(3.*SECTION_W/2., tt, SECTION_W/2., crossfade);

    uv2 = rot(uv2, PI);
    vec2 yy2 = connectYYs(uv2, softness, startScale, scaleDiv)*window(5.*SECTION_W/2., tt, SECTION_W/2., crossfade);

    uv3 = rot(uv3, 3.*PI/2.);
    vec2 yy3 = connectYYs(uv3, softness, startScale, scaleDiv)*window(7.*SECTION_W/2., tt, SECTION_W/2., crossfade);


    // Stitching the 4 parts to make it a loop
    yy = yy0 + yy1 + yy2 + yy3;

    // delineate yy
    if(iTime < period/4.)
        yy *= yy.y;

    fragColor = vec4(yy.x);

}

