const int RAY_STEPS = 256;
const vec3 lightPos = vec3(0., 3., 3.);
const vec3 eyePos = vec3(0.0, 2., -20.0);
const vec3 refPos = vec3(0., 2.5, 0.);
const float PI = 3.14159265359;
const float FOVY = 19.5 * PI / 180.;

// Toggle ATTENUATION 
const bool ATTENUATION = false;
// Adjust these to alter where the subsurface glow shines through and how brightly
const float FIVETAP_K = 2.0;
const float AO_DIST = 0.085;

// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.0;
// Toggle this to affect how easily the subsurface glow propagates through an object

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

/***************** BASIC SCENE SET UP *******************/

#define FLOOR_SDF plane(pos, vec4(0, 1, 0, 2.5));
#define LEFT_WALL_SDF plane(pos, vec4(1, 0, 0, 5));
#define RIGHT_WALL_SDF plane(pos, vec4(-1, 0, 0, 5));
#define BACK_WALL_SDF plane(pos, vec4(0, 0, -1, 5));
#define CEILING_SDF plane(pos, vec4(0, -1, 0, 7.5));
#define GROUND_SPHERE_SDF sphere(rotateY(pos + vec3(0., 2.5, 0.), 27.5 * 3.14/180.0), 4., vec3(-2.0, 0.0, -3.0));
#define RIGHT_SPHERE_SDF sphere(pos + vec3(0., 2.5, 0.), 4., vec3(5.0, 7.0, -5.0));
#define LEFT_SPHERE_SDF sphere(pos + vec3(0, 2.5, 0.), 4., vec3(-5.0, 7.0, -5.0));

const int FLOOR_ID = 0;
const int LEFT_WALL_ID = 1;
const int RIGHT_WALL_ID = 2;
const int BACK_WALL_ID = 3;
const int CEILING_ID = 4;
const int GROUND_SPHERE_ID = 5;
const int RIGHT_SPHERE_ID = 6;
const int LEFT_SPHERE_ID = 7;


vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sphere(vec3 p, float r, vec3 c) {
    return distance(p, c) - r;
}

float sceneMap3D(vec3 pos) {
    float flr = FLOOR_SDF;
    float left_wall = LEFT_WALL_SDF;
    float right_wall = RIGHT_WALL_SDF;
    float back_wall = BACK_WALL_SDF;
    float ceiling = CEILING_SDF;
    float ground_sphere = GROUND_SPHERE_SDF;
    float right_sphere = RIGHT_SPHERE_SDF;
    float left_sphere = LEFT_SPHERE_SDF;
    return min(back_wall, min(left_wall, min(right_wall, 
       min(ceiling, min(flr, min(ground_sphere, min(right_sphere, left_sphere)))))));
}

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2 = LEFT_WALL_SDF
    if (t2 < t) {
        t = t2;
        obj = LEFT_WALL_ID;
    } 
    t2 = RIGHT_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = RIGHT_WALL_ID;
    }
    t2 = BACK_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = BACK_WALL_ID;
    }
    t2 = CEILING_SDF;
    if (t2 < t) {
        t = t2;
        obj = CEILING_ID;
    }
    t2 = GROUND_SPHERE_SDF;
    if (t2 < t) {
        t = t2;
        obj = GROUND_SPHERE_ID;
    }
    t2 = RIGHT_SPHERE_SDF
    if (t2 < t) {
        t = t2;
        obj = RIGHT_SPHERE_ID;
    }
    t2 = LEFT_SPHERE_SDF;
    if (t2 < t) {
        t = t2;
        obj = LEFT_SPHERE_ID;
    }
}

/***************** RAY MARCHING LOGIC *******************/

vec3 rayCast(vec3 eye, vec3 ref, vec2 uv) {

    float len = tan(FOVY) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    return normalize(p - eye);
}


void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01) { return; }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

/***************** EFFECTS *******************/

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}
float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;   
    float attenuation = 1.0;
    if (ATTENUATION) { attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir)); }
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

/***************** MATERIALS *******************/

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 nor, vec3 lightDir, vec3 view, float thick) {
    float lambert = max(0., dot(lightDir, nor));

    if (hitObj == GROUND_SPHERE_ID) {
       vec3 color = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.67, 0.67) * max(0.0, dot(nor, lightDir));
       return color + vec3(1.0, 0.67, 0.67) * subsurface(lightDir, nor, view, thick) * vec3(1.0, 0.88, 0.7);
    } else if (hitObj == RIGHT_SPHERE_ID) {         
       vec3 color = vec3(0.45, 0.45, 1.0) * vec3(1.0, 0.67, 0.67) * max(0.0, dot(nor, lightDir));
       return color + vec3(0.45, 0.45, 1.0) * subsurface(lightDir, nor, view, thick) * vec3(1.0, 0.88, 0.7);
    } else {
        return vec3(0.15, 0.15, 1.0)  * lambert;
    }
}

/***************** BASIC FUNCTIONALITY *******************/

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = normalize(computeNormal(isect));

    //oscillating light
    float radius = 3.;

    float x  = 5. + radius * cos(iTime);
    float z  = 2. + radius * sin(iTime);

    vec3 lightDir = normalize(vec3(x,3.,z) - isect);

    //SSS logic
    float thickness = fiveTapAO(isect, -nor, FIVETAP_K);
    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);

    return Intersection(t, surfaceColor, isect, hitObj);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.);

    vec3 eye = eyePos;
    vec3 ref = refPos;

    vec3 dir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(dir, eye);

    // Output to screen
    fragColor = vec4(isect.color, 1.);


}

/*
Visualization of the group structure construction
for an elliptic curve.

Given two points p and q on the curve,
p * q is the third intersection of the
line through p and q with the curve.
(Tangent if p=q)

Define 0 to be an inflection point of the curve.

We want an abelian (commutative) group on the points of the
curve such that 0 is the neutral element and
for three points p,q,r
p + q + r = 0 <=> p,q,r collinear

For a point p, define q := 0 * p
Then p, q and 0 are collinear and therefore
p + 0 + q = 0 => p + q = 0 => -p = q = 0 * p

So if there is an inverse element, it has to be 0 * p
for each point p on the curve

Assume there is indeed an inverse element, and
let now p,q be arbitrary points on the curve, then
p + q + (p*q) = 0 => p + q = -(p*q) = 0 * (p*q)

So the only operation "+" that fulfills the above requirements
is defined by: p + q := 0 * (p * q)

We can now test if -p is indeed the inverse element:
p + (-p) = 0 * (p * (-p)) = 0 * (p * (0*p)) = 0 * 0
                                \_________/
                                     v
                                    = 0

So "-p" is an inverse exactly when 0 = 0 * 0,
which means 0 is the only intersection of the
curves tangent in 0 with the curve,
which is the defining property of an inflection point.

For a geometric proof of associativity, see:
https://www.shadertoy.com/view/3djyz1

Note:
It is standard to take the inflection point at infinity
as neutral element. In that case, 0 * p
becomes the reflection of p at the x axis.
This leads to an particularly easy computation of
the operation. However, i decided to use an
inflection point that can be actually seen.

The letters are from the Latin Modern font
(https://en.wikipedia.org/wiki/Computer_Modern#Latin_Modern)
converted to shader with
https://gist.github.com/Ninja-Koala/74fa7652fb4de248949ce1e27b989c14
*/

//#define ZERO 0
#define ZERO min(0,iFrame)

float border;

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quadric(vec2 coeffs, inout vec2 roots){

	// normal form: x^2 + px + q = 0
	float p = coeffs[1] / 2.;
	float q = coeffs[0];

	float D = p * p - q;

	if (D < 0.){
		return 0;
	}
	else{
		roots[0] = -sqrt(D) - p;
		roots[1] = sqrt(D) - p;

		return 2;
	}
}

//From Trisomie21
//But instead of his cancellation fix i'm using a newton iteration
int solve_cubic(vec3 coeffs, inout vec3 r){

	float a = coeffs[2];
	float b = coeffs[1];
	float c = coeffs[0];

	float p = b - a*a / 3.0;
	float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
	float p3 = p*p*p;
	float d = q*q + 4.0*p3 / 27.0;
	float offset = -a / 3.0;
	if(d >= 0.0) { // Single solution
		float z = sqrt(d);
		float u = (-q + z) / 2.0;
		float v = (-q - z) / 2.0;
		u = sign(u)*pow(abs(u),1.0/3.0);
		v = sign(v)*pow(abs(v),1.0/3.0);
		r[0] = offset + u + v;	

		//Single newton iteration to account for cancellation
		float f = ((r[0] + a) * r[0] + b) * r[0] + c;
		float f1 = (3. * r[0] + 2. * a) * r[0] + b;

		r[0] -= f / f1;

		return 1;
	}
	float u = sqrt(-p / 3.0);
	float v = acos(-sqrt( -27.0 / p3) * q / 2.0) / 3.0;
	float m = cos(v), n = sin(v)*1.732050808;

	//Single newton iteration to account for cancellation
	//(once for every root)
	r[0] = offset + u * (m + m);
	r[1] = offset - u * (n + m);
	r[2] = offset + u * (n - m);

	vec3 f = ((r + a) * r + b) * r + c;
	vec3 f1 = (3. * r + 2. * a) * r + b;

	r -= f / f1;

	return 3;
}

float cubic_bezier_normal_iteration(float t, vec2 a0, vec2 a1, vec2 a2, vec2 a3){
	//horner's method
	vec2 a_2=a2+t*a3;
	vec2 a_1=a1+t*a_2;
	vec2 b_2=a_2+t*a3;

	vec2 uv_to_p=a0+t*a_1;
	vec2 tang=a_1+t*b_2;

	float l_tang=dot(tang,tang);
	return t-dot(tang,uv_to_p)/l_tang;
}

float cubic_bezier_dis_approx_sq(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a3 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a2 = (3. * p0 - 6. * p1 + 3. * p2);
	vec2 a1 = (-3. * p0 + 3. * p1);
	vec2 a0 = p0 - uv;

	float d0 = 1e38;

	float t;
	vec3 params=vec3(0,.5,1);

	if(all(lessThan(uv,max(max(p0,p1),max(p2,p3))+border)) && all(greaterThan(uv,min(min(p0,p1),min(p2,p3))-border))){
		for(int i=ZERO;i<3;i++){
			t=params[i];
			for(int j=ZERO;j<3;j++){
				t=cubic_bezier_normal_iteration(t,a0,a1,a2,a3);
			}
			t=clamp(t,0.,1.);
			vec2 uv_to_p=((a3*t+a2)*t+a1)*t+a0;
			d0=min(d0,dot(uv_to_p,uv_to_p));
		}
	}

	return d0;
}

//segment_dis_sq by iq
float length2( vec2 v ) { return dot(v,v); }

float segment_dis_sq( vec2 p, vec2 a, vec2 b ){
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length2( pa - ba*h );
}

int segment_int_test(vec2 uv, vec2 p0, vec2 p1){
	p0-=uv;
	p1-=uv;

	int ret;

	if(p0.y*p1.y<0.){
		vec2 nor=p0-p1;
		nor=vec2(nor.y,-nor.x);

		float sgn;

		if(p0.y>p1.y){
			sgn=1.;
		}
		else{
			sgn=-1.;
		}

		if(dot(nor,p0)*sgn<0.){
			ret=0;
		}
		else{
			ret=1;
		}
	}
	else{
		ret=0;
	}

	return ret;
}

int cubic_bezier_int_test(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){

	float cu = (-p0.y + 3. * p1.y - 3. * p2.y + p3.y);
	float qu = (3. * p0.y - 6. * p1.y + 3. * p2.y);
	float li = (-3. * p0.y + 3. * p1.y);
	float co = p0.y - uv.y;

	vec3 roots = vec3(1e38);
	int n_roots;

	int n_ints=0;

	if(uv.x<min(min(p0.x,p1.x),min(p2.x,p3.x))){
		if(uv.y>=min(p0.y,p3.y) && uv.y<=max(p0.y,p3.y)){
			n_ints=1;
		}
	}
	else{
		if(abs(cu) < .0001){
			n_roots = solve_quadric(vec2(co/qu,li/qu),roots.xy);
		}
		else{
			n_roots = solve_cubic(vec3(co/cu,li/cu,qu/cu),roots);
		}

		for(int i=ZERO;i<n_roots;i++){
			if(roots[i] >= 0. && roots[i] <= 1.){
				float x_pos = -p0.x + 3. * p1.x - 3. * p2.x + p3.x;
				x_pos = x_pos * roots[i] + 3. * p0.x - 6. * p1.x + 3. * p2.x;
				x_pos = x_pos * roots[i] + -3. * p0.x + 3. * p1.x;
				x_pos = x_pos * roots[i] + p0.x;

				if(x_pos > uv.x){
					n_ints++;
				}
			}
		}
	}

	return n_ints;
}

vec3 line(vec2 p_0, vec2 p_1){
	vec2 tan1 = p_1-p_0;
	vec2 nor1 = normalize(vec2(tan1.y,-tan1.x));
	return vec3(nor1,-dot(nor1,p_0));
}

vec2 third_intersection(vec2 p0, vec2 p1, float g_2, float g_3){
	vec2 tang=p1-p0;
	float p0_val = p0.y*p0.y - (( 4.*p0.x*p0.x - g_2 ) * p0.x - g_3);

	float co=p0_val;
	float li=-12.*p0.x*p0.x*tang.x + g_2*tang.x + 2.*p0.y*tang.y;
	float qu=-12.*p0.x*tang.x*tang.x+tang.y*tang.y;
	float cu=-4.*tang.x*tang.x*tang.x;

	vec3 roots = vec3(1e38);
	int n_roots = solve_cubic(vec3(co/cu,li/cu,qu/cu),roots);

	vec2 p2=vec2(1e38);

	for(int i=ZERO;i<n_roots;i++){
		vec2 tmp=p0+roots[i]*tang;
		if(distance(tmp,p0)>0.0001 && distance(tmp,p1)>0.0001){
			p2=tmp;
		}
	}

	return p2;
}

