void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float aspect_ratio = iResolution.x / iResolution.y;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= aspect_ratio;

	vec2 mouse = iMouse.xy / iResolution.xy;
	mouse-=.5;
	mouse.x *= iResolution.x / iResolution.y;

	vec2 mouse2 = iMouse.zw / iResolution.xy;
	mouse2-=.5;
	mouse2.x *= iResolution.x / iResolution.y;

	const float line_width = .001;
	const float curve_width = .001;
	const float dot_size = .005;

	const float axis_width = .005;

	const vec3 bg_col = vec3(1,1,1);
	const vec3 axis_col = vec3(0,0,0);
	const vec3 line_col = vec3(0,1,0);
	const vec3 second_line_col = vec3(1,1,0);
	const vec3 curve_col = vec3(0,0,1);
	const vec3 dot_col = vec3(1,0,0);
	const vec3 text_col = vec3(0,0,0);

    border = 2./iResolution.y;

	float curve_dis = 1e38;
	float second_curve_dis = 1e38;
	float dot_dis = 1e38;
	float axis_dis = 1e38;
	float line_dis = 1e38;
	float second_line_dis = 1e38;
	float text_dis_sq = 1e38;
	float text_dis = 1e38;

	//elliptic curve parameters (weierstrass normal form)
	float g_2 = .3;
	float g_3 = -.05;

	//initial approximations for various points (not exactly lying on the curve)
	//you can play around with these values, but the gradient descent
	//can get unstable if the points are too far away from the curve

	vec2 p0=vec2(0.043,-0.193); //0: the inflection point
	vec2 p1=vec2(-.3,.05*sin(mod(iTime,6.28318530717959))); //p
	vec2 p2=vec2(.1,-.1); //q

	//compute points lying exactly on the curve by gradient descent
	const int grad_descent_iterations=8;
	float p0_val, hesse_p0_val, p1_val, p2_val;
	vec2 p0_grad, hesse_p0_grad, p1_grad, p2_grad;

	for(int i=ZERO;i<grad_descent_iterations;i++){
		p0_val = p0.y*p0.y - (( 4.*p0.x*p0.x - g_2 ) * p0.x - g_3);
		p0_grad = vec2(-12.*p0.x*p0.x+g_2,2.*p0.y);
		p0-=(p0_grad*p0_val)/dot(p0_grad,p0_grad);

		//compute inflection point by alternately gradient descenting to curve and hessian of curve
		hesse_p0_val = -96.*g_2*p0.x*p0.x + 96.*p0.x*p0.y*p0.y - 8.*g_2*g_2 - 288.*g_3*p0.x;
		hesse_p0_grad = vec2(-192.*g_2*p0.x + 96.*p0.y*p0.y - 288.*g_3,192.*p0.x*p0.y);
		p0-=(hesse_p0_grad*hesse_p0_val)/dot(hesse_p0_grad,hesse_p0_grad);

		p1_val = p1.y*p1.y - (( 4.*p1.x*p1.x - g_2 ) * p1.x - g_3);
		p1_grad = vec2(-12.*p1.x*p1.x+g_2,2.*p1.y);
		p1-=(p1_grad*p1_val)/dot(p1_grad,p1_grad);

		p2_val = p2.y*p2.y - (( 4.*p2.x*p2.x - g_2 ) * p2.x - g_3);
		p2_grad = vec2(-12.*p2.x*p2.x+g_2,2.*p2.y);
		p2-=(p2_grad*p2_val)/dot(p2_grad,p2_grad);
	}

	vec2 p3=third_intersection(p1,p2,g_2,g_3); //p*q
	vec2 p4=third_intersection(p0,p3,g_2,g_3); //p+q
	vec2 p5=third_intersection(p0,p1,g_2,g_3); //-p

	dot_dis=min(dot_dis,distance(p0,uv)-dot_size); //0
	dot_dis=min(dot_dis,distance(p1,uv)-dot_size); //p
	dot_dis=min(dot_dis,distance(p2,uv)-dot_size); //q
	dot_dis=min(dot_dis,distance(p3,uv)-dot_size); //p*q
	dot_dis=min(dot_dis,distance(p4,uv)-dot_size); //p+q
	dot_dis=min(dot_dis,distance(p5,uv)-dot_size); //-p

	vec3 l0=line(p1,p2);
	vec3 l1=line(p0,p4);

	line_dis=min(line_dis,abs(dot(vec3(uv,1),l0)));
	line_dis=min(line_dis,abs(dot(vec3(uv,1),l1)));

	vec3 l2=line(p1,p5);

	p0_grad = vec2(-12.*p0.x*p0.x+g_2,2.*p0.y);
	vec3 tangent=vec3(normalize(p0_grad),0);
	tangent.z=-dot(p0,tangent.xy);

	second_line_dis=min(second_line_dis,abs(dot(vec3(uv,1),l2)));
	second_line_dis=min(second_line_dis,abs(dot(vec3(uv,1),tangent)));

	float uv_val = uv.y*uv.y - (( 4.*uv.x*uv.x - g_2 ) * uv.x - g_3);
	vec2 uv_grad = vec2(-12.*uv.x*uv.x+g_2,2.*uv.y);

	curve_dis=min(curve_dis, abs(uv_val)/length(uv_grad)-curve_width);

	axis_dis=min(axis_dis, abs(uv.x));
	axis_dis=min(axis_dis, abs(uv.y));

	vec2[4] p_positions=vec2[](vec2(.03,.05)-p1,vec2(-.04,-.03)-p3,vec2(.13,.03)-p4,vec2(.03,.03)-p5);
	vec2[3] q_positions=vec2[](vec2(-.01,-.05)-p2,p_positions[1]+vec2(-.1,.0),p_positions[2]+vec2(-.1,.0));

	vec2 plus_position=p_positions[2]+vec2(-.05,1e-7);
	vec2 minus_position=p_positions[3]+vec2(.05,1e-7);
	vec2 zero_position=vec2(.003,.0425)-p0;
	vec2 asterisk_position=p_positions[1]+vec2(-.05,0.);

	text_dis_sq=min(text_dis_sq,plus_dis_sq(uv+plus_position));
	text_dis_sq=min(text_dis_sq,minus_dis_sq(uv+minus_position));
	text_dis_sq=min(text_dis_sq,asterisk_dis_sq(uv+asterisk_position));
	text_dis_sq=min(text_dis_sq,zero_dis_sq(uv+zero_position));

	for(int i=ZERO;i<4;i++){
		text_dis_sq=min(text_dis_sq,p_dis_sq(uv+p_positions[i]));
	}
	for(int i=ZERO;i<3;i++){
		text_dis_sq=min(text_dis_sq,q_dis_sq(uv+q_positions[i]));
	}

	text_dis=sign(text_dis_sq)*sqrt(abs(text_dis_sq));

	vec3 color = bg_col;

	color=vec3(mix(axis_col,color,smoothstep(0., border, axis_dis)));
	color=vec3(mix(line_col,color,smoothstep(0., border, line_dis)));
	color=vec3(mix(second_line_col,color,smoothstep(0., border, second_line_dis)));
	color=vec3(mix(curve_col,color,smoothstep(0., border, curve_dis)));
	color=vec3(mix(dot_col,color,smoothstep(0., border, dot_dis)));
	color=vec3(mix(text_col,color,smoothstep(-border/2., border/2., text_dis)));

	fragColor = vec4(color,1);
}

/*
Mapping of the projective plane to a disk.

The whole affine plane is mapped to the disk without border,
the border is the line at infinity. Two opposing points on
the disk border are corresponding to just one point in the
projective plane. I gave two opposing points the same color
to emphasize this.

Basically, the lines parallel to the x axis are
an interpolation between the x axis and the disk border,
those parallel to the y axis between y axis and disk border accordingly.
Every point can then be described as the intersection of two
of those circles.
(You can think of the disk border as third axis)

The animation shows how ellipses are those conic sections without
intersections with the line at infinity,
parabolas are those with exactly one intersection, and
hyperbolas those with exactly two intersections.

(Or optionally a triangle where one point is movable with mouse)
(Or optionally an elliptic curve whose parameters you can change with mouse)

I got the basic idea of the mapping from a prof of mine,
i derived the exact formulas myself. I don't know
how this mapping is called, but this has certainly been done
before.
*/

//#define TRIANGLE
//#define ELLIPTIC_CURVE

const float pi=3.14159265358979;

const float eps=.000001;

const float t0=2.00001;
const float t1=4.00002;
const float t2=7.00003;
const float t3=9.00004;
const float tt=11.00005;
//the perturbations are a workaround for https://gitlab.freedesktop.org/mesa/mesa/issues/2610

float sgn(float a){
	if(a<0.){
		return -1.;
	}
	else{
		return 1.;
	}
}

vec2 from_projective(vec3 p, float stretch_factor, float radius, out vec2 int_p2){
	vec2 int_p;

	float a=p.x*p.x+p.y*p.y;
	float b=p.z*p.x/(stretch_factor*stretch_factor);
	float c=-p.x*p.x*radius*radius;

	float int_x=(-b+sgn(p.x)*sqrt(b*b-4.*a*c))/(2.*a);
	float int_y=int_x*p.y/p.x;

	int_p=vec2(int_x,int_y);

	if(abs(p.z)<eps){
		int_p2=-int_p;
	}
	else{
		int_p2=int_p;
	}

	return int_p;
}

vec3 to_projective(vec2 p, float stretch_factor, float radius){
	return vec3(p.xy,stretch_factor*stretch_factor*(radius*radius-dot(p,p)));
}

mat2x3 to_projective_jacobi(vec2 p, float stretch_factor, float radius){
	vec3 column1=vec3(1.,0.,-2.*stretch_factor*stretch_factor*p.x);
	vec3 column2=vec3(0.,1.,-2.*stretch_factor*stretch_factor*p.y);

	return mat2x3(column1, column2);
}

float to_projective_line(float a, float b){
	return -sgn(a)*sgn(b)*abs(a)/(abs(a)+abs(b));
}

vec2 from_projective_line(float a){
	return vec2(a,1.-abs(a));
}

float line_dist(vec2 uv, const vec2 p0, vec2 p1){
	vec2 tang=p1-p0;
	vec2 nor=normalize(vec2(tang.y,-tang.x));

	if(dot(tang,uv)<dot(tang,p0)){
		return distance(p0,uv);
	}
	else if(dot(tang,uv)>dot(tang,p1)){
		return distance(p1,uv);
	}
	else{
		return abs(dot(nor,uv)-dot(nor,p0));
	}
}

vec3 line(vec2 p_0, vec2 p_1){
	vec2 tan1 = p_1-p_0;
	vec2 nor1 = normalize(vec2(tan1.y,-tan1.x));
	return vec3(nor1,-dot(nor1,p_0));
}

vec3 line(vec3 p_0, vec3 p_1){
	return normalize(cross(p_0,p_1));
}

//mapping from barth's skript:
//https://web.archive.org/web/20051104003320/http://www.mi.uni-erlangen.de/~barth/docs/geoset.ps (german)
//it basically gives the formula and says:
//by this formula, you see the projective plane can be bijectively mapped to a disk, but this is not too useful
//the mapping does not have any favorably properties compared to the other one, i just implemented it for reference
vec2 from_projective_barth(vec3 p, float radius, out vec2 int_p2){
	vec2 int_p = 2.*radius/pi*p.xy*atan(dot(p.xy,p.xy)/(p.z*p.z))/length(p.xy);

	if(abs(p.z)<eps){
		int_p=radius*normalize(p.xy);
		int_p2=-int_p;
	}
	else{
		int_p2=int_p;
	}

	return int_p;
}

vec3 to_projective_barth(vec2 p, float radius){
	return vec3(p.xy*sqrt(tan(pi/(2.*radius)*length(p))),length(p));
}

mat2x3 to_projective_jacobi_barth(vec2 p, float radius){
	float fac=pi/(2.*radius);
	float len=length(p);

	float tmp=tan(len*fac);

	float a11=.5*(tmp*tmp + 1.)*fac*p.x*p.x/(len*sqrt(tmp)) + sqrt(tmp);
	float a12=.5*(tmp*tmp + 1.)*fac*p.x*p.y/(len*sqrt(tmp));
	float a22=.5*(tmp*tmp + 1.)*fac*p.y*p.y/(len*sqrt(tmp)) + sqrt(tmp);

	vec3 column1=vec3(a11,a12,p.x/len);
	vec3 column2=vec3(a12,a22,p.y/len);

	return mat2x3(column1,column2);
}

//elliptic degree is the same but for lines instead of points
float elliptic_distance(vec3 p1, vec3 p2){
	return acos(1./sqrt(dot(p1,p1)*dot(p2,p2))*dot(p1,p2));
}

float spectral(float x){
        return clamp(abs(mod((x/pi+1.)*4.,8.)-4.)-2.,-.75,.75)*.5/.75+.5;
}

vec3 to_col(vec2 z){
	float direction = 2.*atan(z.x,z.y);
	return vec3(spectral(direction),spectral(direction+pi/2.),spectral(direction-pi));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= iResolution.x / iResolution.y;

	vec2 mouse = iMouse.xy / iResolution.xy;
	mouse-=.5;
	mouse.x *= iResolution.x / iResolution.y;

	vec2 mouse2 = iMouse.zw / iResolution.xy;
	mouse2-=.5;
	mouse2.x *= iResolution.x / iResolution.y;

    #ifdef TRIANGLE
    float animation=1.;
    #else
    #ifdef ELLIPTIC_CURVE
    float animation=1.;
    #else
    float animation;

    float tc=abs(mod(iTime-tt,2.*tt)-tt);

    if(tc<t1){
        animation=exp(mix(5.,0.,smoothstep(0.,t0,tc)));
    }
    else if(tc<.5*(t1+t2)){
        animation=exp(mix(0.,5.,smoothstep(t1,.5*(t1+t2),tc)));
    }
    else if(tc<t3){
        animation=exp(mix(5.,0.,smoothstep(.5*(t1+t2),t2,tc)));
    }
    else{
        animation=exp(mix(0.,5.,smoothstep(t3,tt,tc)));
    }
    #endif
    #endif

    float stretch_factor=1./animation;
    float radius=.47*animation;

	const float line_width = .001;
	const float circle_width = .003;
	const float grid_width = .0;
	const float curve_width = .001;
	const float dot_size = .005;
	const float axis_width = .005;

	const float grid_size = .8;

	const vec3 bg_col = vec3(1,1,1);
	const vec3 axis_col = vec3(0,0,0);
	const vec3 grid_col = vec3(.75);
	const vec3 line_col = vec3(0,1,0);
	const vec3 curve_col = vec3(0,0,1);
	const vec3 dot_col = vec3(1,0,0);

	vec3 circle_col = to_col(uv);

    float border = 2./iResolution.y;

	float curve_dis = 1e38;
	float dot_dis = 1e38;
	float axis_dis = 1e38;
	float grid_dis = 1e38;
	float line_dis = 1e38;
	float circle_dis = 1e38;

	vec3 color=bg_col;

	circle_dis=min(circle_dis,abs(length(uv)-radius)-circle_width);

	axis_dis=min(axis_dis,line_dist(uv,vec2(0,radius),vec2(0,-radius))-line_width);
	axis_dis=min(axis_dis,line_dist(uv,vec2(radius,0),vec2(-radius,0))-line_width);

	vec3 uv_p=to_projective(uv,stretch_factor,radius);

	mat2x3 jac=to_projective_jacobi(uv,stretch_factor,radius);

    #ifdef TRIANGLE
	vec2 m0=normalize(mouse)*min(length(mouse),radius);
	vec3 p0=to_projective(m0,stretch_factor,radius);
	vec3 p1=vec3(2.7,2.2,1.);
	vec3 p2=vec3(2.,-2.,1.);

    vec2 int_p;
	vec2 int_p2;

	int_p = from_projective(p0,stretch_factor,radius,int_p2);

	dot_dis=min(dot_dis,distance(uv,int_p)-dot_size);
	dot_dis=min(dot_dis,distance(uv,int_p2)-dot_size);

	int_p = from_projective(p1,stretch_factor,radius,int_p2);

	dot_dis=min(dot_dis,distance(uv,int_p)-dot_size);
	dot_dis=min(dot_dis,distance(uv,int_p2)-dot_size);

	int_p = from_projective(p2,stretch_factor,radius,int_p2);

	dot_dis=min(dot_dis,distance(uv,int_p)-dot_size);
	dot_dis=min(dot_dis,distance(uv,int_p2)-dot_size);

    vec3 l1=line(p0,p1);
	vec3 l2=line(p1,p2);
	vec3 l3=line(p2,p0);

	curve_dis=min(curve_dis,abs(dot(uv_p,l1)/length(l1*jac)));
	curve_dis=min(curve_dis,abs(dot(uv_p,l2)/length(l2*jac)));
	curve_dis=min(curve_dis,abs(dot(uv_p,l3)/length(l3*jac)));
    #else
    #ifdef ELLIPTIC_CURVE

	float g_2 = 10.*mouse.x;
	float g_3 = 10.*mouse.y;

	float elliptic_curve_val=(4.*uv_p.x*uv_p.x-g_2*uv_p.z*uv_p.z)*uv_p.x-(g_3*uv_p.z*uv_p.z+uv_p.y*uv_p.y)*uv_p.z;
	vec3 elliptic_curve_grad=vec3(12.*uv_p.x*uv_p.x-g_2*uv_p.z*uv_p.z,-2.*uv_p.y*uv_p.z,(-3.*g_3*uv_p.z-2.*g_2*uv_p.x)*uv_p.z-uv_p.y*uv_p.y);

    curve_dis=min(curve_dis,abs(elliptic_curve_val/length(elliptic_curve_grad*jac)));
    #else
	float hyperbola_val=8.*uv_p.x*uv_p.x-4.*uv_p.y*uv_p.y+uv_p.z*uv_p.z;
	vec3 hyperbola_grad=vec3(8.*2.*uv_p.x,-4.*2.*uv_p.y,+2.*uv_p.z);

	float parabola_val=uv_p.x*uv_p.x-uv_p.y*uv_p.z;
	vec3 parabola_grad=vec3(2.*uv_p.x,-uv_p.z,-uv_p.y);

	float circle_val=uv_p.x*uv_p.x+uv_p.y*uv_p.y-uv_p.z*uv_p.z;
	vec3 circle_grad=vec3(2.*uv_p.x,2.*uv_p.y,-2.*uv_p.z);

	float mix_val;
	vec3 mix_grad;

    mix_val=mix(circle_val,parabola_val,smoothstep(t0,t1,tc));
	mix_grad=mix(circle_grad,parabola_grad,smoothstep(t0,t1,tc));

    if(tc>t2){
        mix_val=mix(parabola_val,hyperbola_val,smoothstep(t2,t3,tc));
		mix_grad=mix(parabola_grad,hyperbola_grad,smoothstep(t2,t3,tc));
    }

	curve_dis=min(curve_dis,abs(mix_val/length(mix_grad*jac)));
    #endif
    #endif

	vec3 grid_grad=vec3(1./uv_p.z,0.,-uv_p.x/(uv_p.z*uv_p.z));
	grid_dis = min(grid_dis, abs(mod(uv_p.x/uv_p.z-grid_size*.5,grid_size)-grid_size*.5)/length(grid_grad*jac)-grid_width);
	grid_grad=vec3(0.,1./uv_p.z,-uv_p.y/(uv_p.z*uv_p.z));
	grid_dis = min(grid_dis, abs(mod(uv_p.y/uv_p.z-grid_size*.5,grid_size)-grid_size*.5)/length(grid_grad*jac)-grid_width);

	float d0=length(uv)-radius;

	grid_dis=max(d0,grid_dis);
	curve_dis=max(d0,curve_dis);

	color=vec3(mix(grid_col,color,smoothstep(0., border, grid_dis)));
	color=vec3(mix(axis_col,color,smoothstep(0., border, axis_dis)));
	color=vec3(mix(line_col,color,smoothstep(0., border, line_dis)));
	color=vec3(mix(circle_col,color,smoothstep(0., border, circle_dis)));
	color=vec3(mix(curve_col,color,smoothstep(0., border, curve_dis)));
	color=vec3(mix(dot_col,color,smoothstep(0., border, dot_dis)));

	fragColor = vec4(color,1);
}

const vec3 gamma = vec3(2.2);

// Palette (RGB 2.2 color space)
vec3 green = vec3(81.0/255.0, 201.0/255.0, 150.0/255.0);
vec3 darkGreen = vec3(0.0/255.0, 40.0/255.0, 19.0/255.0);
vec3 headGreen = vec3(223.0/255.0, 252.0/255.0, 226.0/255.0);

float aspectRatio;
vec2 uv;
vec2 uvAspect;


vec3 ACESFitted(vec3 color);

vec3 circle(vec2 samplePoint, vec2 center, float radius)
{
    vec2 sampleDisp = samplePoint - center;
    float sampleLen2 = sampleDisp.x*sampleDisp.x + sampleDisp.y*sampleDisp.y;
    float ratio = sampleLen2 / (radius*radius);

    return vec3(1.0 - min(ratio,1.0));
}
vec3 background()
{
    // Flat shade
    vec3 col = darkGreen;
    float intensity = 2.0;

    // Grid lines
    {
        float thickness = 0.003;
        float spacing = 0.065;

        // Compute first line pos
        float leftBound = 0.0;
    	float firstX = leftBound - fract(iTime*0.4)*spacing;

        // Vertical Grid Lines
        for (float x = firstX; x < aspectRatio; x += spacing)
        {
            if (uvAspect.x > (x-thickness) && uvAspect.x < (x+thickness))
            {
                col += darkGreen;
            }
        }

		// Horizontal Grid Lines
        for (float y = 0.0; y < 1.0; y += spacing)
        {
            if (uvAspect.y > (y-thickness) && uvAspect.y < (y+thickness))
            {
                col += darkGreen;
            }
        }
    }

    return col * intensity;
}
float shape(float x, float offset) // x:[0,1]
{
    x += offset;
    if (x > 1.0) x -= floor(x);

    float s1 = 0.15;         // flat
    float s2 = s1 + 0.2;     // b1
    float s3 = s2 + 0.34;     // flat
    float s4 = s3 + 0.1;     // b2
    float s5 = s4 + 0.03;    // flat
    float s6 = s5 + 0.2;     // b3

    float y = 0.0;

    if (x < s1) { return 0.0; }

    if (x < s2)
    {
    	return 0.05 - 0.05*cos((x-s1)*30.0);
    }

    if (x < s3) { return 0.0; }

    if (x < s4)
    {
    	return 0.02 - 0.02*cos((x-s3)*60.0);
    }

    if (x < s5) { return 0.0; }

    if (x < s6)
    {
    	return 0.3*sin((x-s5)*35.0);
    }

    if (x > 1.0) return -.4;
}
float wrapIntensity(float sampleX, float xMax, float h)
{
    float v = sampleX + xMax - h; // wrap
    if (v > xMax) v -= xMax; // clamp to [0,xMax]
    return v/xMax; // normalize
}
vec3 line()
{   
    float repeats = 3.0;
    float scanRate = 0.18;
	float intensity = 1.5;
    float offset = iTime*0.1;

    vec3 col = vec3(0,0,0);

    // Calc head position
    float x =  aspectRatio * (scanRate*iTime - floor(scanRate*iTime)); // x:[0,aspectRatio)
	float yOffset = 0.5;

    vec2 headPos = vec2(x, yOffset + shape(x * repeats/aspectRatio, offset));
    vec2 linePos = vec2(uvAspect.x, yOffset + shape(uvAspect.x * repeats/aspectRatio, offset));

    // Line
    float thickness = 0.015/2.0;
    if (uv.y > linePos.y - thickness && uv.y < linePos.y + thickness)
    {
        col = green * pow(wrapIntensity(linePos.x, aspectRatio, headPos.x), 2.0);
    }

    // Glowing Head
    vec3 mask = pow(circle(uvAspect, headPos, 0.2), vec3(200.0));
    col += 2.0 * headGreen * mask;

    return col * intensity;
}

vec3 vignette()
{
    float darkest = 0.00;
    vec3 shade = circle(uvAspect, vec2(aspectRatio/2.0, 0.5), 0.85);
    return darkest + (1.0-darkest)*shade;
}
void linearizePallete()
{
    green = pow(green, gamma);
    darkGreen = pow(darkGreen, gamma);
    headGreen = pow(headGreen, gamma);
}

vec3 flicker(vec3 col)
{
    return col * (1.0 + 0.03*sin(iTime*50.0) + 0.04*sin(iTime*29.0) ); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Globals
    linearizePallete();
    aspectRatio = iResolution.x / iResolution.y;
    uv = fragCoord / iResolution.xy;       // w:[0,1], h:[0,1]
    uvAspect = fragCoord / iResolution.yy; // w:[0,apsect], h:[0,1]

    // Set color
    vec3 col = vec3(0,0,0);
    col += background();
    col += line();
    col = flicker(col);
    col *= vignette();

    // Post
	col *= 1.5; // exposure bias
  	col = ACESFitted(col); // tonemap 
    col = pow(col, 1.0/gamma); // gamma

    fragColor = vec4(col,1.0);
}








