// ACES Tonemap: https://github.com/TheRealMJP/BakingLab/blob/master/BakingLab/ACES.hlsl

const mat3 ACESInputMat = mat3(
	0.59719, 0.07600, 0.02840, 
	0.35458, 0.90834, 0.13383,
	0.04823, 0.01566, 0.83777
);
const mat3 ACESOutputMat = mat3(
	 1.60475, -0.10208, -0.00327,
	-0.53108,  1.10813, -0.07276,
	-0.07367, -0.00605,  1.07602
);
vec3 RRTAndODTFit(vec3 v)
{
	vec3 a = v * (v + 0.0245786) - 0.000090537;
	vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
	return a / b;
}
vec3 ACESFitted(vec3 color)
{
	color = ACESInputMat * color;
	color = RRTAndODTFit(color);
	color = ACESOutputMat * color;
	color = clamp(color,0.0,1.0);
	return color;
}

#define MAX_STEPS 100
#define MAX_DIST 50.
#define SURF_DIST .01
#define MAXITE 32.0

mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }

float formula(vec2 p) { // Plasma
    vec3 col;
    float t = iTime;
    for(float j = 0.0; j < 3.0; j++){
        for(float i = 1.0; i < 5.0; i++){
            p.x += 0.1 / (i + j) * sin(i * 10.0 * p.y + t + cos((iTime / (12. * i)) * i + j));
            p.y += 0.1 / (i + j)* cos(i * 10.0 * p.x + t + sin((iTime / (12. * i)) * i + j));
        }
        col[int(j)] = abs(p.x + p.y);
    }
    return (col.x+col.y+col.z)/3.;
}

float mandel(vec2 p) // or Julia
{    
    float zre=p.x*0.6;
    float zim=p.y*0.7;
    float cre=-0.8041213550817069;
    float cim=0.14839772190510192;
    float col=0.;

    for(float i=0.;i<MAXITE;i++)
    {
        float dam=zre*zre-zim*zim+cre;
        zim=2.0*zre*zim+cim;
        zre=dam;
        col++;
        if( (zre*zre+zim*zim)>4.0 )
            break;
    }

    return (col/MAXITE);
}


float GetDist(vec3 p) {
    float v=0.;
    if(p.x>-7. && p.x<7. && p.z>-7. && p.z<7.)
    	v=0.5*mandel(p.xz/3.0);
	vec4 s1 = vec4(p.x,v*sin(iTime),p.z,0.8);

	float cube = length(max(abs(p-s1.xyz)-vec3(0.5,0.5,0.5), 0.))/3.0;
    float planeDist = p.y;

    float d = min(cube, planeDist);
    return d;    
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;

        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    float dif=0.;
    if(p.x>-7. && p.x<7. && p.z>-7. && p.z<7.)
    {

        vec3 lightPos = vec3(0, 6, 2);
        lightPos.xz += vec2(sin(iTime), cos(iTime))*4.;
        vec3 l = normalize(lightPos-p);
        vec3 n = GetNormal(p);

        dif = clamp(dot(n, l), 0., 1.);
    }

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(0., 6, -8.5);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    rd = rd*(rotx(0.4));
    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif);

    fragColor = vec4(col,1.0);
}

vec3 cell;
float g;
float time;
float pi = 3.14159;

float noise(vec3 p) {
    return fract(sin(dot(p, vec3(4.3243241, 34.234, 234.23))) * 342.234);
}

mat2 rotate(float r) { return mat2(cos(r), sin(r), -sin(r), cos(r)); }

float scene(vec3 p) {
    p.x += p.z * p.z * 0.02 * sin(time * 2. * pi);

    p.z += time * 40. * pi;
    p.xy *= rotate(p.z * 0.10);

    vec3 cellsize = vec3(4, 4, 0);
    cell = floor(p / cellsize);

    p = mod(p, cellsize) - cellsize / 2.;

    return length(p.xy) - 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    time = mod(iTime / 10., 1.);

    vec3 cam = vec3(0, 0, -5);
    vec3 dir = normalize(vec3(uv, 1));

    float t = 0.;
    for (int i = 0; i < 128; ++i) {
        t += scene(cam + dir * t) * .7;
        if (t < 0.0001 || t > 50.)
            break;
    }

    vec3 h = cam + dir * t;
    vec2 o = vec2(.001, 0);
    vec3 n = normalize(vec3(scene(h + o.xyy) - scene(h - o.xyy),
                            scene(h + o.yxy) - scene(h - o.yxy),
                            scene(h + o.yyx) - scene(h - o.yyx)));

    vec3 light = normalize(vec3(0, 1, 1));
    float diffuse = max(0., dot(n, light));
    vec3 albedo =
        0.05 + 0.95 * vec3(noise(cell.xyy), noise(cell.yxy), noise(cell.yyx));
    float specular = pow(max(dot(normalize(light + -dir), n), 0.), 10000.);
    float ambient = 0.4;
    float fresnel = min(0.5, pow(1. + dot(n, dir), 4.));
    vec3 bg = vec3(0.001);
    vec3 col = mix((ambient + diffuse) * albedo + specular * vec3(1, 0.5, 0.5),
                   bg, fresnel);

    col = mix(col, bg, 1. - exp(-.0001 * t * t * t)); // apply fog

    col = pow(col, vec3(.454)); // gamma correction

    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 64
#define MAX_DIST 20.
#define EPS 0.001
const float PI = acos(-1.0);

mat3 rotX(float angle)
{
    return mat3(1,	0,			0,
               0,	cos(angle), -sin(angle),
               0,	sin(angle), cos(angle));
}

mat3 rotY(float angle)
{
	return mat3(cos(angle), 0, sin(angle),
               0,			1, 0,
               -sin(angle), 0, cos(angle));
}

mat3 rotZ(float angle)
{
	return mat3(cos(angle), -sin(angle), 0,
               sin(angle), 	cos(angle),	 0,
               0,		 	0,			 1);
}

vec2 opRevolution( in vec3 p, float w )
{
    return vec2( length(p.xz) - w, p.y );
}

float sdCube(vec2 pos, vec2 r)
{
	vec2 q = abs(pos) - r;
	return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

float sdCube(vec3 pos, vec3 r)
{
	vec3 q = abs(pos) - r;
	return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdSphere(vec3 pos, float r)
{
    return length(pos) - r;
}

float sdRing(vec3 pos, float r)
{
    pos.zy = opRevolution(pos, r);
    return sdCube(vec2(pos.z, pos.y), vec2(0.02, 0.03));
}

float sdRingsCross1( in vec3 pos, in float r )
{
    float d1 = sdRing(pos, r);

    pos *= rotZ(PI/2.);
    float d2 = sdRing(pos, r);

    pos *= rotX(PI/2.);
    float d3 = sdRing(pos, r);

    pos *= rotX(PI/4.);
    float d4 = sdRing(pos, r);

    return min(d1,min(d2,min(d3,d4)));
}

float sdRingsCross2( in vec3 pos, in float r )
{
    pos *= rotZ(PI/4.);
    float d1 = sdRing(pos, r);

    pos *= rotZ(PI/2.);
    float d2 = sdRing(pos, r);

    return min(d1,d2);
}

float sdDysonSphere( in vec3 pos, in float r )
{
    float d1 = sdRingsCross1(pos, r);

    pos *= rotY(PI/4.);
    float d2 = sdRingsCross2(pos, r);

    pos *= rotY(PI/2.);
    float d3 = sdRingsCross2(pos, r);

    return min(d1, min(d2, d3));
}

float map(vec3 pos)
{

    float d0 = sdSphere(pos, 0.2+sin(iTime*0.3)*0.05);
    float d1 = sdDysonSphere(pos * rotY(iTime * 1.00), 0.5);
    float d2 = sdDysonSphere(pos * rotY(iTime * 0.50), 0.7);
    float d3 = sdDysonSphere(pos * rotY(iTime * 0.25), 0.9);

    float res = MAX_DIST;
    res = min(res, d0);
    res = min(res, d1);
    res = min(res, d2);
    res = min(res, d3);
    return res;
}

vec3 normals(vec3 pos)
{
    vec2 e = vec2(0, 0.01);
    return normalize(vec3(
    	map(pos+e.yxx)-map(pos-e.yxx),
        map(pos+e.xyx)-map(pos-e.xyx),
        map(pos+e.xxy)-map(pos-e.xxy)
    ));
}

const vec4 miss = vec4(MAX_DIST, vec3(0));
vec4 raymarch(vec3 ro, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i<MAX_STEPS; i++)
    {
        vec3 pos = ro + rd*t;
        float d = map(pos);

        if(abs(d)<=EPS) return vec4(length(pos-ro), normals(pos));
        if(d >= MAX_DIST) return miss;

        t+=d;
    }


    return miss;
}

const vec3 BACKGROUND = vec3(0.2);
vec3 GetScene(vec3 ro, vec3 rd)
{

    vec4 a = raymarch(ro, rd);
    float d = a.x;
    vec3 nor = a.yzw;

    vec3 col = BACKGROUND;

    if(d < MAX_DIST)
    {
        vec3 pos = ro+rd*d;

        float li = dot(normalize(-pos), nor);
        li = li*0.5+0.5;
        li = li*0.9+0.1;

        col = vec3(1, 0.94, 0.8) * li;
    }

    return col;

}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.x;

    vec2 mouse = iMouse.xy / iResolution.xy;
    float camAngle = PI/4.; // mouse.x*PI*2.;
    vec2 p = 3.0 * vec2(cos(camAngle), sin(camAngle));
    vec3 ro = vec3(p.x,1.5,p.y);
    mat3 ca = setCamera(ro, vec3(0,0,0), 0.0);
    vec3 rd = ca*normalize(vec3(uv,1.8));

    vec3 col = GetScene(ro, rd);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float st=.02, maxdist=8.,m,mc;
vec3 ldir=vec3(-.5,-1.,-1.);

mat2 rot(float a) {
	float s=sin(a),c=cos(a);
    return mat2(c,s,-s,c);
}

float map(vec2 p) {
    float s=0.;
    m=mc=100.;
    float l2;
    float y=(sin(p.x+cos(p.y*2.)+2.724)+cos(p.y))*8.;
    vec2 mcol=vec2(100.);
	p=abs(.5-fract(p*.03));
    y+=smoothstep(.5,.4,length(p.xy))*10.;
    vec2 pos=p;
    for (int i=0; i<6; i++) {
    	p=abs(p+1.)-abs(p-1.)-p;
        float l=dot(p,p);
        p=p*2./clamp(l,.25,1.)-pos;
        s+=exp(-.3*abs(l-l2));
        l2=l;
        if (i>2 && i<5) m=min(min(abs(p.y),abs(p.x)),m);
        mc=min(mc,l);
    }
    m=pow(max(0.,1.-m),6.);
    return (y+s)*.05-m*.1;
}


vec3 normal(vec2 p) {
	vec2 eps=vec2(0.,.01);
    return normalize(vec3(map(p+eps.yx)-map(p-eps.yx),2.*eps.y,map(p+eps.xy)-map(p-eps.xy)));
}

vec2 hit(vec3 p) {
    float h=map(p.xz);
    return vec2(step(p.y,h),h);
}

float fog(vec3 p,float h) {
    p.y=p.y*2.-1.-h*1.5;
    p.x+=p.z*1.234+iTime*.5;
	p=abs(2.-mod(p,4.));
    for (int i; i<10; i++) {
    	p=abs(p)/dot(p,p)-.8;
    }
    return length(p);
}

vec3 bsearch(vec3 from, vec3 dir, float td) {
    vec3 p;
    st*=-.5;
    td+=st;
    float h2=1.;
    for (int i=0; i<20; i++) {
        p=from+td*dir;
        float h=hit(p).x;
        if (abs(h-h2)>.001) {
            st*=-.5;
	        h2=h;
        }
        td+=st;
    }
	return p;
}

vec3 shade(vec3 p, vec3 dir, float h) {
    float val = dot(sin(p*6. - cos(p.yzx*12.)*3.), vec3(.166)) + .5; // Or noise, etc.
    vec3 ember = pow(vec3(1.6, 1, 1)*val, vec3(1, 8, 34)); // Poor man's fire palette. 
 	vec3 col = ember *m*pow(smoothstep(.3,.5,abs(.5-fract(mc*.5+h*.5+iTime*.5))),2.);

    ldir=normalize(ldir);
	vec3 n=normal(p.xz);
    vec3 ref=reflect(dir,ldir);
    float spe=pow(max(0.,dot(ref,-n))*.5,3.)*2.5;
    return col+spe;
}


vec3 march(vec3 from, vec3 dir) {
	vec3 p, col=vec3(0.);
    float td=1., k=0.;
    vec2 h;
    for (int i=0; i<350; i++) {
    	p=from+dir*td;
        h=hit(p);
        k+=fog(p,h.y)*pow(max(0.,1.-abs(p.y-h.y)*.25),2.);
        if (h.x > .5 || td>maxdist) break;
        td+=st;
    }
    float f=pow(td/maxdist,2.);
    if (h.x > .5) {
        p=bsearch(from, dir, td);
    	col=shade(p,dir,h.y);
    } 
	col=mix(col,vec3(.2*(1.-p.y*.25)),f)*min(1.,iTime*.5);
    return col*vec3(1.,.9,.8)+k*.0007;
}

mat3 lookat(vec3 dir, vec3 up) {
	dir=normalize(dir);vec3 rt=normalize(cross(dir,normalize(up)));
    return mat3(rt,cross(rt,dir),dir);
}

vec3 path(float t) {
	return vec3(sin(t*.2645)+cos(t*.1213)*2.,2.5+sin(t*.354)*.2,t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
	float t=iTime+15.;
    vec3 from=path(t);
    vec3 dir=normalize(vec3(uv,1.));
    vec3 adv=from-path(t-1.);
    dir=lookat(adv+vec3(0.,-.5,0.),vec3(adv.x*.3,1.,0.))*dir;
    vec3 col=march(from, dir);
    fragColor = vec4(col,1.);
}


