const float pi = asin(1.0) * 2.0;
const float pi_to_rad = pi / 180.0;
const float rad_to_pi = 180.0 / pi;

const vec2 cam_pos = vec2(0.0, 0.0);
const float cam_rot = 10.0 * pi_to_rad;
const float vp_size = 10.0;

vec2 fragToNDC(in vec2 fragCoord)
{
    return 2.0 * vec2(fragCoord / iResolution.xy) - 1.0;
}

vec2 NDCToOrigin(in vec2 ndc)
{
    float aspect_ratio = iResolution.x / iResolution.y;
    return vec2(ndc.x * aspect_ratio, ndc.y);
}

vec2 originToCamSpace(in vec2 pos)
{
    float dcr = iTime * cam_rot;

    return cam_pos + (pos * vp_size) * mat2(cos(dcr), -sin(dcr), sin(dcr), cos(dcr));
}

vec4 map(in vec2 pos)
{
    vec4 res = vec4(0.0, 0.0, 0.0, 0.0);

    vec3 color = vec3(0.0);

    // checker board
    {
        vec2 fract_p = fract(pos + vec2(0.5));
        vec2 floor_p = floor(pos);

        // lines
        vec2 ln_gradient = smoothstep(0.0, 0.01, 0.5 * cos(fract_p * 2.0 * pi) + 0.5);
        float ln_strength = ln_gradient.x * ln_gradient.y;

        // background
        float bg_strength = floor_p.x + floor_p.y;
        vec3 bg_color = mix(vec3(0.99), vec3(0.01), mod(bg_strength, 2.0));

        // combine
        color += bg_color * ln_strength;
    }

    // sphere
    {
        float r = 1.0;
        vec2 p = vec2(0.0, 5.0);
		float d = 1.0 - smoothstep(0.9, 1.0, length(pos - p) / r);
        vec3 c = vec3(1.0, 0.0, 0.5);

        color = d * c + (1.0 - d) * color;
    }

    res.xyz = color;

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

    int aa = 4;
    float aa_nom = 1.0 / float(aa);
    float aaa = 1.0 / float(aa * aa);

    for(int AA_x = 0; AA_x < aa; AA_x++)
        for(int AA_y = 0; AA_y < aa; AA_y++)
        {
            vec2 frc = fragCoord + vec2(float(AA_x) * aa_nom, float(AA_y) * aa_nom);
            vec2 ndc = fragToNDC(frc);
            vec2 org = NDCToOrigin(ndc);
            vec2 pos = originToCamSpace(org);

            vec4 res = map(pos);

            tot += res.xyz;
        }

    tot = tot * aaa;
    tot = pow(tot, vec3(1.0/2.2));

    fragColor = vec4(tot,1.0);
}

