// Finds the distance of P from the line A to B
float segment( in vec2 p, vec2 a, vec2 b )
{
	vec2 ap = p - a;
	vec2 ab = b - a;
	float h = clamp(dot(ap,ab)/dot(ab,ab), 0.0, 1.0); // project ap onto ab
	return length( ap - ab*h ); // finds length of vec from p to the projection of p onto ab
}

vec3 drawShape(vec2 p, mat3 tform, vec3 col)
{
    float thicc = 0.01;

    for(int i = 1; i < numVerts; i++)
    {
    	vec3 a = tform * vec3(shape[i-1], 1);
    	vec3 b = tform * vec3(shape[i], 1);

        if (segment(p, a.xy, b.xy) < thicc)
        	return shapeCol;
    }

    return col;
}

void updateShape()
