// Matrix transform helpers ////////////////////////////////////////////////////////////////////////////
mat3 createTransMat(vec2 translation)
{
    return mat3(vec3(1,0,0), 
                vec3(0,1,0), 
                vec3(translation,1));
}
mat3 createScaleMat(vec2 scale)
{
    return mat3(scale.x, 0, 0,
                0, scale.y, 0,
                0, 0,       1);
}
mat3 createRotationMat(float rad)
{
    return mat3(cos(rad), sin(rad), 0,
               -sin(rad), cos(rad), 0,
                0,        0,        1);
}
mat3 createAffineMat(vec2 translation, float rotation, vec2 scale)
{
    mat3 S = mat3(createScaleMat(scale));
    mat3 R = mat3(createRotationMat(rotation));
    mat3 T = createTransMat(translation);
    return S*R*T;
}


// Transformations ////////////////////////////////////////////////////////////////////////////////////
mat3 matTranslate(mat3 m, vec2 translation)
{
    return createTransMat(translation) * m;
}
mat3 matRotate(mat3 m, float rad)
{
    return createRotationMat(rad) * m;
}
mat3 matScale(mat3 m, vec2 scale)
{
    return createScaleMat(scale) *  m;
}
