void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.yy;
    vec2 uvNorm = fragCoord/iResolution.xy;

    updateShape();

    // Shape mat - moves pivot to the middle of our shape
    mat3 shapeMat = createTransMat(vec2(-.5,-.5));

    // Model mat - moves/rotates and squishes our model
    mat3 modelMat = mat3(1);
   	modelMat = matScale(modelMat, 0.5 + vec2(0.1*sin((iTime+3.0)*3.0), 0.1*cos(iTime*3.0)));
    modelMat = matRotate(modelMat, sin(iTime*0.7)*0.7);
    modelMat = matTranslate(modelMat, vec2(aspectRatio/2., 0.5));

    // View mat - pan the view about
    mat3 viewMat = mat3(1);
    viewMat = matTranslate(viewMat, 0.05 * vec2(2.0*sin(1.2*iTime+3.0), cos(iTime+5.0)) );

    vec3 col = viewMat*vec3(uvNorm,1) * 0.2; // Draw background
    col = drawShape(uv, viewMat*modelMat*shapeMat, col);

    fragColor = vec4(col,1.0);
}

