#define invert(z,ds) {z/=dot(z,z); ds*=dot(z,z);}

#define R {b+=a; d+=c;}
#define L {a+=b; c+=d;}

vec3 color(vec2 z, float ds) {
    bool fl = false;
    for(int i=0;i<60;i++) {
        z.x = fract(z.x);
        if (z.x > 0.5) {
            z.x = 1.0-z.x;
            fl = !fl;
        }
        if (dot(z,z) < 1.0) {
            invert(z,ds);
            fl = !fl;
            continue;
        }

    }
    float c = 0.0;
    if (ds > z.x) {
        c = 0.5 * (1.0 - (z.x / ds));
    }
    if (ds > 0.5 - z.x) {
        c = max(c, 0.5 * (1.0 - ((0.5 - z.x) / ds)));
    }
    if (2.0 * ds > dot(z,z) - 1.0) {
        c = max(c, 0.5 * (1.0 - ((dot(z,z) - 1.0) / (2.0 * ds))));
    }
    c = max(c,ds);
    c = min(c,0.5);
    if (fl) {
        c = 1.0 - c;
    }
    return vec3(c,c,c);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord - iResolution.xy * 0.5)/iResolution.y;
    float ds = 2.0 / iResolution.y;

    float r2 = dot(uv,uv);
    if (r2 < 1.0) {
        uv.y -= 1.0;
        invert(uv,ds);
        uv.y = -0.5 - uv.y;

        int a=1; int b=0; int c=0; int d=1;
        L; R; R; L;
        if (a+d<=2) {
            // treat null rotations specially
            if (c>0) {
                uv.x += 0.5;
            } else {
                uv.x = 0.5 - uv.x;
            }
            invert(uv,ds);
            uv.x -= 0.1 * iTime;
            fragColor = vec4(color(uv,ds),1.0);  // gamma correction suggested by FabriceNeyret2
            fragColor = pow(fragColor, vec4(1./2.2));
            return;
        }
        float disc = sqrt(float((a+d)*(a+d)-4));
        float inverse_diff = float(c) / disc;
        float root = (float(a-d) + disc) / (2.0 * float(c));
        float period = 2.0 * log((float(a+d) + disc) * 0.5);

        float t = 0.1 * iTime;
        t = mod(t,period) - period * 0.5;
        uv *= exp(t); ds *= exp(t);

        uv.x -= inverse_diff;
        invert(uv,ds);
        uv.x += root;
        fragColor = vec4(color(uv,ds),1.0);
    } else {
        float c = (r2 - 1.0) / (2.0 * ds);
        fragColor = vec4(0.5,0.5 + 0.5 * c ,0.5,1.0);
    }
    fragColor = pow(fragColor, vec4(1./2.2));  // gamma correction suggested by FabriceNeyret2
}

