#define S(a,b,t) smoothstep (a, b ,t)
#define sat(x) clamp(x, 0. , 1.) 

float remap01 (float a, float b , float t){
  	return sat((t-a) / (b-a) );										// sat is used to be sure that the value returned  is between 0 & 1
}

float remap (float a , float b , float c , float d, float t ){
    return sat(((t-a)/(b-a)) * (d-c)+c);

}

vec2 within (vec2 uv , vec4 rect ){ 								// return uv coordinate within that rectangle 
	return (uv-rect.xy)/(rect.zw-rect.xy);

}
vec4 Brow (vec2 uv){

	float y = uv.y;
    uv.y += uv.x*.1-2.;
    uv.x-=.1;
    uv -=.5;

    vec4 col = vec4(0.);
    float blur =.1; 
    float d1 = length(uv);
    float s1 = S (.40,.45-blur,d1);
    float d2 = length(uv-vec2(1.,-.2)*.7);
    float s2 = S(.5,.55-blur,d2);
    float browMask = sat (s1-s2);

    vec4 browCol = vec4(.7,.5,.3,1.);
    col = mix (col, browCol,S(.2,.4,browMask));
	return col;
}  
vec4 Eye (vec2 uv,float side,vec2 m,float smile){
	uv -=.5;
    uv.x *= side;													// desable mirior effect for mvt eyes 
    float d = length(uv);
    vec4 irisCol = vec4(.0,.6,.0,4.);								// iris color
    vec4 col = mix (vec4(1.),irisCol,S(.2,.50,d)*.6);				// gradien in eye-white
    col.a= S(.5,.48,d);// eye mask
    col.rgb *= 1. - S(.45,.5,d)*.7*sat(-uv.y+uv.x);					// add a little shadow , sat doesn't get negative in

    d = length(uv-m*.5);											// distance of mouse mvt 
    col.rgb = mix (col.rgb,vec3(0.), S(.29,.15,d));					// iris outline

    irisCol.rgb *= 1.2 + S(.35,.001,d);
    col.rgb = mix (col.rgb,irisCol.rgb, S(.28,.12,d));

    float pupilSize=mix (.30,.18,smile);							// pupil size is bigger if we're not smiling
    d = length(uv-m*.65);
    col.rgb = mix (col.rgb,vec3(0.), S(pupilSize,pupilSize*.95,d)); // blend

    //highlight
    uv.x += sin(iTime+uv.y*20.)*.03;
    float p = length(uv -vec2(-.11,.15));
    float p2 = length(uv +vec2(-.06,.10));

    float highlight = S (.1,.07,p);
    highlight += S(.1,.0001,p2);
    col.rgb= mix (col.rgb, vec3(1.),highlight);
    col.rgb= mix (col.rgb, vec3(1.),highlight);

    return col;
}
vec4 Mouth (vec2 uv,float smile){
	uv-=.5;//renormalize
    vec4 col = vec4(.9,.1,.2,1.);

    uv.y*=1.5;
    uv.y -= uv.x*uv.x*2.*smile;
    uv.x *= mix(3.,1.,smile);
    float d = length(uv);
    col.rgb= mix (col.rgb,vec3(.2,.10,.05),S(.55,.45,d));

    float teeth = length(uv-vec2(0.005,.65));
    vec3 toothcol= vec3(1.)*S(.9,.7,d);
    col.rgb= mix(col.rgb,toothcol,S(.47,.35,teeth));

    float tongue = length(uv-vec2(-.005,-.55));
    col.rgb = mix(col.rgb, vec3(1.,.5,.5),S(.5,.2,tongue));

    col.a= S(.6,.47,d);
    return col;

}

vec4 Head (vec2 uv){
	vec4 col = vec4(.9,.70, .1, 1.);    
    float d = length(uv);

    col.a = S(.5,.49,d);
    float edgeShade = remap01(.35,.5,d);
    edgeShade *= edgeShade;
    col.rgb *= 1.-edgeShade*.5;

    col.rgb = mix (col.rgb,vec3(.65,.4,.1),S(.48,.48,d));			// draw "contour"

    float highlight = S(.41,.405,d)*.75;
    highlight *=remap(.41,-.1,.75,0.,uv.y);
    highlight *=S(.16,.18,length(uv-vec2(.207,.11)));
    col.rgb = mix(col.rgb,vec3(1.),highlight);

    //cheek
    d = length(uv-vec2(.30, -.17));									// position of cheek in the image
    float cheek= S(.20,.02,d)*.35;   
    col.rgb = mix(col.rgb,vec3(1.,.1,.1),cheek);

    return col;
}

vec4 Smiley (vec2 uv,vec2 m,float smile,vec4 fragColor,vec2 fragCoord){

    vec2 uv1 = (fragCoord*2.-iResolution.xy)/40.;
    vec4 col = vec4(.2,0.4,0.3,.2);
     col += vec4(sin(iTime*10.+3.141592653589793*2.)*0.5)*0.3+0.5;
    col+=sin((uv1.x-uv1.y)*60.)/2.;

    //head
    float side = sign (uv.x);											// Every think on one side will be drawn on
   	uv.x=abs(uv.x);														// The other side too "mirror x cordinates 


    vec4 Head = Head (uv);												// The color from the head
    vec4 Eye = Eye (within(uv,vec4(.03,-.05,.36,.26)),side,m,smile);	// Make the eyes
    vec4 Mouth = Mouth (within(uv,vec4(-.3,-.42,.28,-.1)),smile);		// Make Mouth
    vec4 Brow = Brow (within(uv,vec4(.03,.2,.30,.27)));

    //mix color
    col = mix (col, Head,Head.a); 
    col = mix (col, Eye,Eye.a);
    col = mix (col, Mouth,Mouth.a);
    col = mix (col, Brow,Brow.a);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv-= .5;
    uv.x *=iResolution.x / iResolution.y;

