void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = BACK_WALL_SDF;
    obj = BACK_WALL;

    float t2;
    if((t2 = LEFT_WALL_SDF) < t) {
        t = t2;
        obj = LEFT_WALL;
    }
    if((t2 = RIGHT_WALL_SDF) < t) {
        t = t2;
        obj = RIGHT_WALL;
    }
    if((t2 = CEILING_SDF) < t) {
        t = t2;
        obj = CEILING;
    }
    if((t2 = FLOOR_SDF) < t) {
        t = t2;
        obj = FLOOR;
    } 
    if ((t2 = SPHERE_1_SDF) < t) {
    	t = t2;
        obj = SPHERE_1;
    }
    if ((t2 = SPHERE_2_SDF) < t) {
    	t = t2;
        obj = SPHERE_2;
    }
    if ((t2 = SPHERE_3_SDF) < t) {
    	t = t2;
        obj = SPHERE_3;
    }
}


