const int V_COUNT = 16;
const int H_COUNT = 16;

vec2 steps;

vec3 getLetter(int ascii, int row, int column, vec2 uv) {
    uv = vec2(uv.x - (steps.x*float(column))/iChannelResolution[0].x, uv.y - (steps.y*float(row))/iChannelResolution[0].y);
    return texture(iChannel0, uv+(vec2(ascii,15-ascii/16))/16.).xxx;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    steps.x = iChannelResolution[0].x / float(H_COUNT);
	steps.y = iChannelResolution[0].y / float(V_COUNT);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_tex = fragCoord/iChannelResolution[0].xy;
    vec2 nSteps = steps/iChannelResolution[0].xy;

    vec3 col;

    float xIndex = uv.x / (steps.x/iResolution.x);
    float yIndex = uv.y / (steps.y/iResolution.y);

    if (int(ceil(yIndex)) == 1 || int(ceil(xIndex)) == 1) {
        col = getLetter(65, int(yIndex), int(xIndex), uv_tex);
    } else {
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }

    fragColor = vec4(col,1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

