vec4 sphere = vec4(0.0, 0.0, 1.0, 0.4);
vec3 lightPos = vec3(-9.0, 8.0, -8.0);
vec3 lightCol = vec3(1.0, 1.0, 1.0);

float sphereDist1(vec3 ro, vec3 rd)
{
	float R = sphere.w;
	vec3 d1 = sphere.xyz - ro;
	float b = dot(rd, d1);
	float d2 = dot(d1, d1) - b * b;
	float h2 = R * R - d2;
    if (h2 <= 0.0)
        return (-1.0);
    return (b - sqrt(h2));
}

float sphereDist(vec3 p)
{
 return (length(p - sphere.xyz) - sphere.w);   
}

vec3 hsv2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0,4.0,2.0),6.0) - 3.0) - 1.0, 0.0, 1.0);
	return (c.z * mix(vec3(1.0), rgb, c.y));
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float getShadow(vec3 ro, vec3 rd, float tmin, float tmax, const float k)
{
	float res = 1.0;
    float t = tmin;
    for( int i = 0; i < 50; i++)
    {
		float h = sphereDist(ro + rd * t);
        res = min(res, k * h / t);
        t += clamp(h, 0.02, 0.20);
        if(res < 0.005 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 getNormal(vec3 pos)
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    return normalize( e.xyy*sphereDist(pos + e.xyy*ep) + 
					  e.yyx*sphereDist(pos + e.yyx*ep) + 
					  e.yxy*sphereDist(pos + e.yxy*ep) + 
					  e.xxx*sphereDist(pos + e.xxx*ep));
}


vec2 f(vec2 z)
{
    float mag = dot(z, z);
    return (2.0 * z + vec2(z.x * z.x - z.y * z.y, -2.0 * z.x * z.y) / (mag * mag)) / 3.0;
}

vec2 roots[] = vec2[](vec2(1.0, 0.0), vec2(-0.5, 0.5 * sqrt(3.0)), vec2(-0.5, -0.5 * sqrt(3.0)));

float newton(vec2 z) 
{
    vec4 res;
    for (int i = 0; i < 100; ++i)
    {
    	z = f(z); 
        for (int root = 0; root < roots.length(); ++root)
        {
            vec2 differ = z - roots[root];
            float dist = dot(differ, differ);
            if (dist < 0.00001)
				return (0.78 * cos(1.5 * log2(log(dist) / log(0.0001))));
        }
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 7.0 * (iMouse.xy / iResolution.xy - 0.5);  
    vec3 ro = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    mouse.y += 0.3;
	float rot = -0.2;//iTime * 0.2;
    mat3 rotX = mat3(
        vec3(cos(mouse.x - rot), 0.0, sin(mouse.x - rot)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sin(mouse.x - rot), 0.0, cos(mouse.x - rot)));  
	mat3 rotY = mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(mouse.y), sin(mouse.y)),
        vec3(0.0, -sin(mouse.y), cos(mouse.y)));

    float dSphere = sphereDist1(ro, rd);
	vec3 pos = ro + dSphere * rd;
	vec3 normal = getNormal(pos);
	vec3 v3 = rotX * rotY * normal;
    vec2 v2 = 9.0 * (cos(iTime * 0.2)) * v3.xy / abs(v3.z);
    vec3 material;
    if (dSphere < 0.0)
        material = hsv2rgb(vec3(newton(v2), 0.8, 0.5));
    else
        material = hsv2rgb(vec3(newton(v2), 1.0, 1.2));
    vec3 lightDir = normalize(vec3(lightPos - pos));
    vec3 reflectDir = normalize(reflect(lightDir, normal));
    float shadow = getShadow(pos, lightDir, 0.001, 1.0, 32.0);

    float diffuse = clamp(dot(normal, lightDir), 0.0, 1.0) * shadow * 0.4;
    float ambient = 0.4 + 0.55 * normal.y;
	float specular = pow(clamp(dot(reflectDir, rd), 0.0, 1.0), 32.0) * 1.2 * shadow;

	vec3 color = lightCol * material * (diffuse + specular + ambient);
    color = sqrt(color);
    color = pow(color, lightCol);
    fragColor = vec4(color, 1.0); 
}

#define ANGLE 2.1
#define DELTA 0.00635
#define XOFF .034531

#define time iTime
mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}


float f(vec2 p, float featureSize)
{
	p.x = cos(p.x*0.6+time*0.2)*log(time+p.x*0.1)*ANGLE;	
    p += sin(p.x*0.5)*ANGLE;
    return smoothstep(-0.01,featureSize,abs(p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/iResolution.y;
    float featureSize = 135./((iResolution.x*aspect+iResolution.y));

    vec2 p = (fragCoord.xy/(ANGLE*XOFF)) / (iResolution.xy*ANGLE);
	p.x *= aspect;
	p.y = abs(p.y);

	vec3 col = vec3(.04);
    vec3 col2 = (cos(vec3(3.3,2.5,2.2)-2.*0.15)*0.74)*(1.-f(p,featureSize));
	for(float i=0.;i<26.;i+=(XOFF*12.))
	{
		col2 = (cos(vec3(3.3,2.5,2.2)-i*ANGLE)*0.74)*(1.-f(p,featureSize));
		col = max(col,col2);

        p.x -= XOFF;
        p.y -= tanh(time*0.31+1.5)*3.;//((ANGLE/XOFF)*XOFF); //1.5+1.5;
		p*= mm2(i*DELTA+ANGLE);

        vec2 pa = vec2(abs(p.x-0.9),abs(p.y));
        vec2 pb = vec2(p.x,abs(p.y));

        p = mix(pa,pb,smoothstep(-.47,.67,cosh(time*0.14)-ANGLE));
	}
	fragColor = vec4(col,1.0);
}

float sdCylinder(vec3 p, float r, float h)
{
    return max(length(p.xz) -r, abs(p.y) - h);
}

float hash( float n )
{
    return fract(sin(n) * 43758.5453);
}

//Hash from iq
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 k = fract(x);
	k = k*k*(3.0-2.0*k);

    float n = p.x + p.y * 57.0 + p.z * 113.0;
	float a = hash(n);
    float b = hash(n + 1.0);
    float c = hash(n + 57.0);
    float d = hash(n + 58.0);

    float e = hash(n + 113.0);
    float f = hash(n + 114.0);
    float g = hash(n + 170.0);
    float h = hash(n + 171.0);

    float res = mix(mix(mix(a, b, k.x), mix(c, d, k.x), k.y),
                    mix(mix(e, f, k.x), mix(g, h, k.x), k.y),
                k.z);
    return res;                
}
#define rotate(ang) mat2(cos(ang), sin(ang), -sin(ang), cos(ang))
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map(vec3 p)
{
    vec3 rp = p;
    vec2 id = floor(rp.xz / 20.0);
    rp.xz = mod(rp.xz, 20.0) - 10.0;
    float d = sdCylinder(rp + vec3(hash(id.x) * 20.0 - 10.0, 0.0, hash(id.y) * 20.0 - 10.), 2.0, 50.);
    float ground = noise(p * 0.5) + noise(p) * 0.5 + noise(p * 2.0) * 0.25;
    d = smin(d, p.y + 10.0, 5.0);
    return d - ground;
}

vec3 norm(vec3 p)
{
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

float caustic(vec3 p)
{
    return abs(noise(p + mod(iTime, 40.0) * 2.0) - noise(p + vec3(4.0, 0.0, 4.0) + mod(iTime,40.0) * 2.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec3 r0 = vec3(20.0, 10.0, -20.0);
    r0.xz *= rotate(0.58);

    vec3 ww = normalize(-r0);
    vec3 uu = normalize(cross(vec3(0, 1, 0), ww));
    vec3 vv = normalize(cross(ww, uu));

    vec3 rd = normalize(uu * uv.x + vv * uv.y + ww);

    float d = 0.0;
    for(int i = 0; i < 100; ++i)
    {
        vec3 p = r0 + d * rd;
        float t = map(p);
        d += t;
        if(d > 100.0 || t < 0.001) break;
    }
    vec3 col = vec3(0.0);
    vec3 p = r0 + d * rd;
    if(d < 100.0)
    {
        vec3 n = norm(p);
        vec3 ld = normalize(vec3(0.5, 1.0, -0.5));
        float diff = max(dot(n, ld), 0.0);
        col += diff * vec3(0.6, 0.8, 1.0);
        col += (n.y * 0.5 + 0.5) * vec3(0.16, 0.20, 0.28);
        float n1 = noise(p * 0.5);
        float n2 = noise(p);
        float n3 = noise(p * 8.);
        col *= n1 * vec3(0.2, 1.0, 0.1) * 2.0 + n2 * vec3(2.0, 0.2, 0.1) * 2.0 + n3 * vec3(0.5, 0.5, 0.1);
    }

    col += smoothstep(0.0, 1.0, (1.0 - caustic(p * 0.5)) * 0.5);
    float fog = clamp(exp(-d * 0.035), 0.0, 1.0);
    col = mix(col, vec3(0.2, 0.5, 1.0), 1.0 - fog);
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

#define PI 3.14159265
